css:
@-moz-document url-prefix("https://www.google.com/recaptcha/api2/"), domain("www.bibsonomy.org") {

  body {
    background-color: #1C1C1C;
  }
  
  /* Links */
  a {
    color: #057fd4;
  }
  
  a:hover, a:focus {
    color: #129bfa;
  }
  
  /* non-writable Text areas */
  .form-control[disabled], .form-control[readonly], fieldset[disabled] .form-control {
    background-color: #303030;
  }
  
  .container {
    background-color: #222222;
    color: #c0c0c0;
    border-color: #303030;
    box-shadow: 2px 2px 30px 6px #303030; /* non-color values are as default; no way to inherit separately hence written */
  }
  
  .container.main {
    box-shadow: 2px 2px 30px 6px #303030; /* non-color values are as default; no way to inherit separately hence written */
  }
  
  .ptitle a { /* Bookmark Title */
    color: #c0c0c0;
  }
  
  .pmeta > a { /* For links in bookmark description */
    color: #428BCA;
  }
  
  .pmeta > a:hover {
    color: #129bfa;
  }
  
  .ellipsis-content > span > a { /* For first bookmarker reference */
    color: #428BCA;
  }
  
  .thumbnail { /* Book Thumbnail */
    background-color: #303030;
    border-color: #666666;
  }
  
  img[alt="preview"] {
    opacity: 0.4;
  }
  
  .btn-default { /* Small Edit buttons at bottom */
    background-color: #222222;
    color: #999;
    border-color: #333;
  }
  
  .btn-default:hover {
    background-color: #303030;
    color: #c0c0c0;
    border-color: #666666;
  }
  
  .ellipsis {
    background-color: #222222;
  }
  
  .ellipsis::after {
    background: #222222;
  }
  
  .breadcrumb { /* small message tab before link list */
    background-color: transparent;
  }
  
  .breadcrumb > li > a { /* sub-message */
    color: #428BCA;
  }
  
  .post a.thumbnail { /* bookmark + publication thumbnail */
    filter: opacity(30%);
  }
  
  a.preview.bookmark.thumbnail > span { /* bookmark thumbnail */
    filter: opacity(30%);
  }
  
  .ptags { /* tag list */
    font-size: 150%;
    height: 20px; /* from default 17px; */
  }
  
  .fa.fa-tag.pull-left::before { /* tags label */
    color: #555555;
  }
  
  .label-grey { /* tag */
    background-color: #303030;
  }
  
  .label-grey > a { /* tag */
    color: #d0d0d0;
  }
  
  .score { /* Stars */
    color: #303030;
  }
  
  .bootstrap-tagsinput { /* 'edit tags' pop-up pane */
    background-color: #222222;
    border-color: #666666;
  }
  
  .numpostsbadge .badge { /* No. of community posts */
    background-color: #303030;
  }
  
  .badge { /* No. of community posts */
    color: #c0c0c0;
    background-color: #303030;
  }
  
  .pagination > li > a { /* Page No.s clickable */
    background-color: #303030;
    border-color: #555555;
    color: #bbbbbb;
  }
  
  .pagination > li > a:hover, .pagination > li > a:focus {
    background-color: #337ab7; /* from .pagination > .active > span */
    border-color: #337ab7;
    color: #fff;
  }
  
  .pagination > li > span { /* Page No.s non-clickable */
    background-color: #303030;
    border-color: #555555;
    color: #bbbbbb;
  }
  
  .pagination > .disabled > span {
    background-color: #303030;
    border-color: #555555;
    color: #bbbbbb;
  }
  
  .pagination > .disabled > span:hover {
    background-color: #222222;
    border-color: #555555;
  }
  
  .jumbotron { /* Login msg */
    background-color: #303030;
  }
  
  ul.posts li.post { /* bottom separator */
    border-bottom-color: #333;
    height: 150px; /* from default 146px; */
  }
  
  /* .ellipsis > :first-child { / * To prevent bkgd white bleeding through margin * /
    background-color: #303030;
  } */
  
  /* Top Header */
  .container.header {
    background-color: #303030;
    box-shadow: 2px 2px 30px 6px #303030; /* non-color values are as default; no way to inherit separately hence written */
  }
  
  a.dropdown-toggle {
    background: #222222 !important;
  }
  
  .navbar-bibsonomy a {
    color: #c0c0c0;
  }
  
  .navbar-bibsonomy ul.navbar-nav li a:focus, .navbar-bibsonomy ul.navbar-nav li a:active {
    background-color: #222222;
    color: #c0c0c0;
  }
  
  .navbar-bibsonomy ul.navbar-nav > li.active > a {
    background-color: #222222;
    color: #c0c0c0;
  }
  
  .navbar-nav > li > .dropdown-menu {
    background-color: #222222;
  }
  
  .navbar-bibsonomy ul.navbar-nav li a:hover {
    background-color: #222222;
    color: #428bca;
  }
  
  /* Middle Header - 'bookmarks', 'publications' */
  h3.list-headline {
    border-bottom-color: #555555;
  }
  
  /* Profile Icon */
  .navi-info span {
    background-color: #555555;
  }
  
  /* Dropdown */
  .dropdown-menu {
    background-color: #303030;
  }
  
  .dropdown-menu > li > a {
    color: #c0c0c0;
  }
  
  .dropdown-menu > li > a:hover, 
  .dropdown-menu > li > a:focus {
    background-color: #555555;
    color: #d0d0d0;
  }
  
  ul.dropdown-menu.list-options > li > span {
    color: #c0c0c0;
  }
  
  ul.dropdown-menu.list-options > li > span > a {
    color: #428BCA;
  }
  
  ul.dropdown-menu li.divider {
    border-bottom-color: #555555;
  }
  
  .clipboard-counter.badge {
    background-color: #555555;
  }
  
  /** Side Pane **/
  div#sidebar {
    background-color: #222222;
    border-left-color: #555555;
  }
  
  .bmtitle {
    color: #428BCA;
  }
  
  .main .sidebar h4 {
    border-bottom-color: #555555;
  }
  
  .tagcloud > li > a {
    color: #428BCA;
  }
  
  .tagcloud > li > a:hover {
    color: #129bfa;
  }
  
  .img-thumbnail { /* Profile Pic */
    background-color: #303030;
    border-color: #555555;
  }
  
  /* Messages */
  
  .alert-info { /* Did you know... msg 1 */
    background-color: #303030;
    border-color: #555555;
    color: #c0c0c0;
  }
  
  .alert-warning { /* Did you know... msg 2 */
    background-color: #303030;
    border-color: #555555;
    color: #c0c0c0;
  }
  
  .alert-success { /* Did you know... msg 3 */
    background-color: #303030;
    border-color: #555555;
    color: #c0c0c0;
  }
  
  .community-info.warning { /* msg with content, \f071 */
    background-color: #303030;
    border-color: #555555;
    color: #c0c0c0;
  }
  
  .alert-danger { /* 'Oooops, something's gone wrong' msg */
    background-color: #5e2626; /* from default #f2dede */
    border-color: #555555;
    color: #c0c0c0;
  }
  
  /* concepts */
  
  .main .sidebar h4 > a {
    color: #c0c0c0;
  }
  
  #relations > li a {
    color: #bbbbbb !important;
  }
  
  /* similar users */
  .sidebar-setting-box a {
    color: #bbbbbb;
    text-decoration: none;
  }
  
  .sidebar-setting-box a:hover, .sidebar-setting-box a:focus {
    color: #129bfa;
  }
  
  .sidebar-setting-box { /* make parenthesis in '(more...)' invisible */
    color: #222222;
  }
  
  
  /* Search Bar */
  
  .input-group-addon {
    background-color: #222222;
    border-color: #303030;
  }
  
  .input-group-addon a {
    color: #428BCA;
  }
  
  .form-control { /* For all remaining forms */
    background-color: #222222;
    border-color: #555555;
    color: #c0c0c0;
  }
  
  .input-group .form-control {
    border-color: #303030;
  }
  
  #search a.input-info {
    color: #555555;
  }
  
  .input-group-btn:last-child > .btn {
    border-color: #303030;
  }
  
  /* Help */
  .leftmenu a {
    color: #428bca;
  }
  
  .leftmenu a:hover {
    color: #7aadd9;
  }
  
  /* Footer */
  .container.footer dt {
    background-color: #303030;
    border-bottom-color: #555555 !important;
    box-shadow: 0 1px 1px #555555; /* except color remaining same */
  }
  
  .container.footer a {
    color: #7aadd9;
  }
  
  .container.footer a:not(.btn) {
    color: #bbbbbb;
  }
  
  .container.footer dd {
    color: #555555;
  }
  
  .container.footer hr {
    border-bottom-color: #333;
    box-shadow: #333 0 1px 1px 0; /* except color remaining same */
    -webkit-box-shadow: #333 0 1px 1px 0; /* except color remaining same */

  }
  
  /* Login Pop-up Pane */
  .modal-content {
    background-color: #222222;
    color: #c0c0c0;
  }
  
  #loginModal p.authHelp {
    color: #c0c0c0;
  }
  
  /* Pop-up Pane for Dropdown Ref. options e.g. BibTeX, APA, etc. */
  pre {
    background-color: #222222;
    color: #c0c0c0;
  }
  
  .modal-header {
    border-bottom-color: #555555;
  }
  
  .modal-footer {
    border-top-color: #555555;
  }
  
  /*** Save Publication to Bibsonomy ***/
  
  /* Field Pop-over */
  .popover-title, .popover-content, .popover {
    background-color: #303030;
    color: #c0c0c0;
  }
  
  .popover-title {
    border-bottom-color: #555555;
  }
  
  .popover.top > .arrow::after {
    border-top-color: #303030;
  }
  
  /* Right-side Pane */
  .list-group-item {
    background-color: #222222;
    border-color: #555555;
  }
  
  a.list-group-item, button.list-group-item {
    color: #bbbbbb;
  }
  
  a.list-group-item:hover, button.list-group-item:hover, 
  a.list-group-item:focus, button.list-group-item:focus {
    background-color: #303030;
    color: #d0d0d0;
  }
  
  /* Separators */
  legend, .content-header, .content-heading {
    border-bottom-color: #555555;
    color: #666666;
  }
  
  /** Tags auto-complete **/
  /* Dropdown */
  .ui-widget-content {
    background-color: #222222;
    background-image: none;
    border-color: #222222;
  }
  
  a.ui-corner-all {
    border-color: #333;
  }
  
  .ui-state-hover, .ui-widget-content .ui-state-hover, .ui-widget-header .ui-state-hover, 
  .ui-state-focus, .ui-widget-content .ui-state-focus, .ui-widget-header .ui-state-focus {
    color: #c0c0c0;
  }
  
  /* Spinner */
  .ui-autocomplete-loading {
    background-color: rgb(250, 250, 250);
    filter: invert(87%);
  }
  
  .ui-autocomplete-loading.form-control {
    color: #111;
  }
  
/*   / * Captcha - within iframe * /
  iframe[title="recaptcha widget"] > .rc-anchor-light {
    background: #222222;
  } */
  
  /*** Post Bookmark/Publication (in browser, not popup) - www.bibsonomy.org/postBookmark, www.bibsonomy.org/postPublication ***/
  
  /* Separator */
  .separator span {
    background-color: #222222;
  }
  
  p.separator {
    border-bottom-color: #555555;
    color: #666666;
  }

  /* Suggestions dropdown for 'add publication' search */
  .tt-menu {
    background-color: #222222;
  }

  .tt-suggestion {
    color: #bbbbbb;
  }

  .tt-suggestion:hover, .tt-suggestion:focus, .tt-suggestion.tt-cursor {
    background-color: #303030;
    color: #bbbbbb;
  }

  
  /*** Clipboard - www.bibsonomy.org/clipboard ***/
  
/*   / * 'Activate Adobe Flash' message * /
  #QReader {
    background-color: #222222 !important;
  } */
  
  /*** Publication Page - www.bibsonomy.org/bibtex/ ***/
  
  /* Title */
  div[style="border-top: 1px #cbcbcb solid; clear:left"] {
    border-top-color: #555555 !important;
  }
  
  /* Preview Image */
  .carousel-inner > .item > a > img {
    filter: opacity(40%);
  }
  
  #carousel-documents {
    border-color: #555555;
  }
  
  /* 'Add URL' button */
  .btn-primary {
    background-color: #222222;
    border-color: #555555;
    color: #bbbbbb;
  }
  
  /* 'please select' dropdown button */
  .open > .dropdown-toggle.btn-default:focus {
    background-color: #303030;
    color: #c0c0c0;
  }
  
  /* Tags */
  .label-primary {
    font-size: 100%;
    background-color: #303030;
  }
  
  span.label.label-primary > a {
    color: #c0c0c0 !important;
  }
  
  /* Citation Type */
  
  hr {
    border-top-color: #555555;
  }
  
  .nav-tabs > li.active > a, 
  .nav-tabs > li.active > a:hover, 
  .nav-tabs > li.active > a:focus {
    background-color: #555555;
    color: #bbbbbb;
    border-color: #555555;
  }
  
  .nav > li > a:hover, .nav > li > a:focus {
    background-color: #303030;
    border-color: #555555;
  }
  
  .nav-tabs {
    border-bottom-color: #555555;
  }
  
  div.pub-citation-tab-content, div.pub-person-tab-content {
    border-color: #555555;
  }
  
  /* Error Page */
  img[src="/resources/image/404.jpg"] {
    filter: invert(79.9%);
  }
  
  /*** Edit own posts ***/
  
  .table-striped > tbody > tr:nth-of-type(2n+1) {
    background-color: #222222;
  }
  
  .table > thead > tr > th {
    border-bottom-color: #555555;
  }
  
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, 
  .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top-color: #555555;
  }
  
  input[type="checkbox"] {
    filter: invert(87%);
  }
  
  /*** Bookmark Page - www.bibsonomy.org/url/ ***/
  
  /* 'www' symbol */
  div[itemtype="http://schema.org/WebPage"] img[itemprop="image"] {
    filter: invert(87%);
  }
  
  /* Sub-heading underlines */
  .page-header {
    border-bottom-color: #555555;
  }
  
  /* Tags */
  #goldstandard .tagcloud .tag a {
    color: #aaaaaa;
  }
  
  #goldstandard .tagcloud .tag {
    border-color: #555555;
  }
  
  /* Users */
  #goldstandard .community-page-user-list li.user-card {
    border-color: #555555;
  }
  
  #goldstandard .community-page-user-list .user-avatar {
    opacity: 0.7;
  }
  
  /* Write Review/Write Comment */
  .btn-default:active, .btn-default.active {
    background-color: #555555;
    color: #c0c0c0;
    border-color: #555555;
  }
  
  img.simple-user-pic {
    opacity: 0.7;
  }
  
  /*** Tag Page - www.bibsonomy.org/tag/<tag> ***/
  
  /* Right-side Pane */
  
  .taglist li[class=""] {
    color: #444;
  }
  
  
  /*** Author - www.bibsonomy.org/person/ ***/
  
  .simplePub div:nth-child(2n) {
    background-color: #282828;
    border-top-color: #555555;
    border-bottom-color: #555555;
  }
  
  /*** Export - www.bibsonomy.org/export/ ***/
  .select2-container--default .select2-selection--single {
    background-color: #222222;
    border-color: #555555;
  }
  
  .select2-dropdown {
    background-color: #303030;
    color: #c0c0c0;
  }
  
  .select2-container--default .select2-results__option--highlighted[aria-selected] { /* on Hover */
    background-color: #233c64;
    color: #c0c0c0;
  }
  
  .select2-container--default .select2-results__option[aria-selected="true"] { /* previously selected */
    background-color: #555555;
  }
  
  /*** www.bibsonomy.org/concepts ***/
  #relations li ul li a, #relations td a {
    color: #057fd4 !important;
  }
  
  #relations li ul li a:hover, #relations td a:hover, 
  #relations li ul li a:focus, #relations td a:focus {
    color: #129bfa !important;
  }
  
  /*** Help Page ***/
  
  #help-sidebar {
    background-color: #303030;
  }
  
  #help-main img {
    filter: invert(87%);
  }
  
  /* Code snippets e.g. https://www.bibsonomy.org/help_en/Tags */
  code {
    background-color: #282828;
    color: #dc436a;
  }
  
  /***** reCAPTCHA *****/
  
  /* Bkgd */
  .rc-anchor-light {
    background-color: #222222;
    color: #aaaaaa;
  }
  
  .rc-anchor-light.rc-anchor-normal {
    border-color: #666;
  }
  
  /* Checkbox */
  .recaptcha-checkbox-border {
    background-color: #282828;
  }
  
  /** Image Page **/
  
  /* Top text */
  .rc-imageselect-desc-wrapper {
    color: #aaaaaa;
  }
  
  .rc-imageselect-candidates {
    border-color: #aaaaaa;
  }
  
  /* Image */
  div#rc-imageselect {
    background-color: #282828 !important;
  }
  
  /* Optional Message */
  .rc-imageselect-incorrect-response, 
  .rc-imageselect-error-dynamic-more, 
  .rc-imageselect-error-select-more, 
  .rc-imageselect-error-select-something {
    background-color: #282828;
  }
  
  /* Footer */
  .rc-separator {
    border-top-color: #282828;
  }
  
  .rc-footer {
    background-color: #282828;
  }
  
  .rc-button {
    filter: invert(87%);
  }
  
  .rc-challenge-help {
    color: #aaaaaa;
  }
}
