css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://personal.metrobankdirect.com/RetailInternetPortal/menu.do?"),  url-prefix("https://personal.metrobankdirect.com/RetailInternetPortal") {

body, body.login #wrapper {
  background: black url("http://wallpapercave.com/wp/0lzXkHy.jpg") fixed !important;
  background-size: 100% 100%;
}
  
.main-content {
  background: rgba(20, 0, 0, .5);
  color: orange;
  border-color: red;
  border-bottom-right-radius: 50px;
  border-bottom-left-radius: 10px;
  border-top-right-radius: 10px;
}
  
.form-details label {
  color: green;
  background: rgba(0, 0, 0, .4);
}
  
.form-details p.alt-row {
  background: rgba(30, 59, 87, 0.38) none repeat scroll 0% 0%;
}
  
h2, .list-table th.h3heading, tr {
  background: none;
  color: #7BFF00;
}
  
.list-table th {
  background: rgba(0, 30, 255, 0.23) none repeat scroll 0% 0%;
  text-decoration: none !important;
  text-shadow: none !important;
}
  
h2, .list-table th.h3heading {
  border-top: 1px solid #831717;
}
  
.list-table .total {
  border: 1px solid #831717;
}
  
.list-table .alt-row, .list-table .total {
  background: rgba(0, 0, 0, .5);
}
  
a, .form-details span {
  color: cyan;
}
  
.alt-row td a, .list-table td, label {
  color: aqua !important
}
  
h4 {
  color: red;
  background: rgba(0, 0, 50, .8);
}
  
a:hover, ul.no-bullets li:hover {
  background: blue;
}
  
#wrapper {
  background: rgba(0, 0, 0, .5);
  box-shadow: 0px 0px 40px 2px rgba(0, 0, 255, .4);
}
  
.box {
  background: none;
}
  
.box h1 {
  box-shadow: 0px 0px 10px 2px red;
  border-bottom-right-radius: 10px;
  border-bottom-left-radius: 10px;
}
  
#header #bottom-header #login-info {
  color: orange;
}
  
#top-header #advertisement, .box .bottom {
  display: none;
}
  
.box.customercare {
  background-image: none;
}
  
.box .content {
  margin-top: 10px;
  border-top: 1px solid red;
  border-left: 1px solid red;
  border-right: none;
  border-top-left-radius: 10px;
}
  
  body.login #wrapper .loginbox .tipsbox ul {
    background: rgba(0,0,0,0.6);
    border-top-right-radius: 5px;
    border-top-left-radius: 20px;
    border-bottom-right-radius: 50px;
    color: white
  }
  
  body.login #wrapper .loginbox .tipsbox h1 {
    background: rgba(0,40,0,0.7);
    color: lime;
    padding-top: 2px
  }
  
  body.login #wrapper .loginbox .box {
    background: url("https://38.media.tumblr.com/f290b5e1e8e812dc30b1ef528e75b619/tumblr_inline_msxkwk3CKV1qz4rgp.gif");
    background-size: 60% 60%;
    border-radius: 20px;
    border-bottom-left-radius: 60px;
    border-top: 1px solid;
    border-left: 1px solid;
    border-left-color: lime;
    border-top-color: lime;
    box-shadow: 0px 0px 20px 0px lime;
    width: 400px;
  }
  
  .error-message ul {
    background: black;
    padding: 3px
  }
  
  body.login #wrapper .loginbox .box .form .text-box {
    background: rgba(0,0,0,0.8);
    border: 1px solid #3CFF00;
    color: lime
  }
  
  #footer img {display: none}
  
  #main-container #right-col .tabs a, #main-container #right-col .tabs a.selected {
    background: none;
    background-color: rgba(0,0,50,0.5);
    border-top-right-radius: 10px
  }
  
  #main-container #right-col .tabs a span {
    background: none;
    background-color: rgba(0,200,0,0.4)}
  
  #main-container #right-col .tabs a.selected span {
    background: none;
    background-color: rgba(50,0,0,0.5);
    border-top-right-radius: 10px
  }
  
    div p font, p b, p {
  color: white !important
}

font a b, p b{
  color: red !important
}
  
}
