@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("https://books.google") {
  
  body {
    background-color: #333333;
    color: #c0c0c0;
  }
  
  /* Header */
  .gb_uf .gb_ve, .gb_ve {
    background-color: #404040;
  }
  
  .gbqfif, .gbqfsf {
    color: #aaaaaa;
  }
  
  .gb_re {
    background-color: #404040;
  }
  
  .gbqfqw { /* Search bar */
    background-color: #404040;
  }
  
  .ac-renderer { /* Search drop-down bkgd. */
    background-color: #404040;
  }
  
  .kd-appbar { /* Top middle bar */
    background-color: #2A2A2A;
  }
  
  .jfk-button-standard { /* Bar button text colors */
    color: #c0c0c0;
  }
  
  .goog-inline-block .jfk-button .jfk-button-standard {
    background-color: #2A2A2A;
  }
  
  /* Contents */
  .about_title {
    color: #c0c0c0;
  }
  
  div.toc_entry {
    color: #c0c0c0;
  }
  
  /* Bibiographic Info */
  .metadata_label, .metadata_value {
    color: #c0c0c0;
  }
  
  /* Left Side Bar */
  .gb-left-nav-list .gb-left-nav-link, .gb-left-nav-list .gb-left-nav-header {
    color: #c0c0c0;
  }
  
  .gb-left-nav-heading:link {
    color: #c0c0c0;
  }
  
  .goog-zippy-header:focus, .goog-zippy-header:hover { /* selected heading */
    background-color: #444444;
  }
  
  /* Middle Bar symbols */
  .kd-appbar {
    background-color: #444444;
  }
  
  .jfk-button-standard {
    background-color: #444444;
  }
  
  /* Links */
  a, a.primary, a.secondary, a.link_aux, a.link_aux:link, a.hp-bookshelf-link, a.subject_list_link, h3.slider_title a, .slider-annotation a, .gb-hovercard a.gb-hovercard-title, .gb-hovercard a.gb-hovercard-title:link, .buy-sale-terms a, #word_cloud a, #hp-bookshelves-create-btn {
    color: #5888db;
  }
  
  /* Book Preview */
  div#viewport { /* Background for scroll area */
    background-color: #444444;
  }
  
  .linkbar-panel-div, .scroll-toc-div-wide.scroll-toc-div { /* Contents */
    background-color: #404040;
  }
  
  .scroll-toc-div {
    background-color: #404040;
  }
  
  .toc-middle-cell { /* Links in contents */
    color: #5888db;
  }
  
/*   div.pageImageDisplay > div {
    background-color: #aaa;
  } */
  
  /** Related books **/
  /* Pop-up */
  .unified-hovercard {
    background-color: #333333;
    color: #c0c0c0;
    border-color: #333333;
    box-shadow: 0 0 8px #5b5b5b;
  }
  
  .unified-hovercard-footer {
    background-color: #333333;
  }
  
  .unified-hovercard a:link {
    color: #3b7cef;
  }
  
  /* review stars */
  .card-star-off {
    background-color: #333333;
    filter: invert(80%);
  }
  
  /* Options border bars */
  .slider-button {
    background-color: #333333;
  }
  
  .slider-button:hover {
    background-color: #5b5b5b;
  }
  
  /** Home Page **/
  /* Top bar */
  .gb_qf .gb_re {
    background-color: #333333;
  }
  
  /* bottom bar */
  .footerInner {
    background-color: #333333;
  }
  
  /** My library **/
  .header_bar {
    background-color: #333333;
  }
  
  img.gb-prof-card-default-img {
    filter: invert(80%);
  }
  
  .gb-prof-conn-box {
    background-color: #4c4c4c;
  }
  
  .slider-empty-shelf-content {
    background-color: #404040;
  }
  
  .gb-prof-card {
    background-color: #333333;
  }
  
  .dgd {
    background-color: #333333;
  }

}
