@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("jeuxvideo.com") {
	/* No wootbox */
	div#forum-right-col:nth-child(2) script + div {
		display: none;
	}
	/* No outbrain */
	.outbrain, #outbrain_widget_0 {
		display:none;
	}
	/* No banner */
	div.container:nth-child(5) {
		display: none;
	}
	/* No wootbox on footer */
	.footer-links {
		display: none;
	}
	/* No webedia logo */
	.footer-mentions-logo {
		display: none;
	}
	.footer-mentions-txt {
		text-align: center !important;
	}
/*[[menufixe]]*/
}
