css:
@-moz-document url-prefix("https://community.arm.com/message"), url-prefix("https://community.arm.com/thread") {
#jive-body .jive-thread-message,
.jive-content
{
    max-width: 50em;    
}

#jive-body .jive-thread-message span,
.jive-content .j-thread-post span
{
    display: block;
    max-width: 40em;
}

#jive-body .jive-thread-message p,
#jive-body .jive-thread-message ul,
#jive-body .jive-thread-message ol,
.jive-content .j-thread-post p,
.jive-content .j-thread-post ul,
.jive-content .j-thread-post ol
{
    max-width: 40em;
}

/* Force listings that con't fit in narrow column to break onto new lines: */
#jive-body .jive-thread-message pre,
.jive-content .j-thread-post pre
{
    overflow-wrap: break-word;
}

/**
 * Uncomment this to make listing of more similar size to body text:
*/
/*
#jive-body .jive-thread-message pre,
.jive-content .j-thread-post pre
{
    font-size: 75%;
}
*/

#jive-body .jive-thread-message li,
.jive-content .j-thread-post li
{
    max-width: 35em;
}

#jive-body .jive-thread-message img,
.jive-content .j-thread-post img
{
    max-width: 100%;
    height: auto;
}

#jive-body .jive-thread-message dp-highlighter,
.jive-content .j-thread-post dp-highlighter
{
        max-width: 100%;
}
}
