css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain(www.sachsen-fernsehen.de), domain(www.chemnitz-fernsehen.de), domain(www.dresden-fernsehen.de), domain(www.leipzig-fernsehen.de)  {
  
    
  * {
    box-shadow: unset !important;
  }
  
  .alternating {
    background-color:unset !important;
  }
  
  h3 {
     color: white;
  }
  
  .imageTeaserItem.listitem .border h2.headline {
    background-color: black;
  }
  
  .imageTeaserItem.listitem .border a  {
    color:white;    
  }
  
  body {
    background: #222;
    color: #ddd;
  }
  
  div.module.news div.detailview .date, div.module.news div.detailview .category {
    color: #fff;
    font-weight: bold;
}
  
  div.positions.right div.widget, div.positions.right div.sidebar, div.positions.right div.sidebar div.border {
    background-color: unset;
    background-image: unset;
  }
  
  .easySlider .sliderItem div.content {
    background-color: black;
  }
  
  .easySlider .sliderItem h2, .easySlider .sliderItem h2 a {
    color: white;
  }
  
  div.module.news div.detailview .teaser01 {
    color: #fff;
  }
  
  div.positions.left > div {
    background-color: unset;
    border: unset;
  }

  .listitem.teaserItem {
    background-color: #222;
  }
  
  div.left.fullsize div.module.news.list div.sfteaser a.category {
    background: black;
  }
  
  div.left.fullsize div.module.news.list div.sfteaser {
     border-collapse:  separate;
     border: 1px solid gray;
    width: 218px !important;
     
  }
  
    div.left.fullsize div.module.news.list div.sfteaser:hover {
   background-color: black;     
  }
  
  a:link {
    color: #ddd;
  }
  
  a:hover {
    color: #fff;
  }
  
  .thumbnailSlider .photo-meta-data {
    background-color: #000;
    color: #fff;
  }
  
 div.left.fullsize div.module.news.thumbnailSlider:hover .panel .photo-meta-data {
    background-color: #111;
  }
  
  .listitem.standardlist .category, .listitem.commonItem .category, div.overview .listitem span.date, .listitem.standardlist .simpleList a, .listitem.standardlist .simpleList span, .listitem.standardlist .comment span, .listitem.standardlist .comment a {
    color: #aaa;
  }
   
body.standardlayout div.preHeader a {
  color: #aaa !important;
  }
  
  div.positions.right .list.commonList {
    background: black;
  }
  
  .ui-widget-content {
    background: none;
    color: white;
  }
  
  
  div.positions.right .ui-tabs .ui-tabs-panel {
    border-left:1px solid #999;
    border-right:1px solid #999;
    border-bottom:1px solid #999;
    background-image: unset;
  }
 
  div.mainMenu, h3.sidebar, div.positions.right .ui-tabs .ui-tabs-nav, div.positions.right div.sidebar .header, input[type="submit"] {
    background-image: linear-gradient(rgb(50,50,50), black);
}
  
  body.standardlayout div.footer {
    background-color: black;
    background-image: unset;
    color: white;
    font-size: small;
  }
  
  body.standardlayout div.footer div.border {
    color:white;
  }
  
  body.standardlayout div.footer a {
    color: white;
  }
  
  div.zolaniscontainer {
    display: none;
  }
  
  div.header {
     filter: grayscale(100%) invert(100%)
  }
  
  
  div.module.news div.detailview div.container.mediaCount {
     background-color: unset;
  }
}
