css:
@-moz-document url-prefix('https://www.dreadcast.net/Forum') {
/*       'Nostalgia' by .1019      */
/*            DC Wikast            */

#header_logo {								/*Header du Forum*/
    position: absolute;
    top: 10px;
    left: 100px;
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

#header {									/*L'image du bandeau + quick checkup du personnage*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

body {										/*Le fond du forum*/
    background: #0e0e0e;
}

a {											/*Les liens*/
    color: rgb(66, 127, 175);
    text-shadow: none;
}

.link {										/*Les liens, bis*/
    cursor: pointer;
    color: rgb(66, 127, 175);
    text-shadow: none;
}

#menu_droite h3, #menu_gauche h3 {			/*Les + et - (actions, modérateurs, etc)*/
    color: #B0B0B0;
}

#main_content {								/*Le fond du forum centré*/
    color: grey;
    background: #222222;
    border: 1px solid #434343;
    border-bottom: 0px;
}

#main_content h1 {							/*Titre du forum de DreadCast*/
    color: rgb(66, 127, 175);
}

#main_content .sous_forum h3 {				/*Les noms des catégories*/
    color: rgb(66, 127, 175);
}

#main_content .sous_forum 
.forum_description {						/*Descriptions des catégories*/
    color: grey;
}

#main_content .info_stat {					/*Combien de sujets ?*/
    color: grey;
}

#main_content .info_last_modification {		/*Combien de messages ?*/
    color: grey;
}

#facebook {									/*Bouton FB sur la page d'accueil du forum*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    transition: 1s;
}

#facebook:hover {
    filter: grayscale(0%);
    -webkit-filter: grayscale(0%);
    -moz-filter: grayscale(0%);
    -ms-filter: grayscale(0%);
    -o-filter: grayscale(0%);
}

#twitter {									/*Bouton Twitter sur la page d'accueil du forum*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    transition: 1s;
}

#twitter:hover {
    filter: grayscale(0%);
    -webkit-filter: grayscale(0%);
    -moz-filter: grayscale(0%);
    -ms-filter: grayscale(0%);
    -o-filter: grayscale(0%);
}

#rss {										/*Bouton RSS sur la page d'accueil du forum*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
    transition: 1s;
}

#rss:hover {
    filter: grayscale(0%);
    -webkit-filter: grayscale(0%);
    -moz-filter: grayscale(0%);
    -ms-filter: grayscale(0%);
    -o-filter: grayscale(0%);
}

#liste_sujets .sujet.type2 .icon {			/*L'image des sondages sur la liste des sujets*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

#liste_sujets .sujet.verrouille .icon {		/*L'image du lock sur la liste des sujets*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#liste_sujets .sujet.type1 .icon {			/*L'image 'RP' sur la liste des sujets*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

#lock {										/*L'image lock sur le sujet*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

#liste_sujets .sujet.epingle {				/*Le fond des sujets épinglés sur la liste des sujets*/
    background-color: #2B2B2B;
}

#liste_sujets .sujet.vu {					/*Le fond des sujets déjà visités sur la liste des sujets*/
    background-color: #222;
}

#liste_sujets .sujet {						/*Le fond des sujets pas encore visités sur la liste des sujets*/
    background-color: #171717;
}

#liste_sujets .sujet h3 {					/*Couleur des titres sujets sur la liste des sujets*/
    color: rgb(66, 127, 175);
}

h1#nom_sujet {								/*Titre des topics*/
    color: #B0B0B0;
    font-weight: bold;
    font-size: 25px;
    width: 600px;
}

#main_content .navigation_sujet 
.goto_first {								/*Image aller à la première page*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .navigation_sujet 
.goto_previous {							/*Image aller à la précédente*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .navigation_sujet 
.goto_next {								/*Image aller à la suivante*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .navigation_sujet 
.goto_last {								/*Image aller à la dernière*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .bas_de_page {				/*Aller en bas de la page*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .retour {						/*Aller en bas de la page*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .navigation_sujet 
.goto_previous {							/*Aller en bas de la page*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .favori.vu {					/*Image favori*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .haut_de_page {				/*Image aller vers le haut*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#pin {										/*Image du post-it*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#liste_sujets .sujet.epingle .icon {		/*Image du post-it*/
    filter: invert(100%) grayscale(100%);
    -webkit-filter: invert(100%) grayscale(100%);
    -moz-filter: invert(100%) grayscale(100%);
    -ms-filter: invert(100%) grayscale(100%);
    -o-filter: invert(100%) grayscale(100%);
}

#main_content .navigation_sujet 
.nb_pages {									/*Navigation des pages*/
    color: grey;
    cursor: pointer;
}

#visibilite_sujet {							/*"Visible par"*/
    color: grey;
}

#liste_messages .bandeau {					/*Les bandeaux avec le nom, la date, l'ID du post*/
    color: grey;
    background-color: #313131;
}

#liste_messages .bandeau .description {		/*La date*/
    color: grey;
}

#liste_messages .personnage {				/*Les bandeaux avec l'avatar, le titre, le nom de compte et EDC*/
    background-color: #313131;
}

#liste_messages .bandeau.highlight, 
#liste_messages .message.highlight 
.personnage {								/*Quand un personnage fait une balise @Personnage*/
    background: #4A4A4A;
    color: black;
}

#main_content .texte .quote {				/*L'effet citation: comprend aussi le bandeau du FP*/
    background: #434343;
    border: 1px solid #525252;
    color: #A0A0A0;
}

#main_content .texte .invisible {			/*Invisible is invisible*/
    color: #222222;
}

#main_content .quote .invisible {			/*Invisible is invisible: LE RETOUR*/
    color: #434343;
}

#main_content .bleu {						/*Couleur bleue dans les posts*/
    color: #4C7696;
}

#main_content .noir {						/*Couleur noire dans les posts*/
    color: #000;
}

#main_content .quote .rouge {					/*Couleur rouge dans les quote*/
    color: #ce4040;
}

#liste_messages .delimitation {				/*Délimitation entre le post et la signature*/
     background-color: #313131;
}

#footer_sujet {								/*Le footer qui contient la réponse rapide, numéro de page et caetera*/
    color: grey;
    background: #313131;
}

#zone_smileys {								/*Zone des smileys sur la réponse rapide*/
    background: #434343;
    border: 1px solid #434343;
}

#zone_modif_texte {							/*Zone du BBCode sur la réponse rapide*/
    background: #434343;
    border: 1px solid #434343;
}

textarea {									/*La réponse rapide*/
    border: 1px solid #434343;
    color: grey;
    background: #222222;
}

.bouton {									/*Les boutons poster & prévisualiser*/
    border: 1px solid #434343;
    color: grey;
    background: #222;
}

#main {										/*Truc un peu chiant dans le footer qui fait que si on l'enlève y a une sorte de barre noire*/
 	background: #222;
}

#main_bordure_gauche_3 {					/*Coin gauche du footer (bas de page) juste sous le forum*/
    background: #222222;
    border-left: 1px solid #434343;
    border-bottom: 1px solid #434343;
}

#main_bordure_droite_3 {					/*Coin droit du footer juste sous le forum*/
    background: #222222;
    border-right: 1px solid #434343;
    border-bottom: 1px solid #434343;
}

#main_bordure_bas {							/*Footer du bas, juste sous le forum*/
    background: #222222;
    border-bottom: 1px solid #434343;
}

#footer {									/*Le footer*/
    background: #0E0E0E;
    color: grey;
}
}
