css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tr.instela.com") {
  
body {
  background-color: #EBEBEB;
}

header.main_header {
  background-color: #383838;
  border-bottom: 1px solid #191919;
}

header.main_header #top_buttons .floating_opener > a {
  border-right: 0px solid;
  border-left: 0px solid;
}

.fixed-title-header .logotipo a {
  background: url(https://s.instela.com/m/instela--i696380.jpg);
}

.fixed-title-header {
  background: #383838;
}

.fixed-title-header .featured a {
  padding: 11px 8px 10px 8px;
  color: #fff !important;
}

.alternative-header-lamps li > a, .alternative-header-lamps li > a:hover, .alternative-header-lamps li.sahsim .floating a:hover {
  color: #fff !important;
}

.panelmenu .content > ul > li > a:hover {
  background-color: #383838;
}

.panelmenu .content > ul > li > a {
  color:#000;
}

a {
  text-decoration: none !important;
}

h1, h2, h3 a {
  color: #383838 !important;
  text-decoration: none !important;
}

h1 a {
  color: #383838 !important;
  text-decoration: none !important;
}

h2 a {
  color: #383838 !important;
  text-decoration: none !important;
}

h2 a:hover {
  color: #383838 !important;
  text-decoration: none !important;
}

.entries > article h2 {
  color: #383838 !important;
  text-decoration: none !important;
}

.entries .entry_header .dateauthor > a.hop {
  color: #383838 !important;
  text-decoration: none !important;
}

.new-entry-input.focus textarea {
  font-size: 13px;
}

#bgleft a {
  text-decoration: none !important;
  color: #383838;
}

#bgleft .mode-select > ul > li > a:hover {
  color: #333;
  text-decoration: none !important;
  background-color: #f1f1f1;
}

#bgleft .mode-select .floating > li > a:hover {
  background-color: #383838;
}

body.no-touch #solframe li:hover {
  border-left:4px solid #383838;
}

#bgleft #solframe {
  font-size: 14px;
}

#solframe li span.title-section {
  color: #383838;
}
  
#solframe.mode-mostpopular li span.title-section {
    font-size: 14px;
}

.right-section a {
  color: #383838;
  font-size: 13px;
}

.related-titles-right .badge {
  background: #383838;
  color: #fff;
}

.related-titles-right .related-link {
  padding: 3px 0;
}

.related-titles-right .related-link h3 {
  margin-left: 10px;
  margin-bottom: 0;
  margin-right: 10px;
  font-size: 14px;
}
  
span.new-count, .related-titles-right .related-link h3 > span:last-child {
    background-color: #383838;
    color: #fff;
}

.right-box.duyurular .duyuru a, .right-box.duyurular .related a, .right-box.duyurular .top20 a, .right-box.related .duyuru a, .right-box.related .related a, .right-box.related .top20 a {
  display: block;
  border-bottom: 1px solid #e2e2e2;
  text-align: left;
  padding: 3px 10px 3px 10px;
  color: #000;
  font-size: 14px;
}
  
.related-link .entry-statistics {
  color: #7a7a7a;
  font-size: 14px;
}

.gosterpager select {
  background-color: #fff;
}

.gosterpager a {
  color: #383838;
  background-color: #fff;
}

body.no-touch .gosterpager a:hover{
  background-color: #383838;
}

.new-entry-input:not(.focus) {
  border: 1px solid #cfcfcf;
  font-size: 13px;
}

.entries .entry_text {
  font-size: 13px;
}
  
.embed-link > span {
   background-color: #f4f4f4;
   border: 1px solid #e5e5e5;
}
    
.embed-link .link-source {
  font-size: 14px;
  color: #383838;
  margin-top: -12px;
}

.embed-link .link-title {
  font-size: 14px;
  color: #383838;
}

.embed-link.with-image .link-content {
  min-height: 0px;
}

.embed-link .link-description, .embed-link img {
  display: none;
}

span.new-count, .sf_section > span.news  {
  background-color: #383838;
}

body.no-touch .featured .sf_desc a::after, body.no-touch .featured a::after {
  background: #383838;
  height: 2px;
}

.index-header .selector li > a::after {
  background: #383838;
  height: 2px;
}

.new-entry-input:not(.focus) {
  display: none;
}

.view-all-page {
  background: #383838;
  font-size: 14px;
}

input[type="button"], input[type="submit"], .but {
  color: #fff !important;
  background-color: #383838;
}

.index-header .selector li > a:after  {
  background:none repeat scroll 0% 0% #383838;
}

.selector li:hover a {
  color:#fff !important;
}

.index-header .selector li > a {
  color: #000 !important;
}

.profile > .profile_header a:hover {
  color: #383838;
}

.right-section footer.dip {
  display: none;
}

footer.dip {
  border-top: 15px solid #383838;
}

}
