css:
@-moz-document domain("streamup.com") {
body,
.partner p,
.partner .more,
.partner h1,
.partner h2,
.partner h3,
.partner h4
{
    color: #BFBFBF !important;
}

p.notice {
    background-color: #2787E4 !important;
}

strong { color: #4EBAE6; }

html,
body,
.header,
.header .search,
.channel-page,
.channel-page .header,
.footer,
.channel-wrap {
    background-color: #1F1F1F;
}
.channel-profile .channel-title {
    color: #d4d4d4;
}
.channel-profile .username a {
    color: #C2C4C5;
}
.about .hero.bottom,
.home-categories,
.header .search .search-input,
.chat .chat-rows-wrap .chat-rows,
.chat-wrap .chat,
.chat-text-bar .new-chat-line,
.viewers {
    background-color: #000;
}
.chat-row .chat-row-content,
.viewers-list .chat-row .chat-row-user a,
.chat-suggestions .chat-row .chat-row-user a,
.chat-text-bar .new-chat-line {
    color: #CACACA;
}

.header .search .search-input {
    background-color: transparent;
}

.about .hero.bottom,
.home-categories,
.header,
.header .search,
.chat-tipping,
.chat-wrap,
.chat-wrap .chat,
.chat .chat-text-bar,
.chat-text-bar .new-chat-line {
    border-color: #757575;
}
.dropdown-menu {
    background-color: #1E90FF;
}
.dropdown-menu>li>a {
    color: #E0E0E0;
}
.about .hero.bottom,
.dropdown-menu>li>a.special {
    color: #fff;
}
.channel-wrap {
    padding: 0;
}
.chat-wrap {
    height: 45%;
    top: 55% !important;
    z-index: 1000;
    opacity: 0;
}
.chat-wrap:hover {
    opacity: 0.85;
}

body.channel-page {
    padding-top: 0;
}

body.channel-page .header {
    opacity: 0;
}

body.channel-page .header:hover {
    opacity: 0.85;
}

.modal-content {
    background-color: #1D1B1B;
}

}
