css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://survivethedistance.com/forums") {
  body {
    background-color: #1E1E1E;
  }
  
  span[itemprop="title"] {
    color: #FFF;
  }
  
  div[class="breadcrumb-line clearfix"] {
    background-color: #3F3F46;
  }
  
  div[class="breadcrumb-line clearfix"] > ul[class="breadcrumb"] > li > a {
    background-color: #3F3F46;
  }
  
  .section-page {
    background-color: #1E1E1E;
  }
  
  .btn-default {
    background-color: #2D2D30;
  }
  
  .input-group-addon {
    background-color: #2D2D30;
    border-color: #2D2D30;
    color: #ADADAD;
  }
  
  .btn:active, .btn:focus {
    background-color: #30302F !important;
    color: #6E6E85 !important;
  }
    
  .btn-default:hover, .btn:hover {
    background-color: #3F3F46;
    color: #6E6E85;
  }
  
  .btn {
    background-color: #2D2D30;
    color: #6E6E85;
  }
  
  .form-control {
    background-color: #3F3F46;
    border-color: #2D2D30;
    color: #ADADAD !important;
  }
  
  h3 > a:hover {
    color: #A0A0A0 !important;
  }
  
  h3 {
    border: 0px !important;
  }
  
  div.col-md-8 > div.pull-right > ul > li.active > a {
    background-color: #3F3F46;
    border-color: #2D2D30;
  }
  
  div.col-md-8 > div.pull-right > ul > li > a {
    background-color: #3F3F46;
    border-color: #2D2D30;
  }
  
  div.col-md-8 > div.pull-right > ul > li > a:focus, div.col-md-8 > div.pull-right > ul > li > a:active {
    background-color: #2D2D30;
    color: #6E6E6E;
  }
  
  div.col-md-8 > div.pull-right > ul > li > a:hover {
    background-color: #2D2D30;
    color: #6E6E6E;
  }
  
  div.clearfix > div.pull-right > ul > li.active > a {
    background-color: #3F3F46;
    border-color: #2D2D30;
  }
  
  div.clearfix > div.pull-right > ul > li > a {
    background-color: #3F3F46;
    border-color: #2D2D30;
  }
  
  div.clearfix > div.pull-right > ul > li > a:hover {
    background-color: #2D2D30;
    color: #6E6E6E;
  }
  
  div.clearfix > div.pull-right > ul > li > a:focus, div.clearfix > div.pull-right > ul > li > a:active {
    background-color: #2D2D30;
    color: #6E6E6E;
  }
  
  .panel-inner > table > thead > tr > th {
    background-color: #2D2D30 !important;
    color: #6F6F76;
  }
  
  .panel-inner > table > tbody > tr > td {
    background-color: #3F3F46 !important;
    color: #6F6F76;
  }
  
  .panel-inner > table > tbody > tr > td:hover {
    background-color: #3F3F46 !important;
    color: #6F6F76;
  }
  
  ul[class="pagination pagination-forum pagination-xs pull-right hidden-sm hidden-xs"] > li > a {
    background-color: #3F3F46;
    border-color: #2D2D30;
  }
  
  ul[class="pagination pagination-forum pagination-xs pull-right hidden-sm hidden-xs"] > li > a:active {
    background-color: #2D2D30;
    color: #6E6E6E;
  }
  
  ul[class="pagination pagination-forum pagination-xs pull-right hidden-sm hidden-xs"] > li > a:hover {
    background-color: #2D2D30;
    color: #6E6E6E;
    border-color: #2D2D30;
  }
  
  a.topictitle, a.forumtitle {
    color: #7E7E95;
  }
  
  a.topictitle:hover, a.forumtitle:hover {
    color: #FFF;
  }
  
  .table-primary tbody tr td, .table-primary tbody tr th {
    color: #8D8D93 !important;
  }
  
  span.icon-wrapper {
    background-color: #2D2D30;
  }
  
  .table > thead > tr > th, .table > tbody > tr > th, .table > tfoot > tr > th, .table > thead > tr > td, .table > tbody > tr > td, .table > tfoot > tr > td {
    border-top: 1px solid #3F3F46;
  }
  
  article > div.panel {
    background-color: #3F3F46;
  }
  
  article > div.panel > div.panel-heading {
    border: 0px;
  }
  
  div.panel-body {
    padding-top: 0px !important;
  }
  
  div.postbody > div > div.content {
    color: #CFCFCF;
  }
  
  div.avatar-over {
    border-color: #000;
  }
  
  .post-icon {
    display: none;
  }
  
  i[class="fa fa-ellipsis-h fa-2x"] {
    display: none;
  }
  
  p[class="separator text-center hidden-xs"]::before {
    background-color: transparent;
    border-top: 1px dashed #6E6E85;
  }

  .content > blockquote, blockquote > blockquote {
    background-color: #2D2D30 !important;
  }
  
  .content > blockquote:before, blockquote:after, blockquote > blockquote:before, blockquote:after {
    color: gray !important;
  }
  
  .separator {
    color: #4F4F46;
    text-decoration: none;
  }
  
  .panel-forum > .panel-body{ 
   padding-bottom: 0;
  }
  
  .panel-body, .panel-heading {
    background-color: #3F3F46 !important;
  }
  
  .section-page {
    padding-top: 0px;
  }
  
  .section-page > div#rtblock > div#fltl > div > div[class="panel panel-post"] {
    background-color: #2D2D30 !important;
  }
  
  .section-page > div#rtblock > div#fltl > div > div[class="panel panel-post"] > .panel-bottom{
    background-color: #2D2D30 !important;
  }
  
  .well {
    background-color: #1E1E1E;
    border: 0px;
    padding-bottom: 0px;
  }
  
  fieldset.form-actions {
    padding-top: 0px !important;
    background-color: #1E1E1E;
    border: 0px;
  }
  
  .panel.has-nav-tabs.panel-default {
    border: 0px;
  }
  
  label {
    color: #ADADAD !important;
  }
  
  .help-block {
    color: #ADADAD;
  }
  
  .nav.nav-tabs {
    background-color: #3F3F46;
  }
  
  .nav.nav-tabs > li.active > a {
    background-color: transparent !important;
    color: #EEE;
  }
  
  .nav.nav-tabs > li > a:hover {
    background-color: transparent !important;
    color: #EEE;
  }
  
  .panel.panel-default {
    border-color: #666;
  }
}
