css:
@-moz-document domain("forum.teksyndicate.com") {
  /*! TekSyndicate Dark Forum Theme v1.0 05/03/2016 
  * GitHub: https://github.com/sarhatabaot/discourse-teksyndicate
  * https://userstyles.org/styles/125193
  * License: https://creativecommons.org/licenses/by-sa/4.0/
  */
html, .topic-list > tbody > tr, .topic-area {
  background-color: #0d0d0d !important;
}
p {
  line-height: 1.5;
}
.lazyYT-container {
  width:100% !important;
  padding-bottom: 56.25% !important;
}
.ytb-large-play-button {
  transform: none !important;
}
.html5-title-text {
    font-size: 16px;
}
body > div:first-child {
  padding: 0 !important;
}

#post_1 .topic-body, #post_1 .topic-avatar {
  border-top: none;
}

.topic-list .avatar.latest:not(.single) {
  box-shadow: 0 0 4px 1px #cfcfcf;
}

.user-right .item.notification {
  background-color: #0d0d0d !important;
}

#main-outlet .container {
  background: none;
}

.topic-list th, .topic-list td {
  padding: 18px 10px;
}

.show-more.has-topics {
  top: 2px;
}

.topic-list > tbody > tr:first-of-type, .topic-list > tbody > tr {
  border-top: none;
  border-bottom: none;
}

.topic-map {
  background-color: #0d0d0d !important;
  border: none;
}

.topic-map section {
  border-top: none;
}

.docked .d-header {
  background-color: #1a1a1a !important;
  border-top: none;
  border-bottom: 2px solid #262626;
}

body .grippie {
  border-top: 1px solid #262626;
  background-color: #262626;
}

#reply-control {
  transition: height 0.35s ease-in-out;
  background-color: #1a1a1a;
}

#reply-control .d-editor {
  border: 1px solid #1a1a1a;
}

#reply-control .contents .d-editor-preview {
  border: 1px dashed #1a1a1a;
}

#reply-control .wmd-control .d-editor-input, #reply-control .wmd-control .d-editor-preview, #reply-control .wmd-control .d-editor-button-bar {
  background-color: #0d0d0d;
}

#reply-control .d-editor-button-bar {
  border-bottom: 2px solid #262626;
}

#reply-control .wmd-controls .d-editor-input, #reply-control .wmd-controls .wmd-control .d-editor-preview, #reply-control .wmd-control .wmd-controls .d-editor-preview, #reply-control .wmd-controls .d-editor-button-bar, #reply-control .wmd-controls .d-editor-preview, #reply-control .d-editor-button-bar {
  background-color: #0d0d0d;
}

body input[type="text"], body input[type="password"], body input[type="datetime"], body input[type="datetime-local"], body input[type="date"], body input[type="month"], body input[type="time"], body input[type="week"], body input[type="number"], body input[type="email"], body input[type="url"], body input[type="search"], body input[type="tel"], body input[type="color"] {
  background-color: #1a1a1a;
  border: 1px solid #595959;
  border-radius: 3px;
  box-shadow: inset 0 2px 2px rgba(0, 0, 0, 0.3);
}

body input[type="text"]:focus, body input[type="password"]:focus, body input[type="datetime"]:focus, body input[type="datetime-local"]:focus, body input[type="date"]:focus, body input[type="month"]:focus, body input[type="time"]:focus, body input[type="week"]:focus, body input[type="number"]:focus, body input[type="email"]:focus, body input[type="url"]:focus, body input[type="search"]:focus, body input[type="tel"]:focus, body input[type="color"]:focus {
  border-color: #666666;
  outline: 0;
  box-shadow: inset 0 1px 1px rgba(0, 0, 0, 0.3), 0 0 8px gray;
}

.select2-container {
  border: 1px solid gray;
}

.select2-container .select2-choice, .select2-container .select-container-active, .select2-container .select2-dropdown-open a.select2-choice, .select2-dropdown-open .select2-container a.select2-choice {
  border: 1px solid gray;
}

.select2-container .select-container-active {
  box-shadow: gray 0px 0px 6px 0px;
}

.select-drop-active {
  border: 1px solid gray;
  border-top: 0;
}

.badge-category-parent-bg, .badge-category-bg {
  width: 5px !important;
}

.alert.alert-info {
  background-color: #21262c;
}

.menu-panel {
  background-color: #1a1a1a;
  border: 1px solid #404040;
}

.lightbox-wrapper .meta {
  background-color: #1a1a1a;
}

code[class*=lang-], pre[class*=lang-] {
  text-shadow: 0 1px black;
  font-family: Consolas, "Courier New", Courier, monospace;
  text-align: left;
  direction: ltr;
  white-space: pre;
  word-spacing: normal;
  word-break: normal;
  line-height: 1.5;
  -moz-tab-size: 4;
  -o-tab-size: 4;
  tab-size: 4;
  -webkit-hyphens: none;
  -moz-hyphens: none;
  -ms-hyphens: none;
  hyphens: none;
}

pre[class*=lang-] {
  padding: 1em;
  margin: .5em 0;
  overflow: auto;
  border-radius: 0.3em;
}

:not(pre) > code[class*="lang-"], code {
  padding: .1em;
  border-radius: .3em;
}

.hljs {
  display: block;
  overflow-x: auto;
  padding: 0.5em;
  background-color: #404040;
  color: #dbdbdb;
}

.hljs-keyword,
.hljs-selector-tag,
.hljs-tag {
  color: #e3ceab;
}

.hljs-template-tag {
  color: #dbdbdb;
}

.hljs-number {
  color: #8cd0d4;
}

.hljs-variable,
.hljs-template-variable,
.hljs-attribute {
  color: #efddbd;
}

.hljs-literal {
  color: #efefae;
}

.hljs-subst {
  color: #8f8f8f;
}

.hljs-title,
.hljs-name,
.hljs-selector-id,
.hljs-selector-class,
.hljs-section,
.hljs-type {
  color: #efef8f;
}

.hljs-symbol,
.hljs-bullet,
.hljs-link {
  color: #dca3a3;
}

.hljs-deletion,
.hljs-string,
.hljs-built_in,
.hljs-builtin-name {
  color: #cc9393;
}

.hljs-addition,
.hljs-comment,
.hljs-quote,
.hljs-meta {
  color: #7f9f7f;
}

.hljs-emphasis {
  font-style: italic;
}

.hljs-strong {
  font-weight: bold;
}

}
