css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("delicious.com") {

  /*
  * Main Page
  */
  .content-wrapper {
    background-color:rgb(48,48,48) !important;
    /* background-color: #171717 !important; */
  }

  /* Link Tags */
  /* Default */
  .item .meta a {
    color: #cccccc;
  }
  /* On hovering */
  .item .meta a:hover {
    color: #ffffff;
  }
  
  /* Link Comment */
  /* Default */
  .item .note {
    color: #999999;
  }
  /* On hovering */
  .item:hover .note {
    color: #cccccc;
  }
  
  
  
  /*
  * Pop-up Window for Add/Edit Link
  */
  
  /* Whole Background */
  .modal-content {
    background-color: #333333;
  }
  
  /* Private links */
  .item.private {
    background-color: #363636;
  }
  
  /* Date */
  .modal-content .item-status {
    background-color: #333333;
  }
  
  /* URL */
  input#inputLinkUrl.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Title */
  input#inputLinkTitle.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Tags */
  input#tags.form-control.tag-input {
    background-color: #333333;
    color: #cccccc;
  }
  
  .inline-help {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Tags Dropdown */
  .tags-input-container .tag-input-candidates {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* Tag suggestions */
  /* on hover */
  .tag-suggestion .tag-suggestion-item:hover {
    color: #cccccc;
  }
  
  /* Comment */
  .form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /* buttons */
  .modal-content .modal-footer {
    background-color: #333333;
  }
  
  /*
  * Discover Links
  */
  input#tag-query.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  /*
  * Network Links
  */
  /* Default */
  .item.item-rich .note {
    color: #999999;
  }
  /* On hovering */
  .item.item-rich .note:hover {
    color: #cccccc;
  }
  
  /*
  * Profile Page
  */
  input#display_name.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#url.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#email.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#original_password.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#new_password.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  input#new_password_confirm.form-control {
    background-color: #333333;
    color: #cccccc;
  }
  
  div.load-more {
    background-color: #333333;
    color: #cccccc;
  }
}
