css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://survivethedistance.com/forums/") {
  /*
   *  ---
   *  FONTS
   *  ---
   */
  @font-face {
    font-family: 'Gudea';
    font-style: normal;
    font-weight: 400;
    src: local('Gudea'), url(http://fonts.gstatic.com/s/gudea/v4/WTDyO8MdshuMhAnoLO0WMw.woff2) format('woff2');
  }
  
  @font-face {
    font-family: 'bebas_neueregular';
    src: url('http://survivethedistance.com/fonts/BebasNeue-webfont.eot');
    src: url('http://survivethedistance.com/fonts/BebasNeue-webfont.eot?#iefix') format('embedded-opentype'),
         url('http://survivethedistance.com/fonts/BebasNeue-webfont.woff') format('woff'),
         url('http://survivethedistance.com/fonts/BebasNeue-webfont.ttf') format('truetype'),
         url('http://survivethedistance.com/fonts/BebasNeue-webfont.svg#bebas_neueregular') format('svg');
    font-weight: normal;
    font-style: normal;
  }
  
  /*
   *  ---
   *  BODY
   *  ---
   */
  body {
    margin-top: 80px;
    background-color: #000000;
    font-family: "Gudea","Source Sans Pro","Arial","Helvetica",sans-serif;
  }
  
  /*
   *  ---
   *  NAVBAR
   *  ---
   */
  .navbar {
    position: fixed;
    top: 0;
    left: 0;
    right: 0;
    width: 100%;
  }
  .navbar-inverse {
    background-color: #303030;
    box-shadow: 0 0 5px #000000;
    border-color: #303030;
  }
  .navbar-inverse .navbar-nav > li > a,
  .navbar-inverse .navbar-nav > li > a:hover,
  .navbar-inverse .navbar-nav > li > a:focus {
    color: #c6d4ff;
  }
  .navbar-inverse .navbar-nav > .open > a,
  .navbar-inverse .navbar-nav > .open > a:hover,
  .navbar-inverse .navbar-nav > .open > a:focus,
  .navbar-inverse .navbar-nav > .active > a,
  .navbar-inverse .navbar-nav > .active > a:hover,
  .navbar-inverse .navbar-nav > .active > a:focus {
    background-color: #404040;
    color: #dde5ff;
  }
  .navbar-nav-fancy > li::before {
    background-color: #404040;
    border-radius: 0;
  }
  
  /*
   *  ---
   *  HEADER
   *  ---
   */
  .masthead::before {
    transform: none;
    animation: none;
  }
  
  .flat-header h1 {
    text-transform: uppercase;
  }
  .flat-header h1 .label {
    display: none;
  }
  
  /*
   *  ---
   *  BREADCRUMBS
   *  ---
   */
  .breadcrumb-line {
    background-color: #353535;
    border-radius: 0;
  }
  .breadcrumb li:first-child a {
    background-color: #454545;
    border-radius: 0;
  }
  
  /*
   *  ---
   *  FORUM PAGE
   *  ---
   */
  .table > thead > tr > th {
    background-color: #303030;
  }
  .table > tbody > tr > td {
    background-color: #151515 !important;
    border-top: 0;
  }
  .table > tbody > tr:hover > td {
    background-color: #1a1a1a !important;
  }
  .table .stats-col {
    background-color: #151515 !important;
  }
  
  .footable.breakpoint > tbody > tr > td > span.footable-toggle {
    background-color: #404040;
  }
  
  a.forumtitle,
  a.topictitle {
    color: #ffffff;
  }
  .icon-wrapper {
    background-color: transparent;
    border-radius: 0;
  }
  
  /* Login part from the forum page down below */
  
  
  /*
   *  ---
   *  TOPIC PAGE
   *  ---
   */
  article h3,
  article h3 a {
    font-size: 24px;
    font-family: "Gudea","Arial","Helvetica",sans-serif;
    text-transform: none;
    border-color: #303030 !important;
  }
  .panel-heading {
    border-color: #303030;
  }
  
  .avatar-over > .status::before {
    bottom: 0;
    right: 9px;
    width: 14px;
    height: 14px;
    border: 0;
    border-radius: 0;
  }
  
  .panel-default {
    border-color: #202020;
  }
  
  .postbody .content {
    color: #b0b0b0;
  }
  
  .content blockquote {
    padding: 0.3em 3em;
    background-color: #404040;
    border: 2px solid #303030;
    border-radius: 0;
  }
  .content blockquote::before,
  .content blockquote::after {
    font-family: "bebas_neueregular","Arial","Helvetica",sans-serif;
  }
  .content blockquote::before {
    top: 0.45em;
    left: 10px
  }
  .content blockquote::after {
    right: 10px;
  }
  
  a.postlink,
  a.postlink:visited,
  a.postlink:hover,
  a.postlink:active,
  a.postlink:focus {
    border-bottom: 0;
  }
  a.postlink {
    color: #c6d4ff;
  }
  a.postlink:visited {
    color: #ffffff;
  }
  a.postlink:hover,
  a.postlink:active,
  a.postlink:focus {
    color: #dde5ff;
    text-decoration: underline;
  }
  
  .separator::before {
    height: 2px;
    background-color: #404040;
  }
  .separator i {
    display: none;
  }
  
  .notice {
    border-top-color: #404040;
  }
  
  .post-signature img {
    display: inline-block;
  }
  
  /*
   *  ---
   *  MEMBER PAGE
   *  ---
   */
  .mini-profile-contact li a {
    border-radius: 0;
  }
  .mini-profile-contact li a.default-contact {
    background-color: #606060;
  }
  
  /*
   *  ---
   *  LOGIN PAGE
   *  ---
   */
  /* Referring to the forum page */
  .panel-login {
    background-color: #202020;
  }
  /* Reactivating the remove of the background color from the login page */
  .login-full {
    background-color: transparent;
  } 
  
  .panel-login .signin-text {
    font-family: "bebas_neueregular","Arial","Helvetica",sans-serif;
    font-size: 24px;
    color: #ffffff;
  }
  .panel-login .signin-text::before {
    background-color: #303030;
  }
  .panel-login .signin-text span {
    background-color: #303030;
  }
  
  .panel-w-form .form-group input[type="text"],
  .panel-w-form .form-group input[type="password"]{
    padding-left: 35px;
  }
  .login-form-icon {
    padding: 1px 0;
    border-right: 0;
  }
  
  /*
   *  ---
   *  FOOTER
   *  ---
   */
  .section-page {
    background-color: #151515;
  }
  
  .mastbottom .call-to-action {
    font-family: "bebas_neueregular","Arial","Helvetica",sans-serif;
    font-size: 36px;
  }
  .mastbottom .btn-danger {
    background-color: #c6d4ff;
    color: #454545 !important;
  }
  
  .section-pre-footer {
    background-color: #303030;
    box-shadow: 0 -2px 5px #151515;
  }
  .section-pre-footer .section-bg {
    background-color: #353535;
  }
  .section-pre-footer a:hover {
    color: #606060;
  }
  
  .burned-asphalt {
    background-color: #252525;
  }
  .footer-nav a:hover {
    color: #454545;
  }
  
  /*
   *  ---
   *  BACK TO TOP BUTTON
   *  ---
   */
  .top-flat {
    border-radius: 0;
  }
  
  /*
   *  ---
   *  DROPDOWN MENUS
   *  ---
   */
  .dropdown-menu {
    background-color: #404040;
    box-shadow: 0 5px 5px #000000;
    border: 0;
    border-radius: 0;
  }
  .dropdown-menu > li a {
    border-radius: 0;
  }
  .dropdown-menu > li > a:hover,
  .dropdown-menu > li > a:focus {
    background-color: #303030;
    color: #ffffff;
  }
  .dropdown-menu .dropdown-header {
    color: #c6d4ff;
  }
  .dropdown-menu .divider {
    margin: 5px 0;
    height: 2px;
    background-color: #303030;
  }
  .open .dropdown-toggle.btn-default,
  .open .dropdown-toggle.btn-default:hover,
  .open .dropdown-toggle.btn-default:focus {
    background-color: #404040;
    border-color: #404040;
    color: #e0e0e0;
  }
  
  .user-login-drop > li:first-child {
    background-color: #202020;
  }
  .user-login .navbar-footer {
    background-color: #202020;
  }
  .arrow-up::before {
    border-color: transparent transparent #202020;
  }
  
  .search-input-fullscreen {
    background-color: #404040;
  }
  .search-input-fullscreen .close {
    text-shadow: none;
  }
  
  /*
   *  ---
   *  PANELS
   *  ---
   */
  .panel {
    background-color: #202020;
    border-radius: 0;
  }
  .panel-poll {
    border-color: #404040;
  }
  
  .panel-default > .panel-heading,
  .panel-forum > .panel-heading {
    background-color: #404040;
    border-color: #404040;
    border-radius: 0;
    color: #ffffff;
  }
  .panel-default > .panel-heading + .panel-collapse .panel-body {
    border-top: 0;
  }
  .panel-title > a {
    font-family: "Gudea","Arial","Helvetica",sans-serif;
  }
  
  .panel-footer {
    background-color: #202020;
    border-top: 0;
  }
  
  .panel-post {
    margin-bottom: 10px;
    background-color: #202020;
  }
  .panel-post,
  .panel-post .title,
  .panel-post .title .entry-header,
  .panel-post .title .entry-header h6,
  .panel-post .title .entry-header h6 > a,
  .panel-post .content-post,
  .panel-post .content-post p {
    max-height: none;
  }
  .panel-post .title,
  .panel-post .content-post {
    margin-left: 40px;
    text-align: left;
  }
  .panel-post .title {
    margin-top: 0;
    padding-left: 0;
  }
  .panel-post .blog-meta {
    top: 10px;
    left: 10px;
    padding: 5px;
    background-color: #404040;
    border-radius: 0;
    font-family: "bebas_neueregular","Arial","Helvetica",sans-serif;
    font-weight: normal;
    font-size: 18px;
    color: #ffffff;
  }
  .panel-post .title h6,
  .panel-post .title h6 > a{
    font-family: "Gudea","Arial","Helvetica",sans-serif !important;
    font-size: 16px;
    text-transform: none;
  }
  .panel-post .panel-bottom {
    background-color: #303030;
    border-radius: 0;
  }
  .panel-post .panel-bottom .btn:hover,
  .panel-post .panel-bottom .btn:focus {
    color: #ffffff;
  }
  
  /*
   *  ---
   *  PAGINATION
   *  ---
   */
  .pagination,
  .pagination-sm > li:first-child > a,
  .pagination-sm > li:last-child > a {
    border-radius: 0;
  }
  .pagination > li > a {
    background-color: #202020;
    border-color: #303030;
  }
  .pagination > .active > a {
    background-color: #303030;
    border-color: #303030;
  }
  .pagination > li > a:hover,
  .pagination > li > a:active,
  .pagination > li > a:focus,
  .pagination > .active > a:hover,
  .pagination > .active > a:active,
  .pagination > .active > a:focus {
    background-color: #404040;
    border-color: #404040;
  }
  .pagination > li > a:hover,
  .pagination > li > a:active,
  .pagination > li > a:focus {
    color: #c6d4ff;
  }
  
  /*
   *  ---
   *  NAV-TABS
   *  ---
   */
  .nav-tabs {
    background-color: #151515;
    border-radius: 0;
  }
  .nav-tabs > li > a:hover,
  .nav-tabs > li > a:focus {
    background-color: #202020;
    border-radius: 0;
    color: #ffffff;
  }
  .nav-tabs > li.active > a,
  .nav-tabs > li.active > a:hover,
  .nav-tabs > li.active > a:focus {
    background-color: #202020;
    border-radius: 0;
    color: #c0c0c0;
    cursor: pointer;
  }
  
  /*
   *  ---
   *  LIST GROUPS
   *  ---
   */
  .list-group .list-group-item:first-child,
  .list-group .list-group-item:last-child {
    border-radius: 0;
  }
  
  .list-group a.list-group-item {
    background-color: #303030;
    border-color: #404040;
  }
  .list-group a.list-group-item:hover,
  .list-group a.list-group-item:focus {
    background-color: #404040;
    border-color: #404040;
    color: #ffffff;
  }
  .list-group a.list-group-item.active {
    background-color: #404040;
  }
  .list-group a.list-group-item.active:hover,
  .list-group a.list-group-item.active:focus {
    color: #c6d4ff;
  }
  
  /*
   *  ---
   *  MINI-TIMELINE
   *  ---
   */
  .mini-timeline::before {
    display: none;
    top: 19px;
    left: 20px;
    bottom: 61px;
    background-color: #606060;
    z-index: 1;
  }
  .mini-timeline > li:last-child::before {
    display: none;
    border-color: #606060;
  }
  
  .mini-timeline > li .timeline-icon {
    top: 19px;
    left: 15px;
    background-color: #000000;
    border-radius: 0;
    z-index: 1;
  }
  .mini-timeline > li .timeline-body {
    margin-left: 0;
    padding: 15px 15px 15px 45px;
    background-color: #303030;
  }
  .mini-timeline > li .time {
    height: auto;
  }
  
  .mini-timeline li:last-child .timeline-body {
    padding-top: 0;
  }
  
  /*
   *  ---
   *  TIMELINE
   *  ---
   */
  .ui-timeline::before {
    background-color: #303030;
  }
  
  .ui-timeline .tl-item .tl-body .tl-content {
    background-color: #404040;
    border-radius: 0;
  }
  .ui-timeline .tl-item .tl-body .tl-content::after {
    border-color: transparent #404040 transparent transparent;
  }
  .ui-timeline .tl-item.alt .tl-body .tl-content::after {
    border-color: transparent transparent transparent #404040;
  }
  
  .ui-timeline .tl-item .tl-body .tl-entry .tl-icon.square-icon {
    background-color: #000000;
    border-color: #202020;
    border-radius: 0;
    box-shadow: 0 0 0 6px #303030;
  }
  
  /*
   *  ---
   *  MINI PROFILE (MODAL POPUP)
   *  ---
   */
  .modal-dialog .modal-content {
    background-color: #202020;
    border-radius: 0;
  }
  .modal-dialog .modal-content .modal-header {
    background-color: #303030;
    border-bottom: 0;
  }
  .modal-dialog .modal-content .modal-header .close {
    color: #ffffff;
    text-shadow: none;
  }
  .modal-dialog .modal-content .modal-footer {
    background-color: #151515;
    border-top: 0;
  }
  .modal-dialog .modal-content .modal-footer .mini-profile-contact {
    margin-bottom: 0;
  }
  
  /*
   *  ---
   *  AVATARS
   *  ---
   */
  .profile-avatar img,
  .dropdown-avatar img {
    border: 0;
    border-radius: 0;
  }
  .navbar-avatar img {
    border: 0;
    border-radius: 0;
  }
  .avatar-over img {
    border: 0;
    border-radius: 0;
  }
  .jp-views img {
    border-radius: 0;
  }
  
  /*
   *  ---
   *  MISC. THINGS
   *  ---
   */
  .alert {
    border-radius: 0;
  }
  .alert .close {
    color: #ffffff;
    text-shadow: none;
  }
  .alert-default {
    background-color: #606060;
    border-color: #606060;
    color: #ffffff;
  }
  .alert-info {
    background-color: #869AAF;
    border-color: #869AAF;
    color: #ffffff;
  }
  
  .noti-bubble::before,
  .noti-bubble::after {
    border-color: #606060 rgba(255, 255, 255, 0) rgba(255, 255, 255, 0);
  }
  
  .well {
    background-color: #303030;
    border-color: #404040;
    border-radius: 0;
  }
  
  .progress {
    background-color: #808080;
    border-radius: 0;
  }
  
  /*
   *  ---
   *  HEADINGS
   *  ---
   */
  h1, h2, h3, h4, h5, h6, h1 a, h2 a, h3 a, h4 a, h5 a, h6 a {
    font-family: "bebas_neueregular","Arial","Helvetica",sans-serif;
  }
  h1 {
    font-size: 40px;
  }
  h2 {
    font-size: 36px;
    border-bottom-color: #303030;
  }
  h3 {
    font-size: 30px;
    border-bottom-color: #303030;
  }
  h4 {
    font-size: 24px;
  }
  h5 {
    font-size: 20px;
  }
  h6 {
    font-size: 18px;
  }
  
  .side-segment h2::after,
  .side-segment h3::after {
    background-color: #404040;
  }
  
  .section-posting h3,
  .section-mcp h3,
  .section-ucp h3 {
    border-bottom-color: #303030;
  }
  
  /*
   *  ---
   *  LINKS
   *  ---
   */
  a {
    color: #c6d4ff;
  }
  a:hover,
  a:active,
  a:focus {
    color: #dde5ff;
  }
  
  /*
   *  ---
   *  LINK BUTTONS
   *  ---
   */
  .btn {
    border-radius: 0;
  }
  .btn-default {
    background-color: #404040;
  }
  .btn-default:hover,
  .btn-default:focus,
  .btn-default:active {
    background-color: #606060;
  }
  .btn-sm, .btn-group-sm > .btn {
    border-radius: 0;
  }
  
  /*
   *  ---
   *  FIELDSET AND BUTTONS
   *  ---
   */
  .framed-ucp {
    border-color: #404040;
    border-radius: 0;
  }
  
  legend {
    font-family: "Gudea","Arial","Helvetica",sans-serif;
  }
  
  .form-actions {
    background-color: #202020;
    border-color: #404040;
    border-radius: 0;
  }
  
  .input-group > .form-control,
  .input-group-sm > .form-control {
    border-radius: 0;
  }
  .inpout-group > .input-group-btn > .btn,
  .input-group-sm > .input-group-btn > .btn {
    border-radius: 0;
  }
  .input-group-addon {
    background-color: #404040;
    border-color: #606060;
    border-radius: 0;
    color: #a0a0a0;
  }
  .input-group-addon:last-child {
    border-left: 2px solid #606060;
  }
  
  .form-control {
    background-color: #404040;
    border-color: #606060;
    border-radius: 0;
    color: #c0c0c0;
  }
  .form-control:focus {
    border-color: #a0a0a0;
  }
  .form-control[disabled],
  .form-control[readonly],
  fieldset[disabled] .form-control {
    background-color: #ee836e;
    color: #ffffff !important;
  }
  
  .control-label {
    color: #c0c0c0;
  }
  .control-group {
    border-bottom-color: #606060;
  }
  
  @media all and (max-width: 767px) {
    .section-pre-footer .section-bg {
      background-color: transparent;
    }
  }
}
