css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("tvgids.tv") {
  
    /* menu */  
  .navbar .navbar-inner {
    background: #fff;
    border-bottom: 4px solid red;
  }
  
  .navbar .navbar-search .search-query {
    width: 175px;
  }
  
  .dropdown-menu {
    top: 90%;
  }
  
  
  /* hide like box */
  #like-box {
   display: none;
  }
  
  
  /* wider content column */
  .span47.offset1 {
    width: 945px;
    margin: 0;
  }
  
  .span16,
  .span30 {
    display: none;
  }
  
  .section-item.now-and-later .channel,
  .section-item.now-and-later .now,
  .section-item.now-and-later .later {
    padding-top: 5px;
  }
  
  .section-item.now-and-later .now,
  .section-item.now-and-later .later {
    width: 450px;
  }
  
  .section-item.now-and-later .later .content,
  .section-item.now-and-later .now .content {
    width: 100%;
    padding: 0 5px;
  }
  
  
  /* smaller progress bar */
  .progress {
    height: 4px;
  }
  
  
  /* a bit more space */
  .intro {
    margin-bottom: 30px;
  }
  
  
  /* home - smaller rows */
  .section-item img {
    width: 44px;
    margin-bottom: 0;
  }
  
  .section-item p {
    margin-bottom: 0;
  }
  
}
