css:
@-moz-document domain('banking.fidor.de') {


#my_products_index .header-info .total-balance h3 {
    color: #7DAD03;
    font-size: 25px;
    font-weight: 700;
    text-align: right;
    line-height: 55px;
    margin: 0 15px 0 0;
}

body {
    margin: 0;
    font-family: Arial,"Helvetica Neue",Helvetica,sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #D0D0D0;
    fill: #7DAD03;
    background-color: #fff;
}


#ewallet_index #main_content, #corporate_fidorpays_show #main_content {
    background: #000000 !important;
    overflow: hidden;
}


#ewallet_index #main_content .fidorpay-konto-info, #corporate_fidorpays_show #main_content .fidorpay-konto-info {
    height: 156px;
    background-color: #000000 !important;
}


.pfm .axis text {
    font-family: Arial,"Helvetica Neue",Helvetica,sans-serif;
    font-size: 10.5px;
    fill: #FF920D;
}

body.classic #footer #navigation-footer-wrapper ul#navigation-footer li {
    color: #474747;
    font-size: 11px;
    /* text-shadow: 0px 1px 0px #EDEDED; */
    line-height: 18px;
    display: inline;
    line-height: 50px;
}

body.classic {
    background: #121212;
}


.row {
    margin-left: -20px;
    background-color: black;
}


#ewallet_index #main_content, #corporate_fidorpays_show #main_content {
    background: #000000 url(https://assets01.fidor.de/banking/assets/fidorpay/background-image-1918aa3f74a7ede74758b2c6f37e7db7.png) no-repeat 20px top;
    overflow: hidden;
}



a {
    color: #F0A00B;
    text-decoration: none;
}
#121212
#ewallet_index #main_content, #corporate_fidorpays_show #main_content {
    background: #121212 no-repeat 20px top;
    overflow: hidden;
}
body {
    margin: 0;
    font-family: Arial,"Helvetica Neue",Helvetica,sans-serif;
    font-size: 13px;
    line-height: 18px;
    color: #D0D0D0;
    background-color: #fff;
}




#corporate_fidorpays_show h1, #corporate_fidorpays_service_center h1, #corporate_mails_index h1, #corporate_mails_show h1, #ewallet_index h1 {
    color: #D0D0D0;
    font-size: 22px;
    font-weight: 400;
    margin-top: 0;
    margin-bottom: 10px;
}


#ewallet_index .bank_1 .fidorpay-box-lb-wrapper a.toggle-lb-body, #ewallet_index .bank_3 .fidorpay-box-lb-wrapper a.toggle-lb-body, #corporate_fidorpays_show .bank_1 .fidorpay-box-lb-wrapper a.toggle-lb-body, #corporate_fidorpays_show .bank_3 .fidorpay-box-lb-wrapper a.toggle-lb-body {
    color: #7DAD03;
    background: transparent url(https://assets01.fidor.de/banking/assets/shared/icon/arrow-black-chevron-close-79ef3e6ff04e4291d0c803b32ee3c53c.png) no-repeat 315px center;
    background-size: 15px 15px;
    font-size: 16px;
    display: block;
    line-height: 58px;
}


#ewallet_index #main_content .fidorpay-konto-info, #corporate_fidorpays_show #main_content .fidorpay-konto-info {
    height: 156px;
    background-color: #000000;
}


#ewallet_index #main_content, #corporate_fidorpays_show #main_content {
    background: #000000;
    overflow: hidden;
}


.welcome-section .account-number-info {
    background: rgba(0, 0, 0, 0);
    border-top: solid 1px #4EB93A;
    border-bottom: solid 1px #4EB93A;
    float: right;
    font-size: 12px;
    width: 220px;
    list-style-type: none;
    margin-top: 20px;
    padding: 6px 8px;
}

#ewallet_index .bank_2 .box .highlight, #corporate_fidorpays_show .bank_2 .box .highlight {
    border-radius: 4px 4px 0 0;
    background-color: rgba(0, 0, 0, 0);
    overflow: hidden;
    margin: -10px -10px 10px -10px;
}



.community-toolbar>a#carousel-control-next {
    left: auto;
    color: orange;
    background: #191919;
    right: -28px;
    text-decoration: none;
    box-shadow: 0 0 0 4px rgba(0,0,0,0.2);
}


.community-toolbar>a {
    position: absolute;
    left: -28px;
    top: 10px;
    z-index: 10;
    display: block;
    width: 55px;
    height: 55px;
    outline: none;
    text-indent: -9999px;
    border-radius: 30px;
    box-shadow: 0 0 0 4px rgba(0,0,0,0.2);
    background: #191919;
}


#ewallet_index .bank_1 .fidorpay-box-lb-wrapper, #ewallet_index .bank_3 .fidorpay-box-lb-wrapper, #corporate_fidorpays_show .bank_1 .fidorpay-box-lb-wrapper, #corporate_fidorpays_show .bank_3 .fidorpay-box-lb-wrapper {
    position: relative;
    border-color: #FF920D;
}

.community-toolbar {
    position: relative;
    margin: 0 0 45px 0;
    height: 76px;
    overflow: hidden;
    border-top: solid 1px #F0780F;
    border-bottom: solid 1px #F0780F;
    background: #F8F8F8;
}


.community-toolbar ul a {
    -webkit-transition: background-color 0.3s ease-in-out;
    -moz-transition: background-color 0.3s ease-in-out;
    transition: background-color 0.3s ease-in-out;
    background: #121212;
    position: relative;
    display: block;
    padding: 52px 0 0 0;
    width: 100%;
    height: 24px;
    font-size: 15px;
    text-align: center;
    color: #7DAD03;
}

.cfont, .community-toolbar ul a {
    font-family: "RobotoCondensedLight", "Gill Sans", "Trebuchet MS", Helvetica, sans-serif;
}



.community-toolbar ul a:before {
    color: #7DAD03;
    font-size: 40px;
    position: absolute;
    left: 50%;
    top: 6px;
    margin-left: -18px;
    z-index: 5;
}

.btn {
    display: inline-block;
    padding: 4px 12px;
    margin-bottom: 0;
    font-size: 13px;
    line-height: 18px;
    text-align: center;
    vertical-align: middle;
    cursor: pointer;
    color: #000;
    text-shadow: 0 1px 1px rgba(255,255,255,0.75);
    background-image: none;
    background-color: #7DAD03;
       background-repeat: no-repeat;
    border-color: #e6e6e6 #e6e6e6 #bfbfbf;
    border-color: rgba(0,0,0,0.1) rgba(0,0,0,0.1) rgba(0,0,0,0.25);
    border: none;
    border-bottom-color: #b3b3b3;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
    -webkit-box-shadow: inset 0 1px 0 rgba(255,255,255,0.2),0 1px 2px rgba(0,0,0,0.05);
    -moz-box-shadow: inset 0 1px 0 rgba(255,255,255,0.2),0 1px 2px rgba(0,0,0,0.05);
    box-shadow: inset 0 1px 0 rgba(255,255,255,0.2),0 1px 2px rgba(0,0,0,0.05);
}

body.classic {
    background: #121212;
}




