css:
@-moz-document domain("web.skype.com") {
:root .swx {
    color: #D3DAE3;
}
:root .swx .themeLight.chat {
    background-color: #2F343B;
}
:-webkit-scrollbar-track {
    background-color: transparent;
}
:root .swx .Me-small:before {
    border-right: 1px solid #353A42;
}
:root .swx.short #overlayPluginCallback .dual.buttonRow .alignLeft {
    border-right: 1px solid #353A42;
}
:root .swx .Me-skyContainer {
    background-color: #292D33;
}
:root .swx .Me-skyContainer:before {
    background-color: transparent;
}
:root .swx .Me-skyContainer:after {
    background-color: transparent;
}
:root .swx .Me-sky {
    background-color: transparent;
    box-shadow: 0 0 0 0px #55CAF5, 0 0 0 0px #8EDCF8;
}
:root .swx .Me-sky:before {
    background-color: transparent;
}
:root .swx .Me-sky:after {
    background-color: transparent;
}
:root .swx.themeBlue,
:root .swx .themeBlue {
    fill: #2F343B;
    color: #D3DAE3;
    background-color: #2F343B;
}
:root .swx .themeBlue .tile.online .presence::before,
:root .swx .themeBlue .tile.mobile .presence::before,
:root .swx .themeBlue .tile.idle .presence::before,
:root .swx .themeBlue .tile.busy .presence::before,
:root .swx .themeBlue .tile.offline .presence::before,
:root .swx .themeBlue .tile.callForwarded .presence::before,
:root .swx .themeBlue .tile.unknown .presence::before,
:root .swx .themeBlue .tile.blocked .presence::before {
