    color: #555E6B;
}
:root .swx a {
    color: #3EC2AE;
}
:root .swx a:hover,
:root .swx a:focus {
    color: #5DE3CF;
}
:root .swx .chat .conversationHeader .headerMain .roster .grid .tile.conversationTopic .tileText > .tileName + .tileMessage::before {
    display: none;
}
