}
:root .swx .chat .conversationHeader::after {
    border-bottom: 1px solid #353A42;
}
:root .swx .hyperscroll .rail:hover,
:root .swx .hyperscroll.scrolling .rail {
    background-color: rgba(0, 0, 0, 0.2);
    border-color: transparent;
    opacity: 0.9;
    border-radius: 6px;
}
:root .swx .hyperscroll.scrolling .rail .slider,
:root .swx .hyperscroll:hover .rail .slider {
    background: rgba(255, 255, 255, 0.3);
    border-color: transparent;
}
:root .swx .themeLight .tile.online .presence::before,
:root .swx .themeLight .tile.mobile .presence::before,
:root .swx .themeLight .tile.idle .presence::before,
:root .swx .themeLight .tile.busy .presence::before,
:root .swx .themeLight .tile.offline .presence::before,
:root .swx .themeLight .tile.callForwarded .presence::before,
:root .swx .themeLight .tile.unknown .presence::before,
:root .swx .themeLight .tile.blocked .presence::before,
:root .swx .themeLight .tile.online .presence::after,
:root .swx .themeLight .tile.mobile .presence::after,
:root .swx .themeLight .tile.idle .presence::after,
:root .swx .themeLight .tile.unknown .presence::after {
    background-color: #2F343B;
}
:root .swx .tile > .tileAvatar > .tileImage {
    background-color: #2F343B;
}
:root .swx.themeWhite,
:root .swx .themeWhite {
    background-color: #292D33;
    fill: #FFF;
    color: #D3DAE3;
}
:root .swx .chat .input.active {
    background-color: #2F343B;
}
:root .swx .chat .input form textarea {
    color: #596370;
}
:root .swx .chat .input.active form textarea {
    color: #D3DAE3;
}
:root .swx .chat .input form textarea::-moz-placeholder {
    color: #596370;
}
:root .swx .chat .input.active form textarea::-moz-placeholder {
    color: #515A66;
}
:root .swx .themeWhite.side::before,
:root .swx .meApp .summary {
    border-right: 1px solid #353A42;
}
:root .swx .meApp .skyContainer {
    background-color: #292D33;
}
:root .swx .meApp .skyContainer::after,
:root .swx .meApp .skyContainer::before {
    display: none;
}
:root .swx .meApp .skyContainer::before,
:root .swx .meApp .skyContainer .sky {
    display: none;
}
