:root .swx .tile.mobile .presence::before,
:root .swx .tile.idle .presence::before,
:root .swx .tile.busy .presence::before,
:root .swx .tile.offline .presence::before,
:root .swx .tile.callForwarded .presence::before,
:root .swx .tile.unknown .presence::before,
:root .swx .tile.blocked .presence::before {
    background-color: #292D33;
}
:root .swx .themeWhite.side .recent.active,
:root .swx .themeWhite.side .searchItem.active {
    background-color: rgba(0, 0, 0, 0.2);
}
