:root .swx .themeWhite .tile.unknown .presence::before,
:root .swx .themeWhite .tile.blocked .presence::before {
    background-color: #292D33;
}
:root .swx .themeWhite .tile.online .presence::before,
:root .swx .themeWhite .tile.mobile .presence::before,
:root .swx .themeWhite .tile.idle .presence::before,
:root .swx .themeWhite .tile.busy .presence::before,
:root .swx .themeWhite .tile.offline .presence::before,
:root .swx .themeWhite .tile.callForwarded .presence::before,
:root .swx .themeWhite .tile.unknown .presence::before,
:root .swx .themeWhite .tile.blocked .presence::before {
    background-color: #292D33;
}
:root .swx .themeWhite.side .navigation .navItem .icon,
:root .swx .themeWhite.side .navigation .navItem .text,
:root .swx .search .input .icon {
    color: #3EC2AE;
}
:root .swx .search .input input::-moz-placeholder,
:root .swx .search .input.active input::-moz-placeholder {
    color: #39514D;
}
:root .swx .search .input.active input {
    color: #B7BDC4;
}
:root .swx .search .splitter {
    background-color: #353A42;
}
:root .swx .themeLight .btn::before,
:root .swx .themeLight .btn.primary::before {
    background-color: #3EC2AE;
}
