    background-color: #3EC2AE;
}
:root .swx .themeBlue .btn.secondary:hover::before {
    background-color: #5DE3CF;
}
:root .swx .themeWhite.side .recent.active .tile.online .presence::before,
:root .swx .themeWhite.side .recent.active .tile.mobile .presence::before,
:root .swx .themeWhite.side .recent.active .tile.idle .presence::before,
:root .swx .themeWhite.side .recent.active .tile.busy .presence::before,
:root .swx .themeWhite.side .recent.active .tile.offline .presence::before,
:root .swx .themeWhite.side .recent.active .tile.callForwarded .presence::before,
