}
:root .swx swx-expression-picker .bubble {
    background-color: #2F343B;
    color: #D3DAE3;
    border: 1px solid #3EC2AE;
    border-radius: 4px;
    box-shadow: 0px 0px 10px 0px rgba(0, 0, 0, 0.1);
}
:root .swx swx-expression-picker .arrow-down {
    display: none;
}
#footer {
    display: none;
}
:root .swx .themeWhite .timeline .recent.unread:not(.active) .message,
:root .swx .themeWhite .timeline .recent.unread:not(.active) .message a {
