css:
@-moz-document domain("wikia.com"){
/* customize for yourself which obtrusive ads (and other content) get filtered from display at _your_ browser */
/* #wikiaBranding,             */
/* if you are a conscientious objector then you *could* remove wikia's branding but ...                 */
/* #wikia_logo,                */
/* the main piece of Wikia branding                                                                     */
/* #headerButtonHub,           */
/* link just below wikia logo                                                                           */
/* #wiki_logo,                 */
/* some communities put a lot of effort into creatiung a nice logo for the community but ...            */
/* #background_strip,          */
/* recovering vertical space used by wiki_logo adversely affects top navigation bar (wikia_header)      */
/* #cockpit,                   */
/* no good reason for filtering this - its used for managing your widgets and is easily dismissed       */
/* #navigation_widget,         */
/* no good reason for filtering the main navigation box but ...                                         */
/* #request_wiki,              */
/* if you are clumsy and sometimes click "Create a new wiki" instead of "Edit this page" then ...       */
/* #myhome-main,               */
/* does not recover the horizontal space used - therefore this is of questionable value to filter       */
/* #myhome-activityfeed,       */
/* "My Home" - opt for Special:RecentChanges & Special:Watchlist placed in the Monaco toolbox           */
/* #myhome-feed-more,          */
/*           - part2 of removing the activity feed & watch list (above)                                 */
/* #myhome-activity-feed-more, */
/*           - part3 of removing the activity feed & watch list (above)                                 */
/* #myhome-feed-switch,        */
/*           - part4 of removing the activity feed & watch list (above)                                 */
/* #myhome-feed-switch-default,*/
/*           - part5 of removing the activity feed & watch list (above)                                 */
/* #myhome-sidebar,            */
/* probably not a good idea to filter since admin customized "Community Corner" gets hidden as well     */
/* #myhome-user-contributions, */
/* "My Home" - a tab labeled "Contributions" is in plain sight in the usermasthead                      */
/* #myhome-hot-spots,          */
/* "My Home" - a matter of personal preference (note the section header is not removed)                 */
/* .WidgetAnswers,             */
/* this widget now has a dismiss X button in it's top right corner                                      */
/* .WidgetLanguages,           */
/* some wiki communities do translate pages into multiple languages for your benefit, so think twice    */
/* .avatar,                    */
/* avatars are eye-candy - some people love 'em while others hate them so ...                           */
/* .sprite,                    */
/*               ditto for sprites (unless they incorporate a useful link i.e. an HREF tag)             */
/* #mw-data-after-content,     */
/* Semantic MediaWiki enabled wikis display "Facts about PAGENAME" unless __NO_FACTBOX__ is used        */
/* #user_masthead_scorecard,   */
/* big numbers, big font, bold font - is someone overcompensating for an inadequate bra/jockstrap size? */
/* Non-essential forced content */

.WidgetCommunity,
/* a.k.a. "Latest activity" and previously labeled: "Community"                                         */

#spotlight_footer,
/* a special type of advertising featuring other Wikia hosted communities rather than vendors           */

#SPOTLIGHT_FOOTER_1,
#SPOTLIGHT_FOOTER_2,
#SPOTLIGHT_FOOTER_3,
.wikia_spotlight,
/*         ditto                                                                                        */
/* the remainder are each related to advertising - some are no longer in use but remain here just in case they are ever revived */

#HOME_LEFT_SKYSCRAPER_1,
#HOME_LEFT_SKYSCRAPER_2,
#HOME_LEFT_SKYSCRAPER_3,
#LEFT_SKYSCRAPER_1,
#LEFT_SKYSCRAPER_2,
#LEFT_SKYSCRAPER_3,
#HOME_TOP_LEADERBOARD,
#HOME_TOP_LEADERBOARD_iframe,
#HOME_TOP_RIGHT_BOXAD,
#TOP_LEADERBOARD,
#TOP_LEADERBOARD_iframe,
#TOP_RIGHT_BOXAD,
#PREFOOTER_LEFT_BOXAD,
#INCONTENT_BOXAD_1_load,
#WikiaArticleBottomAd,
#WIKIA_BAR_BOXAD_1_iframe,

/* new content 2016 */
.trc_rbox_container,
/********************/

.wikia_ad,
.wikia_ad_placeholder,
.WidgetAdvertiser {
    display: none !important;
}
}
