css:
@-moz-document domain(hkgalden.com){
  ::-moz-selection{background:#080!important;color:#0c0!important;font-weight:bold!important;}
  br{display:block!important;margin:0px 0!important;line-height:0px!important;content:""!important;}
  *{text-shadow:none!important;text-decoration:none!important;border:none!important;box-shadow:none!important;}
  html,body{background:#020!important;}
  /*top menu*/#gb{position:static!important;}
  /*top menu*/#gb.zx{position:static!important;font-size:initial!important;line-height:60px!important;height:60px!important;display:none!important;}
  /*top menu dropdown forum list*/#lgw>.seltp{position:static!important;background:#030!important;color:#ccc!important;}
  /*top menu dropdown forum list*/#lgw>.seltp>option:nth-child(5n){background:#060!important;}
}
@-moz-document regexp("http(s)?://hkgalden\.com/forum"){
  /*banner intro*/.nxt.tp-banner{background:#000!important;color:#ccc!important;}
  /*top bar*/#gb.zx,#gb{position:static!important;}
  /*forum rule*/div#rules{display:none!important;}
  /*forum list*/#ighd{overflow:hidden!important;}
}
@-moz-document regexp("http(s)?://hkgalden\.com/topic.*"){
  /*bottom footer*/body>footer,
  /*bottom rule*/#relativecontainer>#rules,
  /*bottom ad*/#tpglnks+div+.nxt.ihodr,
  /*ad*/#ad1,
  /*ad*/.adContainer{
    display:none!important;}
  
  /*galdon music*/.panel{background:#030!important;color:#bbb!important;}
  /*galdon music*/.panel-heading{background:#000!important;}
  /*galdon music*/.panel-heading a{color:#a11!important;}
  /*galdon music*/.panel-body table{background:inherit!important;color:#bbb!important;}
  
  /*main*/#main{margin:0px!important;}
  /*thread action*/.nxt{background:#000!important;color:#ccc!important;}
  /*thread action*/.ihodr a{color:#c60!important;}
  /*thread action*/.ihodr a:hover{background:#040!important;}
  /*thread search*/.lighearox{background:#333!important;}

  /*thread header*/#tpli.nxt>thead>tr{background:#000!important;}
  /*thread header*/#tpli.nxt>thead>tr th{color:#aaa!important;}
  /*thread BG*/    #tpli.nxt{background:#030!important;}
  /*thread BG*/    #tpli.nxt tr.dt{background:#040!important;}
  /*thread BG*/    #tpli.nxt tr:hover{background:#000!important;}
  /*thread*/       #tpli td,
  /*thread*/       #tpli th{padding:6px 0px!important;}
  
  
  
  
  
  /*th*/   #tpli.nxt *{min-width:initial!important;}
  /*th*/   #tpli.nxt thead>tr>th{display:none!important;}
  /*title*/#tpli.nxt thead>tr>.tnm{}
  /*page*/ #tpli.nxt thead>tr>.tpg{}
  /*name*/ #tpli.nxt thead>tr>.taf{}
  /*time*/ #tpli.nxt thead>tr>.tlt{}
  /*reply*/#tpli.nxt thead>tr>.trc{}
  /*like*/ #tpli.nxt thead>tr>.tgr{}
  
  /*tr*/   #tpli.nxt tbody>tr{}
  /*tr*/   #tpli.nxt tbody>tr:not(.dt):not(.lt):not(.blked){display:none!important;}/*bug*/
  /*block*/#tpli.nxt tbody>tr.blked{}
  /*block*/#tpli.nxt tbody>tr.blked>td{background:#000!important;}
  /*block*/#tpli.nxt tbody>tr.blked>td:hover{background:#222!important;}
  /*block*/#tpli.nxt tbody>tr.blked>td:hover a{color:#a60!important;}
  /*block*/#tpli.nxt tbody>tr.blked>td>a{color:#888!important;font-size:30px!important;line-height:100%!important;}
  /*block*/#tpli.nxt tbody>tr.blked>td>a>br{display:none!important;}
  /*tr*/   #tpli.nxt tbody>tr>td{line-height:100%!important;}
  /*icon*/ #tpli.nxt tbody>tr>.tic{width: 2%!important;}
  /*title*/#tpli.nxt tbody>tr>.tnm{width:62%!important;word-break:break-all!important;white-space:normal!important;}
  /*title*/#tpli.nxt tbody>tr>.tnm>a{color:#c60!important;font-size:34px!important;line-height:100%!important;}
  /*title*/#tpli.nxt tbody>tr>.tnm>a:visited{color:#560!important;}
  /*title*/#tpli.nxt tbody>tr>.tnm>a:hover{color:#c60!important;}
  /*page*/ #tpli.nxt tbody>tr>.tpg{width: 8%!important;}
  /*page*/ #tpli.nxt tbody>tr>.tpg i{color:#ccc!important;}
  /*name*/ #tpli.nxt tbody>tr>.taf{width:12%!important;word-break:break-all!important;white-space:normal!important;}
  /*name*/ #tpli.nxt tbody>tr>.taf>a{color:#0af!important;font-size:22px!important;}
  /*name*/ #tpli.nxt tbody>tr>.taf>a>br{display:none!important;}
  /*name*/ #tpli.nxt tbody>tr>.taf>a~*{display:none!important;}/*bug*/
  /*time*/ #tpli.nxt tbody>tr>.tlt{width: 8%!important;color:#aaa!important;font-size:20px!important;}
  /*reply*/#tpli.nxt tbody>tr>.trc{width: 4%!important;color:#0a0!important;font-size:34px!important;font-weight:bold!important;}
  /*like*/ #tpli.nxt tbody>tr>.tgr{width: 4%!important;color:#a11!important;font-size:34px!important;font-weight:bold!important;}
  
  
  
  
  
  /**/input,button,select{color:#ccc!important;}
  /*bottom page*/.hkga-pagin td:not(.opt){text-align:center!important;padding:0px!important;}
  /*bottom page*/.hkga-pagin td:not(.opt)>a{font-size:30px!important;line-height:44px!important;width:100%!important;display:block!important;}
  /*bottom page*/.hkga-pagin td>a{color:#c60!important;}
  /*bottom page*/.hkga-pagin td>a:hover{background:#063!important;color:#f60!important;}


  #main{width:100%!important;}
  #main>#relativecontainer{width:1200px!important;margin:auto!important;}
  
  @media screen and (max-width:1065px){
    #main>#relativecontainer{width:98%!important;}
    #tpli.nxt tr>.trc,
    #tpli.nxt tr>.taf{display:none!important;}
    #tpli.nxt tr>.tlt{width:10%!important;}
  }
}
@-moz-document regexp("http(s)?://hkgalden\.com/view/.*"){
  /**/
  #GoodRate,#BadRate{color:#0f0!important;}
  
  body{width:950px!important;margin:auto!important;}
  #main{width:100%!important;margin-top:0px!important;}
  #main>article{}
  #main~blockquote{display:none!important;}
  /*thread*/       article{}
  /*thread*/       article>.nxt{}
  /*thread*/       .nxt{background:#000!important;margin:0px!important;}
  /*thread*/       .nxt a{color:#c60!important;}
  /*thread action*/.nxt.ihodr td:hover{background:#063!important;}
  /*thread page*/  .nxt.hkga-pagin .opt select{color:#bbb!important;}
  /*thread title*/ article>.nxln{}
  /*thread title*/ .nxln{background:#000!important;margin:0px!important;padding:10px 0px!important;word-break:break-all!important;white-space:normal!important;}
  /*thread title*/ .nxln>div{color:#a11!important;font-size:40px!important;line-height:100%!important;}
  /*thread title*/ .nxln>div::after{display:none!important;}
  /*thread ad*/    article>.adContainer{}
  /*thread ad*/    .adContainer{display:none!important;}
  /*post*/         article>section{margin-bottom:0px!important;}
  /*post*/         section{max-width:950px!important;margin:auto!important;margin-top:24px!important;}
  /*author*/       section>.l{background:#030!important;width:110px!important;}
  /*author*/       section>.l>.dtl{padding:0px!important;margin:auto!important;width:100%!important;}
  /*author*/       section>.l>.dtl>.unm{}
  /*author name*/  section>.l .unm a,/*bug*/
  /*author name*/  section>.l>.dtl>.unm>a{display:block!important;margin:auto!important;padding-top:3px!important;height:0px!important;}
  /*author name*/  section>.l .unm a,/*bug*/
  /*author name*/  section>.l>.dtl>.unm>a{font-size:26px!important;line-height:100%!important;text-align:center!important;word-break:break-all!important;}
  /*author*/       section>.l>.dtl>.unm.bro{}
  /*author*/       section>.l>.dtl>.unm.sis{}
  /*author avatar*/section>.l img,/*bug*/
  /*author avatar*/section>.l>.dtl>img{width:100%!important;position:relative!important;right:110px!important;top:-20px!important;text-align:center!important;display:block!important;margin:auto!important;z-index:1!important;}
  /*author avatar*/section>.l img:hover,/*bug*/
  /*author avatar*/section>.l>.dtl>img:hover{z-index:10!important;}
  /*author class*/ section>.l:hover .label{display:none!important;}
  /*author class*/ section>.l>.dtl>.label{height:0px!important;}
  /*author class*/ section>.l .label .lv1,/*bug*/
  /*author class*/ section>.l>.dtl>.label>.lv1{display:none!important;}
  /*author class*/ section>.l .label .lv2,/*bug*/
  /*author class*/ section>.l>.dtl>.label>.lv2{height:0px!important;display:block!important;top:45px!important;position:relative!important;}
  /*content*/      section>.r{background:#040!important;padding-left:110px!important;text-align:left!important;}
  /*content*/      section>.r>.ctn{color:#ccc!important;font-size:34px!important;line-height:120%!important;padding:0px!important;min-height:0px!important;}
  /*content*/      section>.r>.ctn span[style*="large;"]{font-size:42px!important;line-height:105%!important;}
  /*content*/      section>.r>.ctn a{color:#c60!important;}
  /*content*/      section>.r>.ctn blockquote{color:#888!important;font-size:26px!important;line-height:110%!important;opacity:1!important;margin:0px!important;padding-left:30px!important;}
  /*content*/      section>.r>.ctn .hidden{background:none #030!important;padding:0px 0px 0px 30px!important;}
  /*content*/      section>.r>.ctn img[src*="/face/"]{width:40px!important;}
  /*content ft*/   section>.r>.bab{padding:0px!important;}
  /*content ft*/   section>.r .bab .hig,/*bug*/
  /*content ft*/   section>.r>.bab>.hig{color:#888!important;font-weight:bold!important;}
  /*content ft*/   section>.r .bab .rtx,/*bug*/
  /*content ft*/   section>.r>.bab>.rtx{color:#999!important;}
  /*content ft*/   section>.r>.bab>.ract{}
  /*content ft*/   section>.r .ract *,/*bug*/
  /*content ft*/   section>.r>.bab>.ract *{color:#a60!important;}
  /*content ad*/   section>.r>hr,
  /*content ad*/   section>.r>.ad{display:none!important;}
  /*content fix*/  section>.r~*{display:none!important;}
  /*OP*/           section.author{margin-top:0px!important;}
  /*OP*/           section.author>.r{}
  /*OP*/           section.author>.r .ad,
  /*OP*/           section.author>.r hr,
  /*OP*/           section.author>.r>.aderfly-ad{display:none!important;}
  /*OP replyCount*/section.author+.nxln{text-align:center!important;}
  /*OP replyCount*/section.author+.nxln>div{font-size:30px!important;line-height:100%!important;}
  /*OP replyCount*/section.author+.nxln>div>b{color:#0a0!important;}
  /*OP replyCount*/section.author+.nxln~.nxln{text-align:center!important;}
  /*reply*/        section.replies{}
  /*block*/        section.blked{height:50px!important;background:#000!important;}
  /*block*/        section.blked:hover{background:#222!important;}
  /*block*/        section.blked>a{font-size:30px!important;line-height:50px!important;color:#888!important;width:100%!important;display:block!important;}
  /*block*/        section.blked>a>br{display:none!important;}


  /*post ad*/
  /*quick reply*/#gae{background:#050!important;}
  /*quick reply*/#gae #ta{background:#030!important;color:#ccc!important;}
  /*quick reply preview*/.prevcon .sclbl{background:#030!important;color:#ccc!important;}

  /*author ad*/.aderfly-ad,
  /*bottom ad*/body>div.adContainer{
    display:none!important;}

  .gpt{background:transparent!important;}
  
  @media screen and (max-width:965px){
    body{width:98%!important;}
    /*author*/.gpt .l .dtl>img{width:75%!important;position:initial!important;right:0px!important;top:0px!important;}
    /*author*/.gpt .l .dtl>span.unm>a{font-size:20px!important;height:initial!important;margin-bottom:5px!important;}
    
  }
}
@-moz-document regexp("http(s)?://hkgalden\.com/(reply|post).*"){
  /*main*/.nxtb{background:#040!important;color:#ccc!important;}
  /*emoji panel*/#gae{background:#000!important;}
  /*emoji panel*/#gae .edt .btn{color:#0a0!important;}
  /*textarea*/#gae #ta{background:#030!important;color:#ccc!important;}
  /*button post*/.bwp>button{background:#030!important;color:#c60!important;}
  /*button post*/.bwp>button:hover{background:#060!important;}
  /*preview*/.prevcon{background:#030!important;color:#ccc!important;}
}
@-moz-document regexp("http(s)?://hkgalden\.com/user/replies"){
  /*my reply time*/#userReplies tr>*:nth-child(4){display:none!important;}
  th{color:#a11!important;}
  td{color:#ccc!important;padding:0px 5px!important;}
  td>a{color:#c60!important;font-size:24px!important;}
  table{background:#030!important;}
  tr:hover td{background:#000!important;}
}
@-moz-document regexp("http(s)?://hkgalden\.com/member/profile/.*"){
  .nxt{background:#030!important;}
  table{background:#040!important;}
  tr:hover td{background:#000!important;}
  td>a{color:#c60!important;}
  td{color:#bbb!important;}
  th{color:#a11!important;}
  .label u.lv1{color:#0a0!important;}
}
