css:
@-moz-document domain(eksisozluk.com) {
    /* sayfa arka planı */
    body.light-theme, body.dark-theme { background-color: rgb(205,205,205) ! important ; font-family: arial ! important; color: black ! important; }
    
    /* entry içeriği */
    #entry-list a { color: rgb(20,20,180) ! important }
    #entry-list { list-style-type: none ! important; }
    #entry-list li { padding-bottom: 5px ! important; border-bottom: 1px solid rgb(120,120,120) ! important }
    
    /* devamını göster */
    #entry-list .content { max-height: 100% ! important; font-size: 14px ! important;}
    #entry-list .read-more-link-wrapper { display: none ! important; }
    
    /* oylama butonları */
    #entry-list footer .like.voted { fill: #666 ! important;}
    #entry-list footer .dislike.voted { fill: #666 ! important;}
    #entry-list footer .favorite-link.favorited { fill: #666 ! important;}
    
    /* tüm  entryleri göster butonu */
    .showall {    border: 1px solid rgb(126, 130, 127) ! important ;}
    .showall:hover, .pager a:hover, #feed-refresh-link:hover { background-color: gray ! important; color: white ! important; }
    
    
    /* header arka plan rengi */
    body.light-theme > header, body.dark-theme > header { background-color: rgb(170,170,170) ! important }
    #content-body {
        width: 760px ! important;
        margin-left: 17.5px ! important;
        margin-right: 17.5px ! important;
    }
    
    /* sol frame başlık listesi */
    .topic-list li > a small { color: rgb(0,0,0) ! important }
    .topic-list.partial > li > a { padding: 4px ! important;  padding-right: 32px ! important }
    .topic-list > li > a { padding: 10px ! important; padding-right: 10px ! important; font-size: 14px ! important; }
    .topic-list.partial > li > a > small { top: 0 ! important; vertical-align: middle ! important; line-height: 30px ! important; }
	html.no-touch #partial-index::-webkit-scrollbar { width: 15px ! important;  }
    
    /* reklamlar */
    #div-gpt-ad-1451403752361-0, #AdvertHolder, .admatic_interstitial, #aside, #overlay, #interstatial_container  { display:none ! important }
    .ad-double-click, .ad-970x250, .ad-banner, .ad-doubleclickwebhomepagethemebanner, .ads { visibility: hidden ! important; height: 0px ! important; }
    a { color: #447 ! important;}
    input, select, textarea, .editform, .topic-list.partial > li > a:hover { background-color: #eee ! important;}
    #search-form, #sub-navigation { background-color: transparent ! important; }
    #editbox, #search-textbox, div.pager > select { color: black ! important; }
    #save-draft-button { border: 1px solid gray ! important; background-color: gray ! important; }
    #index-section { padding-left: 30px ! important;}

}
