css:
@-moz-document url-prefix("http://www.ncbi.nlm.nih.gov/pmc/"), url-prefix("https://www.ncbi.nlm.nih.gov/pmc/") {
/* general theme */
body {
    background-color: black;
    color: #c0c0c0;
    font: 12pt/1.75 arial, helvetica, clean, sans-serif;
}
a {
    color: #6282d0;
}
a:visited {
    color: #a856e5;
}
a:hover {
    color: #d68358;
}
/*color of border between header and article*/
#jr-head {
    border-bottom: 1px solid #333;
}
/*color of text in header*/
.jr-rhead .t {
    color: #666;
}
/*color of border separating columns in article*/
html.jr #jr-content article[data-type=main] {
    -webkit-column-rule: 1px solid #333;
    -moz-column-rule: 1px solid #333;
}
/*color of bottom part (navigation and figures)*/
#jr-dash, #jr-istrip {
    background-color: #202020;
}
a.pr-handle {
    /* no effect */
    background-color: #858585;
}
/*color of background when displaying figures*/
.jr-fs-12 article {
    background-color: black;
}
/*default block setting. ex: previewing figures, previewing bibliographic ref*/
.co-default {
    background-color: black;
    box-shadow: 0 0 5px 3px rgba(255,255,255,0.2);
}
/*preview figure*/
.co-fig {
    border-color: #777777;
}
/*preview bibliographic ref*/
.co-refbox {
    background-color: #0d0c09 ;
}
/*border color of figures block in text*/
.iconblock {
    border-color: #253040;
}
/*border color of supp. mat. block in text*/
.suppmat {
    border-color: #253040;
}
/*color of the closing cross for figures*/
.jr-objectbox-close {
    background-color: rgba(70,70,70, 0.9);
    box-shadow: 0 0 8px #444;
}
/*Highlight box*/
.boxed-text-box  {
    background-color: #222;
}
/*top left menus*/
/*article navigation*/
aside .cnt {
    background-color: #000;
}
aside .lol a {
    background-color: #333;
    color: #7f7f7f;
}
/*settings*/
a.btn {
    border-color: rgba(255,255,255,0.2) ;
}
/*img background (e.g for not vectorized formulas)*/
img {
    background-color: #bbb;
}
}
