css:
@-moz-document url-prefix("http://www.byfandom.org/") {
body,
.ow_canvas,
.jhtmlarea.ow_smallmargin,
.ow_bg_color,
.ow_box.ow_stdmargin.clearfix,
.ow_box_cap,
.ow_box.ow_stdmargin.clearfix.profile-BLOGS_CMP_UserBlogWidget,
.ow_box.ow_stdmargin.ow_break_word
{
/* This colors the background of the page */
    background-color: #000000 !important;
}

.ow_header_pic {
/*this edits header image*/
    background-image: none !important;
    height: 0px !important;
}
h1, h2, h3, h4, h5, h6 {
    color: #ff8040;
}

.ow_table_3 td.ow_value {
/* This affects tables*/
    width: 100%;
    padding-left: 0px;
    font-size: 100%;
    box-sizing: content-box;
    word-wrap: break-word !important;
}
.ow_canvas {
/*sets width of main boxes*/
    width: 1100px !important;
}
 
.ow_remark{
  color: black !important;
}
  
.ow_site_panel.clearfix{
/*This colors the top menu bar*/
  background-color: #000000 !important;
}
  
body,
html {
/* This colors the color and size of regular text site-wide */
    color: #ffffff !important;
    font-size: 16px !important;
    line-height: 18px !important;
}

a {
/* This colors the color of link text site-wide */
    color: #ff8040 !important;
    text-decoration: none;
}


.ow_newsfeed_body,
.ow_dnd_widget.group-FORUM_CMP_LatestTopicsWidget,
.ow_alt2, .ow_alt1,
.feed_item_sharing_section.ow_box,
.ow_newsfeed_comments,
td,
th
{
/* This colors the the background of most container boxes on the newsfeed, blogs, and forums */
    background-color: #000000 !important;
}

.ow_tr_delimiter td {
/* This prevents the background color of the forum boxes from over running their container */
    background-color: transparent !important;
}

.ow_tooltip_body, .ow_tooltip {
/* This colors the activity notification boxes at the top of the site */
    background-color: #000000 !important;
}
/*This is for Chat modification*/
.ow_chat_block_wrap,.ow_chat_in_block,.ow_chat_search input,.ow_chat_list ul {
    color: #ffffff !important;
     box-sizing: content-box;
    background-color: black !important
}
.ow_count_block span.ow_count_txt {
    color: #ff8040 !important;
}
.ow_chat_list {
    position: relative !important;
    overflow: hidden;
    background-color: black !important;
    height: 300px !important;
}
.ow_chat_block .ow_bot_panel {
    position: relative;
    background-color: black !important;
    width: 250px;
    height: 25px;
}
body .ow_chat_block_main .ow_chat_search input[type="text"].invitation, body .ow_chat_block_main .ow_chat_search input[type="password"].invitation {
    background-color: black !important;
    color: #ffffff !important;
    font-size: 12px !important;
}
span.ow_chat_in_item_author, span.ow_chat_in_item_text {
    color: white !important;
}
.ow_chat_block .ow_top_panel .ow_count_block, .ow_chat_block .ow_bot_panel .ow_count_block {
    padding: 5px 0;
    margin: 0;
    text-align: center;
    font-size: 14px !important;
    color: #ff0000;
    font-weight: normal;
    visibility: visible;
}
.ow_chat_block_wrap, .ow_chat_in_block, .ow_chat_search input, .ow_chat_list ul {
    color: #ffffff !important;
    box-sizing: content-box;
    background-color: black !important;
    font-size: 14px !important;
}
/*This is for the individual chat window modification*/
.ow_chat_in_dialog {
    color: black !important;
    background-color: black !important;
}
.ow_chat_dialog .ow_author_block {
    color: white !important;
    background-color: black !important;
}
.ow_dialog_date_cap {
    color: white !important;
    background-color: black !important;
    border-color: black !important;
}
.ow_chat_in_dialog .ow_time_block p span.ow_time_text {
    color: white !important;
}
body
.ow_chat_dialog_wrap .ow_dialog_in_item p {
    color: #000000
}

.ow_remark {
    color: #ffffff !important;
}

.ow_miniic_live .ow_lbutton.ow_green {
    color: #ffffff;
    background-color: #000000;
}

.ow_quote_cont {
    color: black !important;
}

.ow_footer {
    background: black;
}

.ow_highbox_table .ow_highbox, .ow_page .ow_highbox, .ow_sidebar, .ow_table_3 td {
    background-color: #ffffff;
    color: #ff8040 !important;
}
.ow_table_3 td.ow_label {
    color: white !important;   
}
th.ow_section span {
   color: white;
}

html body a.ow_lbutton, html body span.ow_lbutton {
    background-color: black;
}
}
