css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("umunk.net") {

table.tablesorter thead tr th {
	background-color: #383838;
	/* border: 1px solid #ccc; */
	font-size: 10pt;
	padding: 4px;
	text-align: center;
	color: white;
}
body {
	margin: 0;
	padding: 0;
	background: #383838;
	color: #f9f9f9;
}
table.tablesorter tbody tr:nth-child(2n+1) td {
	background: #383838;
}

table.tablesorter tbody td {
	color: #f9f9f9;
	padding: 2px;
	vertical-align: top;
	white-space: nowrap;
	border-bottom: 0px solid #fff;
}

table.tablesorter tbody tr:nth-child(2n) td {
	background: #242424;
}

table.tablesorter tbody tr {
	border: 0; 
}	

table.tablesorter td {
	color: #f9f9f9;
}	

table.tablesorter th {
	color: #242424;
}		
	
table.tablesorter tbody tr:nth-child(n) td.extrainfo {
	background: #fff;
	color: black;
}	
	
table.calced tbody tr:nth-child(n) th {
	background: #383838;
	font-size: 10px;
	color: white;
}
	
.total th {
	background-color: #242424;
	color: #66BBFF;
}
.average th {
    background-color: #242424;
    color: #66BBFF;
}

input {
	background: #242424;
	border: 1px #BDC7D8 solid;
	color: #FFFFFF;
	font-size: 14px;
}	
	
.hcols {		
	z-index : 1; 
	position : absolute;  
	background-color: white;
 	padding: 2px;
	width:200px;
}

a:link {
	text-decoration: none;
	color: white;
	background-color: transparent;
}	

/* Prep tool */
	

.box {
  background-color: #383838;
 border: 1px; 
  border-style: solid;
  border-collapse:collapse;
  border-color: #383838; 
}
	
button, select, textarea {
	font-family: inherit;
	font-size: inherit;
	line-height: inherit;
	color: black;
}

input {
	font-family: inherit;
	font-size: inherit;
	line-height: inherit;
	color: #f9f9f9;
}
	
}
