css:
@-moz-document domain("html5doctor.com") {
body {
    background-color: #111;
    color: #bbb;
}
#content[role="main"] {
    background-color: #333;
    color: #ddd;
    border-color: #0F4056;
}
.glossary {
    background-color: #444;
    color: #ddd;
}
.main, .grid-list h1, .grid-list .post header {
    border-color: #555;
}
.mod ul#networks li a, .sub-footer li a, .mod ul.related li a, .mod ul#recentcomments li, #hashtag li, .grid-list .post {
    border-color: #444;
}
#sub-footer {
    background-color: #444;
}
footer[role="contentinfo"] .inner p#copyright {
    background-color: transparent;
}
img {
    background-color: #111;
}
#sub-footer div .mod img {
    border-color: #555;
}
#sub-footer h3 {
    border-color: #666;
}
#glossary-list ol.nav, #glossary-list section {
	border-color: #555;
}
#glossary-list dd footer {
    border-color: #444;
}
#glossary-list dl:hover {
    background-color: #444;
}
#glossary-list dl:hover dt span a, #glossary-list dl:hover a.code {
    background-color: #111;
}
code {
    background-color: rgba(0, 0, 0, 0.25);
}
.pln {
    color: #fff;
}
.kwd, .tag {
    color: #36f;
}
blockquote, .callout {
    background-color: #111;
    border-color: #222;
}
.changed-block {
    border-color: #1E4804;
}
#comments .comment, #comments .pingback {
    background-color: #222;
}
#comments .comment-author-admin, #comments .byuser {
    background-color: rgba(0, 36, 52, 0.4);
}
#comments .comment-author-admin .comment-body, #comments .byuser .comment-body {
    color: #eee;
}
form#search-form div input[type="text"] {
    background-color: #111;
    color: #ccc;
}
#form #commentform input[type="text"], #form #commentform input[type="email"], #form #commentform input[type="url"], #form #commentform textarea, .wpcf7-form textarea, .wpcf7-form input[type="text"], .wpcf7-form input[type="email"] {
    background-color: #222;
    border-color: #555;
    color: #ddd;
}
#form #commentform input[type="text"]:focus, #form #commentform input[type="email"]:focus, #form #commentform input[type="url"]:focus, #form #commentform textarea:focus, .wpcf7-form textarea:focus, .wpcf7-form input[type="text"]:focus {
	background: #444;
    color: #eee;
}
}
