css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("pinterest.com") {
	/*DARK THEME*/

	html

	{

		background-color: #222 !important;

	}



	/* background color (dimmed) when clicking a pin */

	.Closeup.canClose,

	.Modal .modalMask

	{

		background: rgba(33,33,33,0.975) !important;

	}



/* ---------------------------------------------------------------------------*/



	.Button.btn

	{

		background: linear-gradient(#333, #1c1c1c) !important;

		border: 1px solid #222 !important;

		color: #ddd !important;

		text-shadow: 0px 1px #000;

	}



	.Button.btn:hover

	{

		background: linear-gradient(#333, #111) !important;

		border: 1px solid #1c1c1c !important;

		color: #ddd !important;

		text-shadow: 0px 1px #000;

	}



	/* some other kinda button (softer) - used on Closeup actionbar */

	/* TODO: make these less ugly */

	.Button.medium

	{

		box-shadow: 1px -1px 1px rgba(48, 48, 48, 0.98) inset, -1px 0px 1px rgba(77, 77, 77, 0.98) inset !important;

	}

	

	.Button.medium:not(.hasText)

	{

		background: none !important;

		border: initial !important;

		text-shadow: initial !important;

		box-shadow: initial !important;

	}



	/* text field (like comment box) */

	.Field .content

	{

		background-color: #191919 !important;

		color: #ddd !important;

		border-color: #2c2c2c !important;

	}



	/* red buttons */

	.Button.btn.primary

	{

		background-image: linear-gradient(#E3262E, #AB171E) !important;

		background-color: #AB171E !important;

		border-color: #AF151B #9A1015 #820A0F !important;

		box-shadow: 0px 1px 0px 0px rgba(0, 0, 0, 0.64) !important;

	}



	/* red buttons hover */

	.Button.pinActionBarButton.primary:hover:not(.noHoverActive),

	.Button.btn.primaryOnHover:hover,

	.Button.btn.primaryOnHover:focus,

	.Button.btn.primaryOnHover.active

	{

		background-image: linear-gradient(#E63D45, #C11A22) !important;

		background-color: #AB171E !important;

		border-color: #AF151B #9A1015 #820A0F !important;

		box-shadow: 0px 1px 0px 0px rgba(0, 0, 0, 0.64) !important;

	}



	/* disabled button (TODO: make this look better) */

	.Button.btn[disabled], .Button.btn.disabled, .Button.btn.disabled:hover

	{

		background: linear-gradient(#444, #777) !important;

		border: 1px solid #5c5c5c !important;

		color: #999 !important;

		text-shadow: none !important;

		opacity: 0.5 !important;

	}



	/* footer icon symbols (+) and (?) */

	.Button.addPinFooter em,

	.Button.moreFooter em

	{

		background-color: inherit !important;

	}



	/* footer icon background (circular) */

	.Button.footerIcon.borderless,

	.FooterButtons .nonEuPrivacyPolicy

	{

		background-color: #444 !important;

		opacity: 0.33 !important;

	}



	/* footer icon background (hover state) */

	.Button.footerIcon:hover,

	.Button.footerIcon:hover .Button.addPinFooter em,

	.Button.footerIcon:hover .Button.moreFooter em,

	.FooterButtons [class*="PrivacyPolicy"]:hover

	{

		background-color: #555 !important;

		opacity: 1.0 !important;

	}

	

	.FooterButtons [class*="PrivacyPolicy"] {

		background: none !important;

	}



	/* footer icon shadow */

	.Button.footerIcon

	{

		box-shadow: 0px 1px 2px 0px rgba(0, 0, 0, 0.66) !important;

	}



	/* invert some button icons */

	.PinLikeButton.like em,

	.Button.website em,

	.Button.send em,

	.Button.sendInfoBar em,

	.Button.sendSmall em,

	.Button.sendInvite em,

	.Button.pinFlag em,

	.PinLikeButton.likeSmall em

	{

		-webkit-filter: invert(100%) !important;

		filter: invert(100%) !important;

	}



	.gridLoading

	{

		background: none !important;

		-webkit-filter: invert(100%) !important;

		filter: invert(100%) !important;

	}



	/* loading animation spinner */

	.spinnerMedium

	{

		-webkit-filter: invert(100%) !important;

		filter: invert(100%) !important;

	}



	.FollowButton:not(.primary):not(.followed):not(.disabled) .buttonText {

		color: #ddd !important;

	}

	

	/* X close button on closeup page (upper right) */

	.Closeup.modularCloseup.canClose .close em

	{

		background-position: -293px -316px;

	}



	/* X close button on closeup page (upper right, hover state) */

	.Closeup.modularCloseup.canClose .close:hover em

	{

		background-position: -312px -316px;

	}



/* ---------------------------------------------------------------------------*/





	/* pin summary (info below image) */

	.Pin.summary.summary

	{

		background-color: #0c0c0c !important;

		box-shadow: 0px 1px 3px 2px rgba(0, 0, 0, 0.33) !important;

	}



	/* pin summary: pin description text */

	.Pin.summary .pinDescription,

	.Pin.summary .commentDescriptionContent

	{

		color: #aaa !important;

	}



	/* pin summary: comment author name I guess */

	.Pin.summary .commentDescriptionCreator

	{

		color: #ddd !important;

	}



	/* pin summary: description link */

	.Pin.summary .pinDescription a

	{

		color: #bbb !important;

	}



	/* pin summary: description link on hover */

	.Pin.summary .pinDescription a:hover

	{

		color: #ccc !important;

	}



	/* pin summary: rich title text */

	.Pin.summary .richPinMeta .richPinGridTitle

	{

		color: #ddd !important;

	}



	/* pin summary: divider lines */

	.Pin.summary .pinCredits,

	.Pin.summary .richPinMeta,

	.Pin.summary .creditFooter,

	.PinCommentList

	{

		border-color: #222 !important;

	}



	/* pin summary: credit/picked for you (at bottom) */

	.Pin.summary .creditItem,

	.Pin.summary .pinCreditWrapper

	{

		background-color: #0c0c0c !important;

	}



	/* pin summary: credit/picked for you (at bottom; hover state) */

	.Pin.summary .creditItem:hover,

	.Pin.summary .pinCreditWrapper:hover

	{

		background-color: #000 !important;

	}



	/* pin summary: picked for button */

	.Pin .pinCreditWrapper:hover .hidePinInfo {

		filter: invert(100%) !important;

		-webkit-filter: invert(100%) !important;

	}



	/* pin summary: credit name (or "Picked for you") */

	.Pin.summary .creditItem .creditName

	{

		color: #555 !important;

	}



	/* pin summary: credit title */

	.Pin.summary .creditItem .creditTitle

	{

		color: #777 !important;

	}



	/* pin summary: repins and likes */

	.pinSocialMeta

	{

		opacity: 0.6 !important;

	}



	/* pin summary: repins and likes (hover state) */

	.pinSocialMeta:hover

	{

		opacity: 1.0 !important;

		transition: opacity 0.3s ease-in 0.2s !important;

	}



	.Button.close

	{

		background: linear-gradient(#333, #444) repeat scroll 0% 0% transparent;

		border-radius: 0px 0px 6px 6px;

		box-shadow: 0px 1px 4px rgba(0, 0, 0, 0.34), 1px 0px 0px rgba(0, 0, 0, 0.9) inset;

	}



/* ---------------------------------------------------------------------------*/



	/* pin image dimming overlay gradient (revealed on hover) */

	.Pin.summary .pinImageActionButtonWrapper .pinImageDim .dimGradient

	{

		background: linear-gradient(to top, rgba(0,0,0,1) 0%, rgba(41,41,41,0.25) 33%, rgba(0,0,0,0) 100%) !important;

	}



	/* image dimming overlay */

	.Pin.summary .pinImageActionButtonWrapper .pinImageDim .dimOverlay

	{

		background: none !important;

	}



	/* remove hover mask */

	.Pin.summary .hoverMask

	{

		visibility: hidden !important;

	}



	/* image dimming (animated with delay) */

	.Pin.summary .pinImageActionButtonWrapper:hover .pinImageDim

	{

		opacity: 0.5 !important;

		transition: opacity 0.3s ease-in 1s !important;

	}



	/* show navlink (animated with delay) */

	.Pin.summary .pinImageActionButtonWrapper:hover .navLinkOverlay

	{

		opacity: 1.0 !important;

		transition: opacity 0.3s ease-in 1s !important;

	}



	/* "Learn more at xyz.com" (when hoving over pin) */

	.Pin.summary .pinNavLink.navLinkOverlay .buttonText

	{

		color: #fff !important;

		text-shadow: 1px 1px 2px #000 !important;

	}



	/* "Expand Pin" */

	.Button.expandGiraffe

	{

		background-color: rgba(0, 0, 0, 0.8) !important;

	}



	/* "Expand Pin" text */

	.Button.expandGiraffe span

	{

		color: #777 !important;

	}



	/* "Expand Pin" bar (hover state) */

	.Button.expandGiraffe:hover

	{

		background-color: rgba(0, 0, 0, 0.8) !important;

		color: #fff !important;

	}



	/* "Expand Pin" text (hover state) */

	.Button.expandGiraffe:hover span

	{

		color: #ddd !important;

	}



/* ---------------------------------------------------------------------------*/



	.headerBackground, .Header

	{

		background: #333 !important;

		box-shadow: 0px 2px 5px 0px rgba(0, 0, 0, 0.33) !important;

	}



	/* search input box */

	.TypeaheadField.guided .tokenizedInputWrapper

	{

		background: #222 !important;

		border-color: #111 !important;

	}



	.TokenizedInput.guided .tokensWrapper .tokenizedItem.inputToken .field:focus

	{

		color: #ddd !important;

	}



	.TokenizedInput.guided .tokensWrapper .tokenizedItem.inputToken .field

	{

		color: #aaa !important;

	}



	/* top right button in header with your name in it */

	.UserNavigateButton,

	.Button.btn.notifs

	{

		background: linear-gradient(#777, #444) !important;

		border-color: #333 !important;

	}



	/* top right button in header with your name in it (hover state) */

	.UserNavigateButton:hover,

	.Button.btn.notifs:hover

	{

		background: linear-gradient(#777, #333) !important;

	}



	.UserNavigateButton .profileName

	{

		color: #ddd !important;

	}



	.UserNavigateButton:hover .profileName

	{

		color: #ddd !important;

	}



	/* behind user navigate button */

	.Header .rightHeaderContent .RightHeader

	{

		background: inherit !important;

	}



	/* Pinterest, and category drop down menu button (flanking search box) */

	.Button.logoHeader,

	.Button.categoriesHeader

	{

		background: linear-gradient(#777, #444) !important;

		border-color: #3c3c3c !important;

	}



	.Button.logoHeader:hover,

	.Button.categoriesHeader:hover

	{

		background: linear-gradient(#777, #333) !important;

		border-color: #444 !important;

	}



/* ---------------------------------------------------------------------------*/



	.Pin.detailed .closeupContentSection,

	.Board.boardCoverImage,

	.PinCommentList.detailed .commentsContainer

	{

		background: #0c0c0c !important;

	}



	.imageContainer,

	.transitionImage /* loading image background on closeup (omg, pain in the ass to find!) */

	{

		background: #0c0c0c !important;

/* 		border-color: #f00 !important; */

	}



	/* Closeup: bar above main image */

	.PinActionBar

	{

/* 		background: rgba(12, 12, 12, 0.85) !important; */

		background: #0c0c0c !important;

		border-color: #333 !important;

	}

	

	/* appears behind Closeup PinActionBar temporarily */

	.Closeup.modularCloseup .transitionTitle .noTitle,

	.Closeup.modularCloseup .transitionTitle,.Closeup.modularCloseup .transitionImage {

		background-color: #0c0c0c !important;

		border-color: #0c0c0c !important;

	}



	/* Closeup: background behind actionbar */

/* 	.CloseupContent .closeupContentSection.pinActionBarStickyContainer

	{

		background: #1c1c1c !important;

	} */



	/* Closeup: main image background */

	.PaddedPin .paddedPinLink,

	.PaddedPin .paddedPinLink:hover

	{

		background-color: #fc1c1c !important;

	}



	/* Closeup: main image source/flag area (below image) */

	.Pin .sourceFlagWrapper

	{

		background-color: #0c0c0c !important;

		border-color: #333 !important;

	}



	/* Closeup: main image source link (below image) */

	.Pin .sourceFlagWrapper a

	{

		color: #ddd !important;

		text-shadow: 1px 1px 0px rgba(0, 0, 0, 0.66) !important;

	}



	/* Closeup: "Also on these boards" and "Related Pins" text */

	.CloseupBottom .sectionTitle

	{

		color: #ddd !important;

		text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.6) !important;

	}



	/* Closeup: title for boards (Also on these boards) */

	.User.thumb.boardRepTitle .title

	{

		color: #ddd !important;

	}



	/* Closeup: user name for boards (Also on these boards) */

	.User.thumb.boardRepTitle .fullname

	{

		color: #aaa !important;

	}



	/* Closeup: rich content title/meta */

	.CloseupRichPinContent .richPinNameLink,

	.CloseupRichPinContent .richPinMeta

	{

		color: #ddd !important;

		text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.6) !important;

	}



	/* Closeup: rich content source text */

	.CloseupRichPinContent .richPinSourceLink

	{

		color: #999 !important;

	}



	/* Closeup: rich content meta bar */

	.CloseupRichPinContent .richPinMeta,

	.CloseupRichPinContent .richPinTitleWrapper.onlyAttribution

	{

		background: #0c0c0c !important;

		border-color: #333 !important;

	}



	/* Closeup: fix background of rounded corners */

	.Board.boardCoverImage .boardCoverWrapper

	{

		background: inherit !important;

	}



	/* Closeup: various fix border colors */

	.Pin.detailed .pinnerViaPinnerCredit .User:first-child,

	.Pin.detailed .pinnerViaPinnerCredit,

	.CloseupRichPinContent .richPinInformation

	{

		border-color: #333 !important;

	}



	/* Closeup: usernames for "added by", and "via" */

	.User.thumb.pinner .fullname

	{

		color: #ddd !important;

	}



	/* Closeup: commenter username */

	.PinDescriptionComment.detailed .pinDescriptionComment .commentDescriptionCreator

	{

		color: #ddd !important;

	}



	/* Closeup: pin description (?) */

	.Pin.detailed .closeupContentSection .pinDesc

	{

		color: #999 !important;

	}

	

	/* various text on closeup page */

	.User.modularCloseup .profileSource .profileSourceAttributionContent .title, 

	.User.modularCloseup .profileSource .profileSourceAttributionContent .fullname,

	.PinDescriptionComment.modularCloseup.isPinnerComment .savedTo .boardName,

	.CloseupTitleCard a,

	.CloseupSourceDescription

	{

		color: #bbb !important;

	}



	/* Closeup: "More from xxxxxxx.com" */

	.Domain .domainWrapper

	{

		background-color: #0c0c0c !important;

	}



	/* Closeup: board pins to right of main image */

	.Board.boardPinsGrid,

	.Board.boardPinsGrid.noShadow .boardHeader,

	.Board.boardPinsGrid.noShadow .pinGridWrapper,

	.boardPinsGridContainer

	{

		background-color: #0c0c0c !important;

	}



	/* Closeup: hide scrollbar on board pins to right of main image */

	.pinGridWrapper

	{

		overflow: hidden !important;

	}



	/* Closeup: show scrollbar on board pins to right of main image */

	.pinGridWrapper:hover

	{

		overflow: scroll !important;

	}



	/* Closeup: background behind board pins (noticeable when loading images) */

	.Pin.boardPinsGrid .boardPinsGridPin,

	.Pin.boardPinsGrid .visualObjectGridPin,

	.Pin.visualObjectGrid .boardPinsGridPin,

	.Pin.visualObjectGrid .visualObjectGridPin,

	.Pin.plainImageGrid .boardPinsGridPin,

	.Pin.plainImageGrid .visualObjectGridPin,

	.Domain .thumb

	{

		background-color: #0c0c0c !important;

	}





	/* Close-up left and right arrow circular background color */

/* 	.Closeup .closeupContainer .arrow-nav > div {

		background-color: #333 !important;

	} */



	/* Close-up left and right arrow circular background color (hover) */

/* 	.Closeup .closeupContainer .arrow-nav > div:hover {

		background-color: #444 !important;

	} */



	/* Close-up left arrow color */

	.Closeup .closeupContainer .arrow-nav > div.left-arrow::before {

		border-right-color: #555 !important;

	}

	

	/* Close-up left arrow color (hover) */

	.Closeup .closeupContainer .arrow-nav:hover > div.left-arrow::before {

		border-right-color: #ccc !important;

	}	



	/* Close-up left arrow cut-out mask */

	.Closeup .closeupContainer .arrow-nav > div.left-arrow::after {

		border-right-color: #212121 !important;

	}



	/* Close-up left arrow cut-out mask (hover) */

	.Closeup .closeupContainer .arrow-nav:hover > div.left-arrow::after {

		border-right-color: #212121 !important;

	}



	/* Close-up right arrow color */

	.Closeup .closeupContainer .arrow-nav > div.right-arrow::before {

		border-left-color: #555 !important;

	}



	/* Close-up right arrow color (hover) */

	.Closeup .closeupContainer .arrow-nav:hover > div.right-arrow::before {

		border-left-color: #ccc !important;

	}	

	

	/* Close-up right arrow cut-out mask */

	.Closeup .closeupContainer .arrow-nav > div.right-arrow::after {

		border-left-color: #212121 !important;

	}



	/* Close-up right arrow cut-out mask (hover) */

	.Closeup .closeupContainer .arrow-nav:hover > div.right-arrow::after {

		border-left-color: #212121 !important;

	}



	/* thumbnails sidebar (right side) on close-up page */

	.sidebarContainer {

		background-color: inherit !important;

	}

	

	.Pin.detailed.modularCloseup .hr {

		border-color: #333 !important;

	}



	/* 1px horizonal lines above and below the closeup image, after it's been clicked */

	.modularCloseup

	{

		outline: none !important;

	}

	





	/* blank images in domain thumbs */

	.thumb img[src$="/136x136/5a/22/91/5a229148ac882662ad4aa1901bec1ef9.jpg"]

	{

		-webkit-filter: invert(100%) !important;

		filter: invert(100%) !important;

	}



	/* description comment text */

	.PinDescriptionComment.detailed .pinDescriptionComment .commentDescriptionContent

	{

		color: #999 !important;

	}



	/* description comment links */

	.PinDescriptionComment.detailed .pinDescriptionComment .commentDescriptionContent a

	{

		color: #bbb !important;

	}



/* ---------------------------------------------------------------------------*/



	.BoardHeader

	{

		background-color: #333 !important;

	}



	.BoardInfoBar

	{

		background-color: #333 !important;

		background: linear-gradient(to bottom, #333 0%,#222 100%) !important;

	}



	.BoardHeader .divider hr

	{

		background-color: #222 !important;

	}



	.BoardHeader .boardName

	{

		color: #ddd !important;

	}



	.BoardInfoBar .smallBoardName

	{

		color: #aaa !important;

	}



	.BoardHeader .description

	{

		color: #aaa !important;

	}



	.BoardInfoBar .pinsAndFollowerCount li

	{

		background: inherit !important;

	}



	.User.thumbUserInfo.small .fullname

	{

		color: #ddd !important;

	}



/* ---------------------------------------------------------------------------*/



	.UserProfileHeader.profileHeader .fixedHeader

	{

		background-color: #333 !important;



	}



	.UserProfileHeader.profileHeader .header

	{

		background-color: #333 !important;

	}



	.UserInfoBar.infoBar

	{

		background-color: #333 !important;

		background: linear-gradient(to bottom, #333 0%,#222 100%) !important;

	}



	.UserInfoBar.infoBar .tabs li

	{

		background: inherit !important;

	}



	.UserInfoBar.infoBar .divider hr,

	.UserProfileHeader .divider hr

	{

		background-color: #222 !important;

	}



	.UserInfoBar li a

	{

		text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.8) !important;

	}



	.UserProfileHeader.profileHeader .name

	{

		color: #ddd !important;

	}



	/* thick circular board around avatar */

	.UserProfileHeader.profileHeader .profileImage img

	{

		box-shadow: 0px 0px 0px -2px rgba(0, 0, 0, 0.48) inset, 0px 0px 0px 1px rgba(0, 0, 0, 0.14), 0px 0px 0px 8px #333 !important;

	}



	.UserBoards .secretBoardHeader {

		color: #ddd !important;

	}



	.sectionTitle

	{

		color: #bbb !important;

		text-shadow: 0px 1px 0px rgba(0, 0, 0, 0.8) !important;

	}



	.UserBoards .text

	{

		color: #999 !important;

	}



	.BoardCreateRep,

	.AddPinRep

	{

		border-color: #555 !important;

		text-shadow: 0px 1px 1px #000 !important;

	}



	.BoardCreateRep:hover

	{

		border-color: #777 !important;

	}



	.Board .boardName

	{

		color: #ddd !important;

	}



/* ---------------------------------------------------------------------------*/



	.FeedPage .feedName,

	.DomainFeedPage .feedName

	{

		color: #ddd !important;

	}



/* ---------------------------------------------------------------------------*/



	.FriendsToFollow

	{

		background: #0c0c0c !important;

	}



	.UserNews .buttonHolder {

		border-color: #333 !important;

	}



	.FriendsToFollow .memoTitle:hover

	{

		background: #000 !important;

	}



	#friendsToFollow .Button.btn

	{

		background: linear-gradient(#333, #111) !important;

		border: 1px solid #333 !important;

		color: #999 !important;

		text-shadow: 0px 1px #000;

	}



/* ---------------------------------------------------------------------------*/



	.User.gridItem

	{

		background: #0c0c0c !important;

	}



	.User.gridItem .username

	{

		color: #ddd !important;

	}



	.User .thumbContainer,

	.User .focusThumbContainer

	{

		background: inherit !important;

	}



	.RepinsLikesFeedPage h1

	{

		color: #ddd !important;

	}



/* ---------------------------------------------------------------------------*/



	.Pin.summary .pinImageActionButtonWrapper:hover .likeEditButtonWrapper

	{

		opacity: 1.0 !important;

		transition: opacity 0.3s ease-in 1s !important;

	}



	/* show Pin, and Send button (animated with delay) */

	.Pin.summary .pinImageActionButtonWrapper:hover .repinSendButtonWrapper

	{

		opacity: 1.0 !important;

		transition: opacity 0.3s ease-in 1s !important;

	}



/* ---------------------------------------------------------------------------*/



	.RemovePinDialog

	{

		background: #000 !important;

		color: #777 !important;

		border-color: #222 !important;

	}



	.RemovePinDialog a

	{

		color: #999 !important;

	}



/* ---------------------------------------------------------------------------*/



	/* interest picker when first signing up */

	.NuxModalInterestPicker .footer

	{

		background: none !important;

	}



	/* text box when entering description of pin */

	.pinImageDescriptionWrapper .Field .content,

	.pinModuleHolder .Pin.summary.cloned,

	.pinModuleHolder .Pin.summary.cloned,

	.pinModuleHolder .Pin.summary.cloned,

	.pinModuleHolder .TextField textarea

	{

		background: #fff !important;

		color: #000 !important;

	}



/* ---------------------------------------------------------------------------*/



