css:
@-moz-document domain("smashingmagazine.com") {
@media screen and (min-width: 90.625em) {
    #menu-network-menu, #social-media-nav {
        opacity: 0.05;
        transition: opacity 0.25s ease-in;
    }
    nav:hover #menu-network-menu,
    nav:hover #social-media-nav {
        opacity: 1;
        transition: opacity 0.25s ease-out;
    }
}
@media screen and (min-width: 50em) and (max-width:63.75em),
screen and (min-width: 76.25em) {
    #menu-category-menu {
        opacity: 0.05;
        transition: opacity 0.25s ease-in;
    }
    .sct:hover #menu-category-menu {
        opacity: 1;
        transition: opacity 0.25s ease-out;
    }
}
@media screen and (min-width: 63.75em) {
    #wpsidebar {
        opacity: 0.05;
        transition: opacity 0.25s ease-in;
    }
    #wpsidebar:hover {
        opacity: 1;
        transition: opacity 0.25s ease-out;
    }
}
}
