css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("flowdock.com") {

.theme-flowdark .message.me .message-author {
    color: rgb(250, 250, 250);
}

.bubble-container {
    filter: saturate(20%) contrast(50%);
}
.theme-flowdark .chat-message a {
    color: rgb(140, 160, 140) !important;
}
.image-preview img,
.youtube-preview .video-image,
.vimeo-preview .video-image {
    filter: saturate(20%) contrast(50%);
    width: 20%;
}
.image-preview img:hover,
.youtube-preview .video-image:hover {
    width: inherit;
    filter: inherit;
}
img.thread-avatar,
div.user-avatar,
div.rounded-avatar,
.avatar,
.inbox-thread-author-avatar {
    filter: saturate(20%) contrast(80%);
}
.fa,
.emoji,
.icon {
    filter: saturate(20%) contrast(50%);
}
.tab-avatar-container {
    filter: saturate(20%) contrast(80%);
}
.theme-flowdark .thread-indicator {
    filter: saturate(20%);
}
.bubble-container {
    left: 0;
}
.avatar-container {
    left: 32px;
}
::-webkit-scrollbar {
    height: 8px;
    width: 8px;
    background: #333;
}
::-webkit-scrollbar-thumb {
    background: #ccc;
    -webkit-border-radius: 1ex;
    -webkit-box-shadow: 0px 1px 2px rgba(0, 0, 0, 0.75);
}
::-webkit-scrollbar-corner {
    background: #333;
}
}
