css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("intra.42.fr") {
   body {
    background-image: none;
    background-color: #222;
    color: #818181;
  }
  
  .main-navbar,
  .main-navbar .user-actions .user-action,
  .main-navbar .main-navbar-search::before,
  h1, h2, h3, h4, h5,
  .main-title,
  .profile-title,
  .profile-title a,
  .note-title,
  .marked-title {
    color: #818181;
  }
  
  .main-navbar,
  .container-fullsize {
    background-color: #222;
  }
  
  .tt-menu {
    background-color: #444;
  }

  
  .container-item,
  .notification-list {
    color: #818181;
    background-color: #333;
    border: 1px solid #666; 
  }
  
  .bg-image-item {
    background-color: #818181;
  }
   
  .btn.btn-default,
  .label,
  .js-topic--submit-button {
    color: #999;
    background-color: #444;
    border: 1px solid #666;
  }
  
  .btn.btn-default:hover,
  .js-topic--submit-button:hover{
    color: #00BABC;
    background-color: #444;
    border-color: #00BABC;
  }
}

@-moz-document domain("profile.intra.42.fr") {
  text {
    color: #999 !important;
    fill: #999 !important;
  }

  .highcharts-axis-labels .highcharts-xaxis-labels {
    color: #818181;
    fill: #818181;
  }
  
  .profile-title a.simple-link {
    color: #818181;
    border: 1px solid #818181;
  }
  
  .profile-title a.simple-link:hover {
    color: #00BABC;
    border: 1px solid #00BABC;
  }
  
  .very-big-icon {
    color: rgba(200, 200, 200, 0.4);
  }
  
  .profile-menu .nav li a {
    background-color: #333;
    color: #818181;
  }
  
  .profile-menu .nav li.active a,
  .profile-menu .nav li a:hover {
    background-color: #444;
  }
  
  .project-item[data-project-occurence] .marked-title a {
    color: #00BABC;
  }
  
    .project-item[data-project-occurence] .marked-title a:hover {
      color: #006e70;
  }
  
  .container-fullsize .container-inner-item,
  .container-inner-item,
  .container-item .boxed {
    background-color: #333;
    border: 1px solid #666;
  }

}

@-moz-document domain("projects.intra.42.fr") {

  .project-grid .project-item {
    background-color: #252525;
    border: 1px solid #666;
  }

  .team-item .team-status {
    background-color: #333;
  }
  
  .form-control,
  .form-control[disabled],
  .form-control[readonly],
  fieldset[disabled] .form-control {
    color: #818181;
    background-color: #333;
    border: 1px solid #666;
  }
  
  .projects-list-container .projects-list--header .btn--action:not(:first-child) {
    border-left: 1px solid #666;
  }
  
  .projects-list-container .projects-list--list-header .project-item,
  .projects-list-container .projects-list--list .project-item:hover {
    background-color: #444;
  }
  
  .project-infos,
  .project-infos .project-infos-top p, 
  .note-title,
  .project-repo .input-group-addon {
    color: #818181;
    background-color: #444;
  }


  .projects-list-container .projects-list--header {
    border-bottom: 1px solid #666;
  }
 
}

@-moz-document domain("forum.intra.42.fr") {
  
  .topics-resume .topics-resume-counters .counter-item:not(:last-of-type) {
    border-right-color: #666;
  }
  
  .topic-item,
  .fixed-topic-header,
  .message-item:not(:last-of-type) {
    border-bottom-color: #666;
  }
   
  .message-item .message-reply-item:not(:first-of-type),
  .topics-list:not(.home-list) .topic-item.topic-pinned + .topic-item:not(.topic-pinned) {
    border-top-color: #666;
  }

  .sidebar-right-resume > [class^="summary-"],
  .topic-reply.js-topic-fixed-reply,
  .fixed-topic-header,
  .app-sidebar-right {
    background-color: #333;
  }
  
  .sidebar-search input {
    background-color: #444;
    color: #818181;
  }
  
  .message-footer .resume,
  topic-reply-content,
  .message-item .message-reply-item,
  .message_content textarea.reply-form,
  .message-reply textarea.reply-form,
  .message_content textarea.reply-form:focus, .message-reply textarea.reply-form:focus {
    border: 1px solid #666;
    background-color: #333;
  }
 
  .message-item .message-reply-item .reply-content .reply-content-text,
  .message-item .message-corner .message-corner-item [class^="icon-"]::before,
  .message-item .message-corner-left .message-corner-item [class^="icon-"]::before,
  .message-item .message-reply-corner .message-corner-item [class^="icon-"]::before,
  .message-item .message-corner .message-corner-item, .message-item .message-corner-left .message-corner-item,
  .message-item .message-reply-corner .message-corner-item {
    color: #818181;
  }
  
  .topic-item .topic-content .topic-name,
  .topic-item .topic-content .topic-metadata {
    color: #818181;
  }
  
  .topic-item.topic-unread .topic-content .topic-name {
    color: #006E70;
  }
}

@-moz-document domain("elearning.intra.42.fr") {

  .container-breadcrumb .breadcrumb,
  .container-item-nav-tab,
  .container-item-nav-tab .nav-tabs,
  .container-item-nav-tab .tab-content,
  textarea.form-control,
  .app-sidebar-right {
    background-color: #333;
  }
  
  .container-item-nav-tab
  .container-item-nav-tab .nav-tabs,
  textarea.form-control {
    border: 1px solid #666;
  }
  
  .notion-grid > .notion-item .subnotions-list .subnotion-item:not(:first-child),
  .notion-grid > .subnotion-item .subnotions-list .subnotion-item:not(:first-child),
  .subnotion-grid > .notion-item .subnotions-list .subnotion-item:not(:first-child),
  .subnotion-grid > .subnotion-item .subnotions-list .subnotion-item:not(:first-child) {
    border-color: #666;
  }
  
  .container-item-nav-tab .tab-content {
    border-left-color: #666;
    border-right-color: #666;
    border-bottom-color: #666;
  }
  
  .container-item-nav-tab .nav-tabs {
    border-color: #666;
  }
  
  .page-header,
  .container-item-nav-tab .nav-tabs > li.active > a,
  .container-item-nav-tab .nav-tabs > li.active > a:hover,
  .container-item-nav-tab .nav-tabs > li.active > a:focus {
    background-color: #333;
    border-color: #666;
    border-bottom: none;
  }

  .container-breadcrumb .breadcrumb a {
    color: #818181;
  }
  
  .container-breadcrumb .breadcrumb a:hover {
    color: #00BABC;
  }
  
  .pagination > li > a,
  .pagination > li > span {
    color: #818181;
    border-color: #666;
    background-color: transparent;
  }
  
  .pagination > .active > a,
  .pagination > .active > a:hover,
  .pagination > .active > a:focus,
  .pagination > .active > span,
  .pagination > .active > span:hover,
  .pagination > .active > span:focus {
    background-color: transparent;
    color: #00BABC;
  }
  
  .pagination > li > a:hover,
  .pagination > li > span:hover {
    color: #00BABC;
    background-color: #666;
    border-color: #666;
  }
  
  .notion-grid > .notion-item .notion-title, .notion-grid > .notion-item .subnotion-title,
  .notion-grid > .subnotion-item .notion-title, .notion-grid > .subnotion-item .subnotion-title,
  .subnotion-grid > .notion-item .notion-title, .subnotion-grid > .notion-item .subnotion-title,
  .subnotion-grid > .subnotion-item .notion-title, .subnotion-grid > .subnotion-item .subnotion-title {
    background-color: #333;
    border-color: #666;
  }
  
  .notion-grid > .notion-item, .notion-grid > .subnotion-item, .subnotion-grid > .notion-item, .subnotion-grid > .subnotion-item, 
  .notion-grid > .notion-item .subnotions-list, .notion-grid > .subnotion-item .subnotions-list,
  .subnotion-grid > .notion-item .subnotions-list, .subnotion-grid > .subnotion-item .subnotions-list {
    background-color: #444;
    border-color: #666;
  }
}

@-moz-document domain("meta.intra.42.fr") {
  .changelogs-list .changelog-item .changelog-main:hover {
    background-color: #404040;
  }
  
  code {
    background-color: #111;
    color: #818181;
  }
  
  .changelogs-list .changelog-item .changelog-left span {
    background-color: #444;
  }
  
  .breadcrumb, .well {
    background-color: #444;
    border-color: #666;
  }
  
  .main-title img {
    background-color: #444;
  }
}

@-moz-document domain("shop.intra.42.fr") {
  .shop-item,
  .shop-item .shop-item--image {
    background-color: #444;
    border-color: #666;
  }
  .shop-item .shop-item--overlay {
    background-color: rgba(80, 80, 80, 0.8);
  }
}

@-moz-document domain("api.intra.42.fr") {
  .resource-item, .resource-item:nth-child(2n+1) {
    background-color: #333;
  }
  
  .page-header,table > thead > tr > th, table > tbody > tr > th, table > tfoot > tr > th, table > thead > tr > td, table > tbody > tr > td, table > tfoot > tr > td {
    border-color: #666;
  }
    
  code {
    background-color: #111;
    color: #818181;
  }
  
  .alert-warning {
    background-color: #3E3B30;
    border-color: #746C5C;
  }
  
  tr {
    background-color: #333 !important;
  }
  
  .label.label-default, .label.label-warning, .label.label-danger, .label.label-primary {
    color: #818181;
  }
  
  pre {
    border-color: #666;
    color: #818181;
    background-color: #444;
  }
}

@-moz-document domain("intra.42.fr") {
  
  .white-background {
    color: #818181;
  }
  
  button, input, optgroup, select, textarea {
    background-color: #333;
  }
  
  .select-tag-filtre {
    border: 0px;
  }
  
  .show-right {
    border-top: 1px solid #818181;
  }
}
