css:
@-moz-document domain("www.joduska.me") {
.ggbg {
    background-image: url('http://i.imgur.com/riv3sRB.png');
    max-width: 1920px;
    height: 750px;
}

.input_submit:hover {
    background: #921A39;
    border-color: #921A39;
}
    
.ipbfs_titlebox:before {
    background: #C1234B;
    height: 6px;
    border: 1px solid #921A39;
    content: "";
    display: block;
    margin: 0 -16px 9px;
}

.wrapper {
width: 1300px;
}


.topic_buttons li.important a, .topic_buttons li.important span, .ipsButton .important, .topic_buttons li a, .topic_buttons li span, .ipsButton {
    background: #ECEFF1;
    color: #fff;
    font-size: 12px;
    font-weight: bold;
    line-height: 36px;
    height: 36px;
    padding: 0 20px;
    text-align: center;
    min-width: 70px;
    display: inline-block;
    border: 1px solid #921A39;
    margin: 0;
    cursor: pointer;
}
.butn.main {
    color: #FFF;
    background-color: #C1234B;
    border-color: #901B38;
}


.maintitle, .col_c_icon img[src*='f_'], .f_icon, .topic_buttons li a, .pagination .pages li.active, .post_block h3, .mini_pagination a, #vnc_filter_popup_close, .col_f_icon img, .ipsBadge_green, .input_submit.alt, .popupInner h3 {
    background-color: #C1234B;
    border-color: #921A39;
}

#profile_panes_wrap .ipsFilterbar {
    color: #fff;
    padding: 13px;
    font-size: 17px;
    font-weight: 300;
    background: #A31D3F;
    border: 1px solid #921A39;
}

.champ-title.assem-sort-cont.btcf {
    background-color: #C1234B;
}


#themeEditor #editPattern span, .pagejump:hover, .topic_buttons:hover, .pagination .pages a:hover, .pagejump.topic_buttons li a:hover, .fbutton:hover, ul.post_controls a:hover,.ipsButton_secondary:hover, .user_controls li a, .bbc_spoiler_show:hover, .user_controls li a .answerBadgeInPost, .ipsLikeButton.ipsLikeButton_enabled, .ipsTag {
    background-color: #C1234B;
    border-color: #921A39;
}

.post_block {
    position: relative;
    border-bottom: 6px solid #C1234B;
    padding-bottom: 9px;
    background: #fafafa;
}

.topic_buttons li a {
    background-color: #C1234B;
}

.pagination .pages li.active{
    background-color: #C1234B;
}

.pagination .pages li.active {
    color: #fff;
    padding: 2px 10px;
    cursor: default;
    border: 1px solid #921A39;
}

body {
    background: #f6f6f6;
}

.col_c_icon img[src*='f_'] {
background-color: #C1234B;
}
.ipbwrapper{
	position: relative;
}
#mainContent {
    padding: 0px;
    background: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    box-shadow: none;
    box-shadow: 0 2px 5px 0 rgba(0, 0, 0, 0.16), 0 2px 10px 0 rgba(0, 0, 0, 0.12);
    width: 1300px;
    position: relative;
}

ol.breadcrumb li.first:hover {
    background: #c0392b;
}

#community_app_menu {
        margin-top: 55px;
    position: relative;
}

.statistics_head {
    font-size: 13px;
    font-weight: bold;
    color: #666;
    border-top: 1px solid #ddd;
    border-bottom: 1px solid #ddd;
    background: #111111;
    padding: 12px;
    width: 1594px;
}

.input_submit{
    background: #C1234B;
    border: 1px solid #921A39;
}

.input_submit.alt:hover {
    background-color: #C1234B;
    border-color: #921A39;
}

.input_submit.alt{
    background-color: #111111;
    border: 1px solid #000000;
}

.leaguebtn:hover {
        background-color: #E45F81;
    border: 1px solid #AB4860;
}

.leaguebtn {
    background-color: #C1234B;
    border: 1px solid #901B38;
}

strong {
    font-weight: bold;
    padding-left: 0px;
}

#messenger_utilities.left .ipsSideBlock h3 {
    margin: -10px -11px 10px;
    font-size: 13px;
    font-weight: bold;
    color: white;
    padding: 13px;
    background: #C1234B;
    border: 1px solid #921A39;
}

ul {
padding-left: 10px;
}

.statistics_head {
    margin: -0px -0px 0px -1px;
}

#primary_nav {
    background: none;
    z-index: 1;
    min-width: 600px;
    max-width: 1000px;
    box-shadow: none;
    width: 700px;
    top: 0px;
    height: 50px;
    margin-top: 242px;
    margin-bottom: 0px;
    position: absolute;
    margin-left:315px;
    margin-right:315px;
}

.maintitle {
    background-color: #111111;
    border: 1px solid #000000;
}

.blur_menu {
    background: none;
}

#secondary_navigation {
    background: #111111;
    border: 1px solid #000000;
    height: 42px;
}

#secondary_navigation #breadcrumb li {
    background: #C1234B;
    border: 0 #C1234B;
}

#secondary_links>li a {
    background: #111111!important;
    position: relative;
    border: 0px solid #C1234B!important;
    transition: background .25s, border-color .25s, margin .125s;
    cursor: pointer;
    margin-top: 0px;
}

.bbc {
	padding-left: 0px;
}

.ipsVerticalTabbed_tabs>ul {
z-index: 1000;
}

#socialLinks a {
    background: #C1234B;
        margin-top: 0px;
}

.message, .message a, .message h3 {
    color: #000000;
}

.message {
        background: #F6F6F6;
    padding: 10px;
    border: 1px solid #111111;
    line-height: 1.6;
    font-size: 12px;
    margin: 0px 0px;
    border-left-width: 0px;
    border-right-width: 0px;
}


.border {
    border: 1px solid #000000;
}

#user_bar {
    background: rgba(0, 0, 0, 0.3);
    min-width: 280px;
    max-width: 1882px;
    width: 300px;
    height: 80px;
    z-index: +5;
    top: 265px;
    position: relative;
}

.blur_ubar {
    background: none;
}

#user_navigation {
    float: left;
}

#search {
    float: left;
    margin-left: 3px;
    margin-right: 0px;
    margin-top: 3px;
}

#search_options {
    float: left;
}

#search_wrap {
    position: relative;
        width: 190px;
    padding-left: 10px;
    display: block;
    min-width: 200px;
    padding: 0px 34px 0px 0px;
    margin-left: 10px;
}

#branding {
    width: 510px;
    padding-left: 390px;
    padding-top: 0px;
    height: 100px;
    padding-bottom: 170px;
}

#secondary_navigation #breadcrumb {
    z-index: +5;
}

ol#tabs.clearfix li a.active {
    background: #C1234B!important;
    border: 1px solid #921A39!important;
    text-shadow: 0 1px 1px #541525!important;
    -webkit-box-shadow: inset 0 1px 0 #96281B!important;
    -moz-box-shadow: inset 0 1px 0 #96281B!important;
    box-shadow: inset 0 1px 0 #711E33!important;
}

#secondary_navigation #breadcrumb li {
    display: inline-block;
    line-height: 30px;
    background: #C1234B!important;
    position: relative;
    border: 0px solid #C1234B!important;
    transition: background .25s, border-color .25s, margin .125s;
    cursor: pointer;
    margin: 0px;
}

a:hover {
    color: #7B152F;
}

#user_navigation a {
    color: #fff;
    float: left;
    padding: 0 8px;
    line-height: 30px;
    outline: none;
    height: 30px;
    margin: 6px 2px;
    background: rgba(0, 0, 0, 0.3);
    box-shadow: inset 0 0 0 1px rgba(0, 0, 0, 0.10);
}

a#user_link {
    margin-right: 25px;
}

.statistics_head {
    font-size: 13px;
    font-weight: bold;
    color: #666;
    border-top: 1px solid #ddd;
    border-bottom: 1px solid #ddd;
    background: #111111;
    padding: 12px;
    width: 1276px;
}

#footer_utilities, #footer_utilities a {
    color: #111;
}

#footer_utilities {
    width: 1290px;
}
}
