css:
@-moz-document domain("127.0.0.1"), domain("kingdomofloathing.com") {
/* reverse images */
img {
   -webkit-filter: invert(1);
   filter: invert(1);
}

a.close {
    border-color: white !important;
}

span.label {
    padding: 3px;
}

.blur1 {
    color: transparent !important;
    text-shadow: 0 0 1px #fff, 1px 0 1px #fff;
}

/* BEGIN pool code */
#poolc {
    -webkit-filter: invert(1);
    filter: invert(1);
}
#pool {
    -webkit-filter: invert(1);
    filter: invert(1);
}
#pool > div[class="p"] {
    -webkit-filter: invert(1);
    filter: invert(1);
}
#pool > div[class="p flip"] {
    -webkit-filter: invert(1);
    filter: inver(1);
}
#pool > div[class="splash"] {
    -webkit-filter: invert(1);
    filter: invert(1);
}
/* END pool code */

img[src="https://s3.amazonaws.com/images.kingdomofloathing.com/WR.png"] {
    -webkit-filter: invert(0);
    filter: invert(0);
}
td[bgcolor="black"] {
    background-color: white !important;
}

textarea {
    border-color: white !important;
}
.rcm {
    border-color: white !important;
}
#pop_ircm {
    border: 1px solid white !important;
    border-radius: 0px !important;
}

div[style="color:white;background-color:blue;padding:2px 15px 2px 15px;white-space: nowrap;text-align:center; font-weight: bold"] {
    background-color: blue !important;
}
.close {
    background-color: blue !important;
}

input {
    border-color: white !important;
} 
/* reverse text */
*:not(font) {
    color: white !important;
    background-color: black !important;
}
div[style*="color: green"] {
    color: green !important;
    background-color: rgba(0, 0, 0, 0) !important;
}
font[color="black"] {
    color: white !important;
    background-color: black !important;
}
font[color="000000"] {
    color: white !important;
    background-color: black !important;
}
font[color="red"] > b, .elementhot {
    color: red !important;
}
font[color="blue"] > b, .elementcold {
    color: blue !important;
}
font[color="green"] > b, .elementstench {
    color: green !important;
}
font[color="gray"] > b, .elementspooky {
    color: gray !important;
}
font[color="blueviolet"] > b, .elementsleaze {
    color: blueviolet !important;
}

*[style="color: blue"] {
    color: blue !important;
}
/**:not(font) > * {
    color: white !important;
    background-color: black !important;
}*/

#rollover {
    color: red !important;
}

/* keep blue things blue */
font[color="blue"] {
    color: blue !important;
}

/* CHAT */
.header {
    background-color: blue !important;
}

.chatdisplay {
    padding: 5px;
    border-color: white !important;
}
.label {
    padding: 1px 2px;
    border-color: white !important;
    border-style: solid !important;
    border-width: 2px !important;
}


/* chat input box*/
.inputform {
    border-color: white !important;
}
#graf {
    border-color: white !important;
}
input[value="Chat"] {
    border-color: white !important;
}

/* the little tooltips that tell you how many messages are in a given channel */
.count {
    background-color: red !important;
    border-color: #333333 !important;
}
td[bgcolor="blue"] > b {
    text-shadow: black 0px 0px 5px !important;
}
td[bgcolor="#5A5A5A"] {
    background-color: #5A5A5A !important;
    border-radius: 8px 0px !important;
}
img {
    background-color: white !important;
}

.active {
    border-right: none !important;
}

div[class="msg event"] > .guts {
    color: green !important;
}

font[color="green"] {
    color: green !important;
}

font[color="CC9900"] > *, *[style="color: rgb(204, 153, 0);"] {
    color: #C90 !important;
}
font[color="FFCC00"] > *, *[style="color: rgb(255, 204, 0);"] {
    color: #FC0 !important;
}
font[color="CC3300"] > *, *[style="color: rgb(204, 51, 0);"] {
    color: #C30 !important;
}
font[color="FF0033"] > *, *[style="color: rgb(255, 0, 51);"] {
    color: #F03 !important;
}
font[color="FF33CC"] > *, *[style="color: rgb(255, 51, 204);"] {
    color: #F3C !important;
}
font[color="FF99FF"] > *, *[style="color: rgb(255, 153, 255);"] {
    color: #F9F !important;
}
font[color="663399"] > *, *[style="color: rgb(102, 51, 153);"] {
    color: #639 !important;
}
font[color="9933CC"] > *, *[style="color: rgb(153, 51, 204);"] {
    color: #93C !important;
}
font[color="CC99FF"] > *, *[style="color: rgb(204, 153, 255);"] {
    color: #C9F !important;
}
font[color="000066"] > *, *[style="color: rgb(0, 0, 102);"] {
    color: #006 !important;
}
font[color="0000CC"] > *, *[style="color: rgb(0, 0, 204);"] {
    color: #00C !important;
}
font[color="9999FF"] > *, *[style="color: rgb(153, 153, 255);"] {
    color: #99F !important;
}
font[color="336600"] > *, *[style="color: rgb(51, 102, 0);"] {
    color: #360 !important;
}
font[color="339966"] > *, *[style="color: rgb(51, 153, 102);"] {
    color: #396 !important;
}
font[color="66CC99"] > *, *[style="color: rgb(102, 104, 153);"] {
    color: #6C9 !important;
}
font[color="CCCC00"] > *, *[style="color: rgb(204, 204, 0);"] {
    color: #CC0 !important;
}
font[color="FF9900"] > *, *[style="color: rgb(255, 153, 0);"] {
    color: #F90 !important;
}
font[color="000000"] > *, *[style="color: rgb(0, 0, 0);"] {
    color: white !important;
}
font[color="666666"] > *, *[style="color: rgb(102, 102, 102);"] {
    color: #666 !important;
}
font[color="CCCCCC"] > *, *[style="color: rgb(204, 204, 204);"] {
    color: #CCC !important;
}
.help {
    border-color: white !important;
}
a.tab > .label {
    background-color: gray !important;
    border: 1px solid white !important;
}
a[class="tab unread"] > .label {
    text-decoration: bold !important;
    background-color: gray !important;
    border: 1px solid white !important;
}
/* The channel you're in */
a[class="tab active"] > span.label {
    background-color: black !important;
    border: 2px solid white !important;
}
a[class="tab"], a[class="tab unread"], a[class="tab active"] {
    padding: 0px !important;
    padding-bottom: 3px !important;
}
div[class="msg welcome"] > .guts {
    color: green !important;
    text-decoration: bold !important;
}

span.welcome {
    color: green !important;
    text-decoration: bold !important;
}

.event > * {
    color: green !important;
}

.welcome > b {
    color: green !important;
}

#awesomedesc {
    border-color: white !important;
    border-radius: 0px !important;
}


/*td[align="center"] > b {
    background-color: blue !important;
    background-repeat: repeat-x;
	background-position: 0 0;
	width: 100%;
	height: 23px;
	margin: 0;
	padding: 0;
}*/

td[style="color: white;"] {
    background-color: blue !important;
    background-repeat: repeat-x;
	background-position: 0 0;
	width: 100%;
	height: 20px;
	margin: 0;
	padding: 0;
}


/* daily reminders */
td[bgcolor="orange"] {
    background-color: orange !important;
}

button[class="button bfast"] {
    border: 2px solid white !important;
}

div#bfastholder > table > tbody > tr > td {
    border: 5px solid orange !important;
    border-top: 0px !important;
}

div#bfastholder > table > tbody > tr > td[height="4"] {
    border: 0px !important;
}
/* end reminder code */


/* matrix-y "the source" challenge path text */





/* SOMEBODY ELSE WROTE THIS */
/* --- Round border corners --- */

td[style*="border: 1px solid"], .tborder, #recipeContent {
  border: 5px solid blue !important;
  border-top: 0px !important;
  -moz-border-top-colors: rgb(65,65,152) darkblue !important;
  -moz-border-bottom-colors: rgba(0,0,0,0.8) rgb(46,116,177) !important;
  -moz-border-left-colors: rgba(0,0,0,0.8) rgb(46,116,177) !important;
  -moz-border-right-colors: rgba(0,0,0,0.8) rgb(46,116,177) !important;
/*  border-radius: 8px/12px !important;*/
    border-radius: 0px 0px 12px 12px !important;
}
[style*="border: 1px solid"], td[bgcolor*="e"] {
    border-bottom: 0.15em solid #333 !important;
/*  border-radius: 8px/16px !important;*/
    border-radius: 8px 8px 0px 0px !important;
}

/* The odd jobs board --Elronnd */
td[style="border: 1px solid black;"] {
    border: 1px solid white !important;
    border-radius: 0px !important;
}
#InputForm, textarea, #recipeHeader { border-radius: 6px/12px !important;}


/* --- Blue headers --- */

td[bgcolor="blue"][width="50"] + td, 
td[bgcolor="blue"][width="50"]:first-child {
  border-right: none !important; border-radius: 0px !important;}
td[bgcolor="blue"][width="50"] + td, 
td[bgcolor="blue"][width="50"]:last-child {
  border-left: none !important; border-radius: 0px !important;}

td[bgcolor="blue"][width="50"]:first-child { 
  border-radius: 8px 0px 0px 6px !important;}
td[bgcolor="blue"][width="50"]:last-child {
  border-radius: 0px 8px 6px 0px !important;}
td[bgcolor*="e"] { border-bottom: none !important;}

td[bgcolor="blue"] *, td[bgcolor="orange"] * {
  background: transparent !important;
  background-repeat: repeat-x !important; }


td[bgcolor="blue"], td[bgcolor="orange"]/*, p.rcm:hover, #recipeHeader, #jumptobot, #fortuneCookieCounter, a[target="mainpane"] */{
    background-image: url("data:image/png;base64,") !important;
    background-repeat: repeat-x !important; }

table[style="border: 1px solid #5A5A5A"] {
    border-radius: 8px 0px !important;
}
}
