css:
@namespace url(http://www.mozilla.org/keymaster/gatekeeper/there.is.only.xul);

@-moz-document url("about:fvdsddials") {
  
  .topMenuLine{
    opacity: 0;
    transition: opacity ease-in-out 1s;
  }
  .topMenuLine:hover{
    opacity: 1;
  }
  
  .topMenuLine .group, .sd_cell .preview_parent{
    border-radius: 0;
  }
  
  .sd_cell .preview_parent{
    filter: blur(3px) saturate(150%);
    opacity: .7;
    transition: all ease-in-out .2s;
  }
  .sd_cell:hover .preview_parent{
    filter: blur(0) saturate(100%) drop-shadow(5px 5px 5px rgba(0,0,0,.5));
    opacity: 1;
  }
}
