css:
@-moz-document url-prefix("http://www.byfandom.org/") {
body,
.ow_canvas,
.jhtmlarea.ow_smallmargin,
.ow_bg_color,
.ow_box.ow_stdmargin.clearfix,
.ow_box_cap,
.ow_box.ow_stdmargin.clearfix.profile-BLOGS_CMP_UserBlogWidget,
.ow_box.ow_stdmargin.ow_break_word,
.ow_site_panel,
.ow_highbox,
.ow_avatar_console,
.ow_box,
.ow_comments_mipc
{
/* This colors the background of the page */
    background-color: #daf2ed !important;
}

.ow_tooltip_body, .ow_tooltip {
/* This colors the activity notification boxes at the top of the site */
    background-color: #daf2ed !important;
    border: 1px solid #3d3e41;
}

/* some text needs to be the same colour as the background */
.ow_highbox.ow_center a {
    color: #daf2ed !important;
}

body,
html,
.ow_stdmargin.ow_ic_user,
.ow_stdmargin.ow_ic_house,
.ow_stdmargin.ow_ic_files,
.ow_stdmargin.ow_ic_forum,
.ow_stdmargin.ow_ic_write,
.ow_stdmargin.ow_ic_picture,
.ow_stdmargin.ow_ic_script {
/* This colors the color and size of regular text site-wide */
    color: #3d3e41 !important;
    font-family: Ubuntu, Verdana, sans-serif !important;
    font-size: 16px !important;
    line-height: 20px !important;
}

a {
/* This colors the color of link text site-wide */
    color: #20806a !important;
    text-decoration: none;
}

.ow_newsfeed_body,
.ow_dnd_widget.group-FORUM_CMP_LatestTopicsWidget,
.ow_alt2, .ow_alt1,
.feed_item_sharing_section.ow_box,
.ow_newsfeed_comments,
td,
th {
/* This colors the the background of most container boxes on the newsfeed, blogs, and forums */
    background-color: inherit !important;
}

.input_ws_cont,
.htmlarea_styles {
    /* this makes the background of forum text editors white */
    background-color: white !important;
}

.ow_tr_delimiter td {
/* This prevents the background color of the forum boxes from over running their container */
    background-color: transparent !important;
}

.ow_newsfeed_status_input,
.invitation,
#about_me_widget_input {
    /* fixing the font in some of the text-area input boxes because eww, serif fonts */
    font-family: Ubuntu, Verdana, sans-serif !important;
}

.ow_header {
    background-color: black;
}

.ow_header_pic {
    /* changing the header pic, and some display stuff */
    background-image: url(http://i.imgur.com/1dBaQLq.png) !important;
    background-position: left;
    background-size: auto;
    height: 100px;
}

.ow_quote_header {
    /* making forum quotes match everything else */
    background-color: #2db395;
}

.ow_chat_block.ow_active {
    /* I could hardly see the chatbox, so I've given it a border */
    border: 1px solid #3d3e41;
}

.ow_section,
.ow_label,
.ow_ic_comment,
.ow_ic_user,
.ow_ic_picture,
.ow_ic_write,
.ow_ic_rss,
.ow_ic_friends,
.ow_ic_clock,
.ow_ic_info,
.ow_remark.ow_smallmargin {
    /* colouring some incidental text, widget headings and whatnot */
    color: #84868d !important;
}

.ow_logo {
    /* hiding the top left corner logo */
    visibility: hidden;
}
}
