css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("dagbladet.no") {
  body {
    color:#fff;
    background-color: #222;
  }
  main {
    background-color: #222;
  }
  article.preview .comments {
    color:#ccc;
  }
  .top-nav .primary .search input[type="search"] {
    background-color: #222;
    color: #fff;
    border-color: #222;
  }
  .top-nav .primary .search input[type="search"]:focus {
    border-color: #ddd;
  }
  .top-nav .primary .search::before {
    color: #ddd;
  }
  .row.secondary {
    color:#fff;
    background-color: #444;
  }
  .row.secondary .deck {
    color: #fff;
  }
  .row.secondary article.preview:not(.primary):not(.orange):not(.yellow):not(.green):not(.blue):not(.purple):not(.burgundy):not(.pink):not(.secondary):not(.black), article.preview.secondary {
    color:#fff;
  }
  .row.secondary article.preview:not(.primary):not(.orange):not(.yellow):not(.green):not(.blue):not(.purple):not(.burgundy):not(.pink):not(.secondary):not(.black) > a, article.preview.secondary > a {
    background: transparent;
  }
  .row.secondary article.preview:not(.primary):not(.orange):not(.yellow):not(.green):not(.blue):not(.purple):not(.burgundy):not(.pink):not(.secondary):not(.black) .label, article.preview.secondary .label {
    color: #fff;
  }
  .row.extrinsic {
    color: #fff;
    background-color: #666;
  }
  .row.extrinsic .deck {
    color: #fff;
  }
  a {
    color: #fff;
  }
  .tv-guide .section-bar .heading {
    color: #fff;
  }
  .tv-guide .tvguide-legend li a {
    color: #fff;
  }
  h1, h2, h3, h4, h5, h6 {
    color: #fff;
  }
  .comic-entry .title-group h2 span {
    color: #fff;
  }
  .panel {
    background-color: #444;
    border-color: #444;
    color: #fff;
  }
  .section-title span {
    background-color: #444;
  }
  .article-list li a {
    color: #fff;
  }
  .label.black {
    background-color: #444;
  }
  
  /* ARTICLE PAGE */
  #menuwrapper {
    border-color: #444;
  }
  .upper {
    background: #222;
  }
  .nav.upper a {
    color: #fff;
  }
  .lower {
    background-color: #444;
  }
  .nav.lower .hottopics {
    background-image: none;
    color: #d60000;
  }
  .nav a:hover {
    background-image: none;
    color: #d60000;
  }
  .nav a {
    color: #ddd;
  }
  .db-search-10 input.searchText {
    background: #222;
  }
  .db-search-10 input.searchText:hover, .db-search-10 input.searchText:focus {
    background: #222;
  }
  .db-search-10 a.db-button {
    border: #fff solid 1px;
  }
  .db-button {
    background: #fff;
  }
  .db-search-10 a.db-button {
    border-color: #fff;
  }
  #doc {
    background-color: #222;
  }
  p.image-caption {
    color: #ddd;
  }
  p.image-caption b, p.image-caption strong {
    color: #ddd;
  }
  .vcard .fn {
    color:#ddd;
  }
  .vcard a.email {
    color:#fff;
  }
  .article-body .tool-box .article-tools {
    background-color: #444;
    border-color: #555;
  }
  a:link {
    color:#fff;
  }
  a:hover {
    color: #d60000;
  }
  #dzGroup .related-container {
    border-color: #444;
  }
  .l-gray {
    background: #444;
  }
  p.image-caption {
    border-color: #444;
  }
  .ref h2 a:link, .ref h2 a:visited {
    color:#fff;
  }
  .ref {
    border-color: #ddd;
  }
  #footerwrapper {
    border-color: #444;
  }
  .fullbleed .adunit-wrapper::before {
    color:#fff;
    background-color: #333;
  }
}

@-moz-document domain("dbtv.no") {
  body {
    background: #222;
    color: #fff;
    overflow: auto;
  }
  .top-bar {
    border-color: #555;
    background: #666;
  }
  .top-bar-section ul {
    background: #666;
    color: #fff;
  }
  .top-bar-section li a:not(.button){
    background-color: #666;
    color: #fff;
  }
  .top-bar-section > ul > .divider, .top-bar-section > ul > [role="separator"] {
    border-color: #555;
  }
  .top-bar-section .has-dropdown > a::after {
    border-color: #fff transparent transparent;
  }
  nav.top-bar #search {
    background-color: #666;
  }
  section.player #player .caption .description, section.player #player .caption .datepublished {
    color: #ddd;
  }
}
