css:
@-moz-document domain("soundcloud.com") {
.dialog{
    border: 0px;
    height: 50px;
    min-width: 220px;
    padding: 0px;
}

.dialog.rounded{
    border-radius: 0px;
}

.dialog__arrow{
    Visibility: hidden;
}

.userDialogBadge {
    height: 200px;
    margin-bottom: 0px;
}

.userDialogBadge__avatar{
    float:left;
    height: 0px;
    width: 0px;
}

.userDialogBadge__avatar .sc-artwork{
    border-radius: 0px;
    max-height: 0px;
    max-width: 0px;
}

.userDialogBadge__avatar .m-loaded{
    z-index: -1;
}

.userDialogBadge__avatar span{
    margin:0px;
    min-height: 220px;
    min-width: 220px;
    position:absolute;
    top:-15px;
	visibility: visible;
    z-index: -1;
}

.userDialogBadge__description {
    background-color: rgba(0,0,0,.75);
    float:left;
    margin: 0px;
    margin-left: 4%;
    margin-top: 4%;
    padding: 2px;
    text-align: left;
}

.userDialogBadge__description .sc-link-dark, .userDialogBadge__description a:hover{
    Color: #CCC;
}

.userDialogBadge__stats {
    background-color: rgba(0,0,0,.75);
    float:left;
    margin: 0px;
    margin-left: 4%;
    margin-right: 70%;
    padding-left: 2px;
    padding-right: 2px;
    text-align: left;
}

.userDialogBadge__stats span:hover {
    color:#999;
}

.userDialogBadge__address {
    visibility: hidden;
    height: 0px;
    width: 0px;
}

.userDialogBadge__action {
    float: right;
    margin: 0px;
    margin-left: auto;
    margin-right: 4%;
    margin-top: 52%;
    text-align: right;
}
}
