css:
@-moz-document domain("joinsquad.com") {
body.ipsfocus_background {
    background: url('http://forums.joinsquad.com/uploads/set_resources_10/005d20f304c7929cbfdb3â€¦/uploads/set_resources_10/005d20f304c7929cbfdb3fe61d088022_background.jpg'), #2D2D2D;
    background-size: auto, 100% auto, auto;
    background-position: 50% 0, 50% 0, 0 0;
}
.ipsfocus_guestAlert {
    background: #E2E2E2;
    color: #000000;
    border: 1px solid rgba(20, 20, 20, 0.15);
    border-radius: 4px;
    line-height: 180%;
    padding-top: 6px;
}
.ipsfocus_guestAlert .ipsfocus_guestHeader {
    background: #2D2D2D;
    box-shadow: inset rgba(0, 0, 0, 0.25) 0px 1px 3px, rgba(255, 255, 255, 0.5) 0px 1px 0px;
    border-radius: 4px;
    color: #EAEAEA;
    text-shadow: rgba(255, 255, 255, 0.4) 0px 1px 0px;
    padding: 14px;
    font-size: 1.3rem;
    font-weight: bold;
    margin: 0 6px;
}
.ipsfocus_guestAlert .ipsfocus_guestButtons {
    background: rgb(226, 226, 226);
    border-top: 1px solid rgb(226, 226, 226);
    border-radius: 0px 0px 4px 4px;
    padding: 10px;
}
.ipsfocus_guestAlert a.guestButton {
    background: #E2E2E2;
    background-image: linear-gradient(to bottom, rgba(255, 255, 255, 0.2) 0%, rgba(255, 255, 255, 0) 100%);
    border: 1px solid rgba(0, 0, 0, 0.2);
    box-shadow: inset rgba(255, 255, 255, 0.3) 0px 1px 0px;
    border-radius: 4px;
    color: #333;
    text-shadow: rgba(255, 255, 255, 0.4) 0px 1px 0px;
    padding: 0 14px 0 46px;
    display: inline-block;
    height: 36px;
    line-height: 34px;
    outline: 0;
    margin-right: 4px;
    font-weight: bold;
    text-decoration: none;
    position: relative;
}
#navbar {
    background: rgb(23, 71, 134) !important;
    position: static !important;
}
.ipsBox:not(.ipsBox_transparent):not(.ipsModerated),
.ipsAreaBackground_reset,
.ipsPageHeader,
.ipsComment,
.ipsPager,
.ipsTabs_panel,
.ipsWidget.ipsWidget_horizontal .ipsTabs_panel,
.cCalendar,
.acpBlock .ipsTable_zebra {
    background-color: rgb(230, 230, 230);
}
.ipsWidget.ipsWidget_vertical .ipsWidget_title,
.ipsWidget.ipsWidget_horizontal .ipsWidget_title,
#elVNC_sidebar .ipsFieldRow_label,
#elSearch_sidebar .ipsFieldRow_label,
.ipsTable.ipsMatrix .ipsMatrix_subHeader,
.ipsSideMenu_title,
.ipsSideMenu_subTitle {
    color: #ffffff;
    background-color: #174786;
}
.ipsButtonBar {
    line-height: 1;
    background: #174786;
}
.ipsDataItem_icon,
.cForumRow .ipsDataItem_main,
.cForumRow .ipsDataItem_stats,
.cForumList .ipsDataItem_icon,
.cForumList .ipsDataItem_main,
.cForumList .ipsDataItem_stats {
    background-color: #D8D8D8;
}
.ipsCode,
blockquote.ipsQuote,
html[dir="rtl"] blockquote.ipsQuote,
iframe[data-embedContent],
.ipsStyle_spoilerFancy {
    background: rgba(0, 0, 0, 0.08);
    border-color: rgba(168, 168, 168, 0.6);
}
a {
    color: #1B1B1B;
    text-decoration: none;
}
}
