css:
@-moz-document domain("userstyles.org") {
* {font-family:Open Sans, Segoe UI !important; color:#ECF0F1 !important}
body {background:#2C3E50 !important; max-width:100% !important; margin:0 !important}
a {text-decoration:none !important}
a:hover {border-bottom:solid 1px #ECF0F1 !important}
a:visited {color:#BDC3C7 !important}
a:active {color:#fff !important}
dt {padding:10px 0 5px 0}
th {font-size:18px !important; font-weight:100 !important}
h1 {font-size:28px !important; font-weight:100 !important}
h2 {font-size:28px !important; font-weight:100 !important}
h3 {font-size:22px !important; font-weight:100 !important}
h4 {font-size:22px !important; font-weight:100 !important; margin:0 0 10px 0 !important}
tr {margin:5px 0}
td {padding:10px !important}
input, textarea {background:none !important; border:solid 1px #34495E !important; padding:5px 7px !important}

/* main */
#main-header {width:100% !important; background:#2C3E50 !important; margin:0 0 15px 0 !important; padding:0 !important; border-radius:0 !important; box-shadow:#000 0 0 10px}
#main-header>h1 {padding:5px 10px 10px 7% !important; color:#ECF0F1 !important; font-weight:100 !important}
#main-header>h1>a {font-weight:normal !important}

#left-sidebar {width:20% !important; margin-top:5px !important}
#left-sidebar>* {background:#2C3E50 !important; border-radius:0 !important; box-shadow:#000 0 0 10px}
#left-sidebar ul {margin:0 !important; padding:10px !important}
#left-sidebar ul li {font-size:14px !important; padding:3px 0 !important}
#left-sidebar h2 {font-size:18px !important; font-weight:100 !important; padding:5px 0 0 10px}
/* search */
#simple-search {width:87% !important; display:inline-block !important}
#search-terms {width:85% !important; float:none !important; display:inline-block !important; background:#2C3E50 !important; border:solid 1px #34495E !important; border-right:none !important; padding:5px !important;}
#search-submit {width:15% !important; float:none !important; display:inline-block !important; background:#2C3E50 !important; border:solid 1px #34495E !important; padding:5px !important; cursor:pointer}
#search-submit:hover {background:#34495E !important}
/* advanced */
#show-advanced-search {width:12% !important; overflow:hidden !important; background:#2C3E50 !important; border:solid 1px #34495E !important; border-left:none !important; float:right !important; text-align:center !important; max-height:27px !important; padding-left:1px !important; font-size:12px !important; text-decoration:none !important}
#show-advanced-search:hover {background:#34495E !important}
#show-advanced-search:before {content:"↓   "; line-height:27px}
#advanced-search {margin:5px 0 !important; border-bottom:none !important}
#advanced-search select {margin:5px 5px 5px 0 !important; background:#2C3E50 !important; border:solid 1px #34495E !important; height:20px !important; font-size:12px !important}
#advanced-search select option {font-size:12px !important; padding:2px 0 !important}
#advanced-search-submit button {position:relative !important; top:-124px; width:13% !important; overflow:hidden !important; background:#2C3E50 !important; border:solid 1px #34495E !important; border-left:none !important; float:right !important; text-align:center !important; max-height:29px !important; padding-left:5px !important; font-size:12px !important; cursor:pointer}
#advanced-search-submit button:hover {background:#34495E !important}
#advanced-search-submit button:before {content:"↑   "; line-height:27px}
#advanced-search-submit input {background:#2C3E50 !important; border:solid 1px #34495E !important; padding:5px 10px !important; margin-right:-30px !important; cursor:pointer}
#advanced-search-submit input:hover {background:#34495E !important}
/* main container */
#main-article {margin-left:21% !important; margin-bottom:30px !important}

body>footer {display:none !important}

/* style page */
#screenshots {border:none !important}
#more-screenshots {padding:10px 0 5px 0 !important}
#more-screenshots span {font-size:16px !important; font-weight:100 !important}
.install-status {border-radius:0 !important}
.install {background:#27AE60 !important}
.install:hover {background:#2ECC71 !important}
.install-info {background:#2980B9 !important}
#style-settings select {background:none !important; border:solid 1px #34495E !important; padding:3px 5px !important}
#style-settings select option  {background:#2C3E50 !important}
/* show css */
#show-button, #control-panel>a {border:solid 1px #34495E !important; padding:5px 15px !important}
#show-button:hover, #control-panel>a:hover {background:#34495E !important}
pre {background:#34495E !important; word-wrap: break-word; width:90% !important; padding:15px !important; border: 1px dashed #7F8C8D !important;}
#additional-info, #discussions-area {width:45% !important; display:inline-block !important; vertical-align:top !important}
section>h2 {margin-left:0 !important}

/* edit page */
#main-article > form > input {background:#2C3E50 !important; border:solid 1px #34495E !important; padding:5px 10px !important; cursor:pointer}
.form-controls label {font-size:18px !important; font-weight:100 !important}
.form-controls input, .form-controls textarea {background:none !important; border:solid 1px #34495E !important; padding:5px 7px !important}
.form-controls textarea {width:90% !important; margin-top:5px !important}
#new-setting input {cursor:pointer}
#new-setting input:hover, #main-article > form > input:hover {background:#34495E !important}
.edit-setting {bakcground:none !important}

/* login */
.login-option-explanation {font-size:18px !important; font-weight:100 !important; margin:10px 0 5px 0 !important}
#login .btn-auth {font-weight:normal; margin-top:4px; width:1px; padding:5px 20px; margin:0 5px; overflow:hidden; background:none !important; border:solid 1px #34495E !important}
#login .btn-auth:hover {background:#34495E !important}
.btn-auth.btn-password {width:150px !important}
#login-existing, #password-existing, #password-login>input {background:none !important; border:solid 1px #34495E !important; padding:5px 7px !important; display:block; margin:0 0 10px 0;}
#remember-normal {display:inline-block !important; margin:0 5px 10px 0 !important;}
[type="submit"]:hover {background:#34495E !important}

/* forum */
#Head {padding:8px 0 !important; background:#2C3E50 !important; box-shadow:#000 0 0 10px; margin:0 0 15px 0 !important}
.SiteTitle, .SiteTitle a {font-weight:normal !important}
#Head a:visited {color:#ECF0F1 !important}
ul.SiteMenu a {font-size:14px !important; font-weight:100 !important; line-height:47px !important}
div.SiteSearch > form {margin:8px 0 !important}
#Head .SiteSearch .InputBox {background:none !important; border:solid 1px #34495E !important}
div.SiteSearch > form > div > input.Button {margin-top:9px !important}
.Username {font-size:18px !important; font-weight:100 !important}
.P > a {background:none !important; border:solid 1px #34495E !important; font-weight:normal !important; box-shadow:none !important; text-shadow:none !important; padding:5px 0 !important; font-size:14px !important; margin:2px 1% !important; width:46% !important; text-align:center !important}
.P > a:hover {background:#34495E !important}
.ConnectLabel.TextColor {font-weight:normal !important; color:#95A5A6 !important}
#Panel .FilterMenu .Active, .PanelInfo .Active {font-weight:normal !important; padding-left:25px !important}
.PanelCategories li:first-child, .PanelCategories li:nth-last-child(2) {border-bottom:solid 1px rgba(0,0,0,.1) !important}
.Count {background:rgba(0,0,0,.2) !important}
.Pager {font-weight:normal !important}
.Title {margin:10px 0 7px 0 !important}
.Title a {font-size:18px !important; font-weight:100 !important}
/* new discussion */
.DiscussionForm label {font-size:22px !important; font-weight:100 !important; margin:0 0 10px 0 !important}
.EmotifyDropdownActive, .EmoticonBox {background-color:#34495E !important}
.EmoticonContainer {background:#34495E !important}
.EmoticonBox {border:solid 1px #34495E !important}
ul.List.Inline.PostOptions > label {font-size:16px !important}
.Button {background:none !important; border:solid 1px #34495E !important; font-weight:normal !important; box-shadow:none !important; text-shadow:none !important; padding:5px 7px !important; font-size:14px !important; margin:2px 5px !important}
a.Button.Cancel:hover {text-decoration:none !important}
}
