css:
@-moz-document url("https://mail.notes.na.collabserv.com/verse"), url("https://mail.notes.na.collabserv.com/verse?") {
/* Top banner */

.ics-scbanner {
    background-color: #325C80;
}
.ics-scbanner > .selected {
    background-color: #4178BE;
    font-weight: bold;
    font-size: 14px;
}
/* E-Mail row */

.seq-msg-row {
    background-color: #F9F9F9;
}
/* Sender line on left and right panel */

.seq-msg-row .name {
    color: #000000;
}
/* Unread sender line on left and right panel */

.seq-msg-row.unread .name {
    color: #000000;
}
/* Subject line on left and right panels */

.seq-msg-row .subject {
    color: #4178BE;
}
/* Unread mail in red */

.seq-msg-row.unread .subject {
    color: #FF0000;
}
/* Unread thread before bar */

li.seq-msg-row.unread:before {
    background-color: #FF0000;
}
/* Selected thread when loses focus */

li.seq-msg-row.selected:before {
    background-color: #4178BE;
}
/* Begin v 1.4 */
/* High priority mail changes */

.seq-msg-row.importance:before {
    background-color: #325C80;
}
.seq-msg-row.importance.unread:before {
    background-color: #FF0000;
}
.seq-msg-row.importance .avatar {
    border: 2px solid #325C80;
}
.seq-msg-row.importance .avatar .initials {
    background-color: #325C80;
    color: #FFFFFF;
}
.seq-msg-row.importance .avatar:hover {
    border: 2px solid #4178BE;
}
.seq-msg-row.importance .priority {
    margin-top: -5px;
}
/* Divider row */

.seq-msg-row .divider,
.seq-msg-row .dueDateDivider {
    height: 20px;
    line-height: 20px;
    background-color: #325C80;
    color: #FFFFFF;
    font-weight: bold;
}
/* End v 1.4 */

.read-view .sent-indicator {
    font-weight: bold;
}
.socmail-calendarreadview .pimMailSubject {
    color: #00000;
}
/* Abstract on left panel */

.seq-msg-row .abstract,
.seq-msg-row.unread .abstract {
    display: none;
}
/* Show notes on to-do messages */

.msg-row-due-today .abstract,
.msg-row-due-tomorrow .abstract,
.msg-row-due-one-week .abstract,
.msg-row-due-none .abstract {
    display: inline;
}
/* Date on left panel */

.seq-msg-row .date,
.seq-msg-row.unread .date {
    color: #999999;
}
/* Unread message line */

.seq-msg-row.unread {
    background-color: #FAFAFA;
}
/* Unread message line */

.seq-msg-row.unread.name {
    font-weight: bold;
}
/* Unread message line hover */

.seq-msg-row.unread:hover {
    background-color: transparent;
}
/* Unread message line hover left border */

.seq-msg-row.unread:hover:before,
.seq-msg-row.unread.importance:hover:before {
    background-color: #4178BE;
}
/* Name list in right panel */

.socpimMailingList .socpimNameBtn,
.socpimCombinedList,
.name {
    color: #4178BE;
}
.pimInvitees .socpimNameBtn,
.pimPerson {
    color: #4178BE !important;
}
/* Thread Updates */

.threaded .read-view .action-bar {
    padding: 15px 20px 14px 56px;
}
/* The little down arrow on a threaded note */

.read-view.body-open button.toggle-btn .twisty,
.preview-panel.user-searched .read-view.search-match.body-open button.toggle-btn .twisty {
    fill: #325C80;
}
.preview-panel.user-searched .read-view.search-match:not(.body-open) button.toggle-btn .triangle {
    fill: #4178BE;
}
.preview-panel.user-searched .read-view.search-match:not(.body-open) button.toggle-btn {
    border: 1px solid #4178BE;
    border-radius: 8px;
}
.read-view button.toggle-btn {
    top: 7px;
}
.read-view.body-open button.toggle-btn {
    top: 10px;
}
.threaded .read-view:not(.body-open) {
    height: 30px;
    padding: 0px 20px 0px 56px;
}
.pimInviteAction {
    color: #FF8F3A;
}
.pimInviteFree .pimInviteAction {
    color: rgb(180, 224, 81);
}
.pimEventComment {
    padding-top: 5px;
}
.pimInviteState .pimTime {
    margin: 5px 0;
}
.socpimEventDateRangeRow,
.socpimEventDateRangeRow .socpimEventFieldName,
.socpimEventLocationRow .socpimEventFieldName {
    font-weight: bold;
}
/* Mail content right panel */

.socmail-mailreadview .pim-mailread-mailcontent {
    border-top: 1px solid #eeeeee;
    margin-top: 10px;
}
/* Hover thread */

.seq-msg-row:hover {
    background-color: transparent;
}
/* Hover thread left border */

.seq-msg-row:hover::before,
.seq-msg-row.importance:hover::before {
    background-color: #4178BE;
}
/* Selected thread */

.seq-msg-row.selected {
    background-color: transparent;
}
/* Selected thread left border */

.seq-msg-row.selected::before {
    background-color: #cccccc;
}
/* Right panel left border */

.threaded .read-view.body-open::before {
    background-color: #cccccc;
}
.seq-mcv.preview .inbox-container {
    width: 30%;
}
/* Calendar Bar Updates */
/* Next meeting subject*/

#nextMeeting .nextMeetingSubject {
    color: #4178BE;
}
/* Meeting event bars */

#eventBar .meeting {
    background-color: #4178BE;
}
.calendarToday {
    color: #4178BE !important;
}
/* End Calendar Bar Updates */
/* Calendar Popup Updates */

.bar.event.meeting,
.eventPopup > .meeting,
.eventPopup > .past-event.meeting,
.eventPopup > .past-event.alldayevent,
.bar.event.alldayevent,
.eventPopup > .alldayevent,
.bar.event.appointment,
.eventPopup > .appointment,
.bar.event {
    background: #4178BE;
}
.eventPopup > .meeting::before,
.eventPopup > .alldayevent::before,
.eventPopup > .anniversary::before,
.eventPopup > .appointment::before {
    background: #325C80;
}
.eventPopup > .eventDetailContainer,
.eventPopup > .meeting dd.subject,
.eventPopup > .alldayevent dd.subject,
.eventPopup > .anniversary dd.subject,
.eventPopup > .appointment dd.subject,
.eventPopup > .meeting .online-meeting-password-mask {
    color: #FFFFFF;
}
/* Keep reminders darker so they can be seen */

.eventPopup > .eventDetailContainer.reminder {
    color: #006d5d;
}
.eventDetailContainer .online-meeting-password {
    color: #4178BE;
}
.eventDetailContainer button svg {
    fill: #000000;
}
.eventPopup > .unprocessed {
    color: #000000;
}
.eventPopup {
    width: 35%
}
/* End of Calendar Popup Updates */

.seq-mcv.preview .left-header {
    width: 30%;
}
.preview-panel {
    width: 70%;
    left: 30%;
}
/* Update not YS */

.static-sets {
    margin-right: 14px;
}
.static-sets:after {
    height: 30px;
    display: block;
    border-right: 2px solid #E8E8E8;
    content: "";
    position: absolute;
    right: -7px;
    top: 7px;
}
.carousel .loading-indicator {
    bottom: 18px;
}
.setSideBar {
    height: 45px;
    width: 60%;
}
.setSideBar .set-actions {
    padding: 2px 0px 8px 20px;
    display: inline;
}
.setSideBar .add-contacts {
    margin-bottom: 0px;
    bottom: 8px;
}
.setSideBar .remove-contacts {
    bottom: 14px;
}
.seq-window .seq-header {
    position: absolute;
    width: 40%;
    height: 45px;
    left: 60%;
    top: 0%;
}
.seq-window.calendar .seq-mcv {
    height: calc(100% - 144px);
}
.suggested-sets::before {
    display: none;
    height: 30px;
    top: 7px;
}
.suggested-sets {
    margin-left: 0px;
}
.carousel.sets-overflown .scroll-button.left:after,
.carousel.sets-overflown .scroll-button.right:before {
    height: 50px;
}
.active-sets {
    margin-right: 14px;
}
.active-sets:after {
    height: 30px;
    display: block;
    border-right: 2px solid #E8E8E8;
    content: "";
    position: absolute;
    right: -7px;
    top: 7px;
}
.carousel .scroll-button {
    top: 16px;
}
.set-base {
    height: 25px;
    width: 25px;
    margin: 7px 7px 0px;
}
.active-sets .drop-target .drop-target-button {
    top: -10px;
    left: -10px;
    border: 2px solid #676767;
}
.active-sets .drop-target .drop-target-button svg {
    top: -10px;
    left: -9px;
    height: 19px;
    width: 19px;
}
/* tky */
.active-sets .drop-target {
    margin: -4px;
}

/* Update */

.set-base {
    border: 2px solid transparent !important;
}
.set-base.selectedSet,
.set-base.selectedSet.selected {
    margin: 7px 7px 0px;
    border: 2px solid #4178BE !important;
}
.set-base .new-indicator {
    top: -3px;
    right: -3px;
}
.keyset.inbox.show-focus-count,
.keyset.needs-attention.show-focus-count,
.keyset.waiting-for.show-focus-count {
    background-position: center 1px !important;
    background-size: 12px !important;
}
/* Icon realignment v1.5 */

.keyset.inbox.show-focus-count .set-icon,
.keyset.needs-attention.show-focus-count .set-icon,
.keyset.waiting-for.show-focus-count .set-icon {
    background-position: center 16px !important;
    background-size: 16px !important;
    width: 14px;
    height: 0px;
    top: 0px;
    left: 6px;
}
.keyset.inbox .set-icon,
.keyset.needs-attention .set-icon,
.keyset.waiting-for .set-icon {
    width: 20px;
    height: 0px;
    top: 2px;
    left: 3px;
}
/* Icon realignment v1.5 */

.keyset.inbox .setLabel,
.keyset.needs-attention .setLabel,
.keyset.waiting-for .setLabel {
    display: none;
}
.set-base .setLabel {
    font-size: 9px;
    margin-left: -55%;
    z-index: 2;
    top: -10px !important;
}
.people-set .ic-bizcard-section1 .action {
    width: 25px;
    height: 25px;
    padding: 0;
    padding-top: 1px;
    border: 2px solid transparent;
}
.people-set .ic-bizcard-section1 .action.act-compose {
    left: -16px;
    top: 15px;
}
.people-set .ic-bizcard-section1 .action.act-chat {
    left: 0px;
    top: 25px;
}
.people-set .ic-bizcard-section1 .action.act-card {
    left: 17px;
    top: 15px;
}
.people-set.chat-offline .ic-bizcard-actions .act-chat {
    border-color: transparent;
}
.people-set .ic-bizcard-section1 .action:hover,
.people-set .ic-bizcard-section1 .action:focus {
    padding: 0px;
    padding-top: 1px;
    border-color: transparent;
    background-color: #e8e8e8;
    z-index: 999;
}
/* Avatar Updates */
/* normal color for avatar circle */

.seq-msg-row .avatar .initials {
    color: #325C80;
}
.seq-msg-row .avatar {
    border: 1px solid #325C80;
}
.seq-msg-row .avatar:hover {
    border: 1px solid #4178BE;
}
.avatar .initials {
    font-size: 16px;
}
.keyset.inbox.show-focus-count .focus-count,
.keyset.needs-attention.show-focus-count .focus-count,
.keyset.waiting-for.show-focus-count .focus-count {
    margin-top: 13px !important;
    font-size: 10px !important;
    width: 40px !important;
    height: 12px !important;
    border-radius: 0px 0px 12px 12px !important;
    line-height: 12px !important;
}
.seq-search-container .oti-model .shadow-input {
    background-color: transparent !important;
    top: 0px !important;
}
.seq-search-container .oti-view .placeholder,
.oti-view .displayed {
    line-height: 20px;
}
.seq-search-container .chiclet {
    height: 22px !important;
    margin-top: 2px !important;
}
.seq-search-container .chiclet .chiclet-label {
    line-height: 22px !important;
}
.seq-search-container .chiclet.contactChiclet .show-tray-button {
    padding-top: 2px !important;
}
.seq-search-container .chiclet .chiclet-close {
    margin-top: 5px !important;
}
/* From Steve B */
/* Following are the updates to the Draft/Sent box */

.seq-window .compose-button,
.seq-window .outgoing-button {
    background-color: #325C80;
}
.seq-window .compose-button:hover,
.seq-window .outgoing-button:hover {
    opacity: 0.8;
}
.seq-window.tray-open .outgoing-button {
    opacity: 0.8;
    background-color: #325C80;
}
.outgoing-tray {
    left: 3%;
    top: -95px;
    background-color: #325C80;
}
.outgoing-view dt {
    left: -2000px;
}
.outgoing-view .discard svg {
    background-color: #4178BE;
}
.outgoing-view:hover {
    background-color: #4178BE;
}
.outgoing-view:hover:before {
    background-color: #FFFFFF;
}
.outgoing-view .disabled {
    background-color: #4178BE;
}
.seq-window.tray-open .outgoing-tray .show-sent,
.seq-window.tray-open .outgoing-tray .show-drafts {
    background-color: #325C80;
}
/* End of Draft/Sent box changes */

.dockable.has-action-bar .action-bar {
    height: 25px;
}
.seq-msg-row .hover-actions .attention-toggle-button .attention-icon .icon-iouo {
    margin-top: 0px;
}
.triage-actions {
    position: relative;
    top: 8px;
}
.triage-actions .triage-action svg {
    fill: #325C80;
}
.triage-actions .div.triage-action {
    padding-top: 12px !important;
}
.threaded .socmail-mailreadview.collapsed:hover .triage-actions .enabled,
.threaded .socmail-mailreadview.collapsed:focus .triage-actions .enabled {
    padding-top: 0px !important;
}
.socmail-mailreadview .pim-mailread-mailcontent {
    margin-top: 0px;
}
/* Begin v1.5 */
/* Color the bee */

.seq-mcv .bee {
    fill: #325C80;
    height: 120px;
}
/* Make the folder view smaller */

.folder-view .this-folder {
    height: 24px;
}
/* Make the message row smaller */

.seq-msg-row .msg-info {
    padding: 4px 6px 4px 0px;
}
.seq-msg-row.thread .count {
    color: #FFFFFF;
    background-color: #325C80;
    border-radius: 4px;
}
/* Begin v1.5 */
/* Update business cards */

.people-set .ic-bizcard-section1 .action {
    width: 25px;
    height: 25px;
    padding: 0;
    padding-top: 1px;
    border: 2px solid transparent;
}
.people-set .ic-bizcard-section1 .action.act-compose {
    left: -16px;
    top: 15px;
}
.people-set .ic-bizcard-section1 .action.act-chat {
    left: 0px;
    top: 25px;
}
.people-set .ic-bizcard-section1 .action.act-card {
    left: 17px;
    top: 15px;
}
.people-set.chat-offline .ic-bizcard-actions .act-chat {
    border-color: transparent;
}
.people-set .ic-bizcard-section1 .action:hover,
.people-set .ic-bizcard-section1 .action:focus {
    padding: 0px;
    padding-top: 1px;
    border-color: transparent;
    background-color: #325C80;
    z-index: 999;
}
.people-set.bizcard-open .ic-bizcard-section1,
.people-set.bizcard-open .ic-bizcard-section2 {
    background-color: #325C80;
}
.ic-bizcard.ic-bizcard-list.bizcard-open {
    height: 17em;
}
.ic-bizcard.ic-bizcard-vert .ic-bizcard-section1,
.ic-bizcard.ic-bizcard-vert .ic-bizcard-section2 {
    background-color: #325C80;
}
.ic-bizcard.ic-bizcard-list .ic-bizcard-section1 > div,
.ic-bizcard.ic-bizcard-list .ic-bizcard-section2 > div {
    background-color: #325C80;
}
.ic-bizcard.ic-bizcard-list .ic-bizcard-section1,
.ic-bizcard.ic-bizcard-list .ic-bizcard-section2 {
    background-color: #325C80;
}
.ic-bizcard.ic-bizcard-vert .ic-bizcard-section2 > div:not(:first-child) {
    border-top: 1px solid #FFFFFF;
}
/* Compose and signature windows */

.dockable.docked-compose.has-action-bar .action-bar,
.dockable.has-action-bar .action-bar .title,
.dockable.mail-signature.has-action-bar .action-bar,
.dockable.out-of-office-panel.has-action-bar .action-bar,
.socpimSignatureView .socpimSignatureDialog .socpimDialogFooter .signatureSetDefault.enabled {
    background-color: #325C80;
}
.dockable.docked-compose .content,
.dockable.mail-signature .content {
    border-left: 1px solid #325C80;
    border-bottom: 1px solid #325C80;
    border-right: 1px solid #325C80;
    border-top: 1px solid #325C80;
}
.socpimSignatureView .socpimSignatureDialog .socpimDialogFooter .signatureDiscard.enabled {
    color: #325C80;
    border-color: #325C80;
}
.socpimSignatureView .socpimSignatureDialog .socpimDialogFooter .signatureDiscard.enabled:hover {
    color: #4178BE;
    border-color: #4178BE;
}
.socpimSignatureView .socpimSignatureDialog .socpimDialogFooter .signatureSetDefault.enabled:hover {
    opacity: .8;
}
.socpimSignatureView .socpimSignatureDialog .socpimDialogFooter .always-on-checkbox:checked + .always-on-label::before {
    background-color: #325C80;
    border: 1px solid #325C80;
}
.socpimSignatureView .socpimSignatureDialog .socpimDialogFooter .always-on-label {
    color: #325C80;
}
.out-of-office-view .footer-row .action-enable {
    background-color: #325C80;
}
.out-of-office-view .footer-row .action-enable:hover {
    background-color: #325C80;
    opacity: .8;
}
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimDiscard a {
    color: #325C80;
    border-color: #325C80;
}
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimDiscard a:hover {
    color: #4178BE;
    border-color: #4178BE;
}
.socpimSignatureView.signature-notification .socpimSignatureDialog .socpimDialogBody .socpimSignatureBody .notification .action-bar .action:hover,
.socpimSignatureView.signature-notification .socpimSignatureDialog .socpimDialogBody .socpimSignatureBody .notification .action-bar .action {
    color: #325C80;
    border-color: #325C80;
}
.socpimSignatureView.signature-notification .socpimSignatureDialog .socpimDialogBody .socpimSignatureBody .notification .action-bar .action:hover,
.socpimSignatureView.signature-notification .socpimSignatureDialog .socpimDialogBody .socpimSignatureBody .notification .action-bar .action:focus {
    color: #FFFFFF;
    background-color: #4178BE;
    border-color: #4178BE;
}
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend.socpimBtnEnabled {
    background-color: #325C80;
}
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend.socpimBtnEnabled:hover,
.socpimComposeView .socpimComposeDialog .socpimDialogFooter .socpimBtn.socpimSend.socpimBtnEnabled:focus {
    background-color: #325C80;
}
.socpimComposeView.delivery-options-selected .socpimToolbarCtrl .delivery-options-icon {
    background-color: #325C80;
}
.socpimBtn.socpimSend.socpimBtnEnabled:hover,
.socpimBtn.socpimSend.socpimBtnEnabled:focus {
    opacity: 0.8;
}
.seq-window .compose-button:hover,
.seq-window .outgoing-button:hover {
    opacity: 0.8;
}
/* End compose and signature windows */
/* Update all the different buttons */

.seq-mcv .refresh-button .get-new-mail {
    fill: #325C80;
}
.seq-mcv .refresh-button:focus svg {
    fill: #325C80;
}
.open-in-notes .pim-open-button {
    background-color: #325C80;
}
.open-in-notes .pim-open-button:hover {
    opacity: 0.8;
    background-color: #325C80;
}
.notification > .action-bar .action {
    background-color: #325C80;
}
.notification > .action-bar .action:hover {
    opacity: 0.8;
    background-color: #325C80;
}
.sametime-tray .log-btn {
    background-color: #325C80;
}
.sametime-tray .log-btn:hover {
    opacity: .8;
    background-color: #325C80;
}
/* the folder and facets toggles */

.seq-mcv.facets-open .facets-toggler,
.seq-mcv.folders-open .folders-toggler {
    border-color: #325C80;
}
.folder-tray,
.seq-mcv .facets-container {
    color: #325C80;
}
/* needs action stuff --------------------------------------------------- */

.iouo-panel .iouo-bar button.date-selector-toggle svg path {
    fill: #325C80;
    background-color: #325C80;
}
.iouo-panel .iouo-bar .title,
.iouo-panel .iouo-bar .iouo-message-container .iouo-message {
    color: #FFFFFF;
    background-color: #325C80;
}
.iouo-panel {
    background-color: #325C80;
}
.outgoing-view .discard svg {
    background-color: #325C80;
}
.outgoing-view:hover {
    background-color: #325C80;
}
.outgoing-view:hover:before {
    background-color: #4178BE;
}
.outgoing-view .disabled {
    background-color: #325C80;
}
.seq-window.tray-open .outgoing-tray .show-sent,
.seq-window.tray-open .outgoing-tray .show-drafts {
    background-color: #325C80;
}
}
