css:
@-moz-document domain("www.skout.com") {
body {
    background: #151515 !important;
}
.app-chat .tab-chat-body {
    background: #222222;
}
.main-container {
    background: #151515;
}
.app .sidebar-card.card {
    background: #222222;
}
.app .sidebar-card {
    background: #222222;
}
.app .sidebar-card.card ul li a {
    border-top: 1px solid #151515;
}
.app .sidebar-card.card ul li a:hover {
    background: #111111;
}
.app-body-bg #main {
    background: transparent;
}
.profile-buzz .buzz-author-bar.tall {
    background: #222222;
}
.profile-buzz {
    background: #151515 !important;
}
container {
    background: #151515 !important;
}
.card-grid .tiles liÂ {
    background: #222222;
}
.comment-input, .interested-card, .item-status, .item-bar, .item-comment, .mp-card, .col-xs-6, .mp-page-link, .card-grid .tiles li, .post-molecule .post-dnd-wrap, .dnd-input-wrap .post-button-column, .comment-show a p, .card, .profile-header .profile-user-tabs li a, .timeline-wrap .timeline-header {
    background: #222222;
}
.profile-header .profile-user-tabs li a:hover {
    background: #111111;
}
textarea.form-control.input-sm.ng-pristine.ng-untouched.ng-valid, .comment-input button, .comment-input textarea {
    background: #151515;
    border: 1px solid #151515;
}
.dnd-input-wrap .text-input-column, .profile-buzz .item-aboutme .aboutme-contentarea .row div:nth-child(odd) {
    background: #151515;
}
.dnd-input-wrap {
    border: 1px solid #222222;
}
.btn {
    background: #151515;
    border: 1px solid #151515;
}
.app .sidebar-card.card img, .notifications-body .notifications-image {
    border: 0px solid transparent;
}
.app-chat .chat-body {
    background: #222222;
    border: 1px solid transparent;
    border-radius: 3px;
}
.chat-bottom {
    background: #151515;
    border: 0px solid transparent;
    border-top: 1px solid transparent;
}
.dnd-input-wrap .post-button-column, .profile-buzz .item-aboutme .aboutme-contentarea .row div:first-child {
    border-radius: 0 0 0 0;
}
.profile-header .profile-top .profile-img-wrap .profile-img {
    border: 0px solid transparent;
}
img.img-circle.img-bubble.pull-left {
    background: transparent !important;
}
.tabrow-wrap .tab .header-wrap .header, .tabrow-wrap .tab-open .header-wrap {
    background: #333333;
    border-bottom: 1px solid #151515;
}
a {
    color: salmon;
}
a:focus, a:hover {
    color: rgba(250,128,114,0.50);
    text-decoration: inherit;
}
.app .sidebar-card.card .name, .app .sidebar-card.card p {
    display: none;
}
.navbar-nav>li>.dropdown-menu {
    background: #333333;
}
.notification-item {
    border-bottom: 1px solid #151515;
}
.app-chat .chat-body .chat-bubble-right label, .app-chat .tab-chat-body .chat-bubble-right label {
    background: #151515;
}
.app-chat .chat-body .chat-bubble-right label:after, .app-chat .tab-chat-body .chat-bubble-right label:after {
    border-left: 6px solid #151515;
}
.app-chat .tab-chat-body .chat-bubble-left label {
    background: #333333;
    color: white;
}
.app-chat .tab-chat-body .chat-bubble-left label:before {
    border-right: 6px solid #333333;
}
.form-control {
    background: #333333;
}
.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    border-bottom: 1px solid #151515;
}
.timeline-wrap {
    background: transparent;
}
.dropdown-menu .divider {
    background: #151515;
}
.profile-header .profile-user-options .action a i, .profile-header .profile-user-options .action a small {
    color: #666666;
}
.profile-header .profile-user-options .action a:hover i, .profile-header .profile-user-options .action a:hover small {
    color: salmon;
}
.profile-header .profile-user-tabs li.active a {
    background: #333333;
}
.profile-header .profile-user-tabs li.active a:hover {
    background: #222222;
}
.dropup .dropdown-menu, .navbar-fixed-bottom .dropdown .dropdown-menu, .dropdown-menu>li>a:focus, .dropdown-menu>li>a, .dropdown-menu>li>a:focus, .dropdown-menu>li>ahover {
    background: #333333;
}
.dnd-input-wrap .attachment-preview-column {
    background: #111111;
    border-top: 1px dashed transparent;
}
.modal-area-info .comments {
    background: #222222;
}
.modal-imageview .modal-content .comment-input {
    border-top: 1px solid #222222;
}
.app-chat .chat-body .chat-bubble-left label, .app-chat .tab-chat-body .chat-bubble-left label {
    background: #333333;
    color: white;
}
.app-chat .chat-body .chat-bubble-left label:before {
    border-right: 6px solid #333333;
}
.dropdown-menu-right {
    background: #333333;
}
li.chat-list-li {
    background: #151515 !important;
}
li.chat-list-li:hover {
    background: #111111 !important;
}
.chat-list .chat-list-ul .chat-list-li, .tabchat-list .chat-list-ul .chat-list-li {
    border-bottom: 1px solid #111111;
}
.chat-column {
    background: #292929;
}
.interested-report-menu a {
    display: none;
}
.previous-interested {
    margin-bottom: 0px;
}
.chat-list .chat-list-ul .chat-list-li .online-status.online, .tabchat-list .chat-list-ul .chat-list-li .online-status.online {
    border: 2px solid #151515;
}
/* Front page */

.header-phones, .slide-header.landing-background-blur, .slide-header.landing-background-solid, .slide-1, .slide-3, .slide-4, .content-header, .container-small, .footer, h1.xl-font.slide-text.scroll-slide-2.fadeInDown.animated.show {
    display: none !important;
}

.navbar-default {
    background: rgba(0,0,0,0.5);
    border-color: rgba(0,0,0,0.25);
}

.slide-2 {
    margin-top: -50px;
    filter: brightness(0.3);
    -webkit-filter: brightness(0.3);
}

/* Login page */

.landing-body-bg #main {
    background: transparent;
}

.forgot-fieldset, .interested-in-fieldset, .log-in-fieldset, .sign-up-fieldset {
    margin-top: 50px;
    background: rgba(0,0,0,0.5);
}

/* Scrollbar */

::-webkit-scrollbar {
    display: none;
}

/* Ads */

.card.hidden-md.hidden-sm.hidden-xs, .sidebar-card.hidden-xs.hidden-sm {
    display: none;
}

/* Login page */

#recaptcha_div .captcha-container {
    background: #333333;
    border: 1px solid transparent;
}

#recaptcha_div .captcha-container input[type=text] {
    background: #333333;
}

.form-control, #recaptcha_div #recaptcha_image {
    border: 1px #333333;
}

#recaptcha_div .captcha-options .recaptcha-logo {
    display: none;
}
#recaptcha_div .captcha-options {
    text-align: center;
}
#recaptcha_div .captcha-options a {
    color: rgba(250, 128, 114, 0.25);
}
#recaptcha_div .captcha-options a:hover {
    color: rgba(250, 128, 114, 0.10);
}
.tabchat-wrapper.tabchat-list.antiscroll {
background: #111111;
}
}
