css:
@-moz-document domain("skout.com") {
.main-container {
    display: none;
}

.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    height: 550px;
}

html, .app-body-bg {
    background: url(/*[[bg]]*/) !important;
    background-color: /*[[bgc]]*/ !important;
    background-size: cover !important;
}

.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    background: #222222;
}

.app-chat .chat-body .chat-bubble-left label, .app-chat .tab-chat-body .chat-bubble-right label {
    background: #151515;
}

.app-chat .tab-chat-body .chat-bubble-right label:after {
    border-left: 6px solid #151515;
}

.app-chat .chat-body .chat-bubble-left label, .app-chat .tab-chat-body .chat-bubble-left label {
    background: #333333;
    color: white;
}

.app-chat .tab-chat-body .chat-bubble-left label:before {
    border-right: 6px solid #333333;
}

textarea.form-control.ng-valid.ng-dirty.ng-touched, textarea.form-control.ng-pristine.ng-valid.ng-touched, .tabrow-wrap .tab-open .header-wrap, .header, .form-control {
    background: #333333;
    border-radius: 0;
}

textarea.form-control.ng-valid.ng-dirty.ng-touched {
    color: white;
}

li.chat-list-li {
    background: #141414 !important;
}

.chat-list .chat-list-ul .chat-list-li, .tabchat-list .chat-list-ul .chat-list-li {
    border-bottom: 1px solid rgba(0,0,0,0.5);
}

.app-chat .tab-chat-body, .app-chat .tab-chat-inbox {
    border-bottom: none;
}

.chat-list .chat-list-ul .chat-list-li:active, .tabchat-list .chat-list-ul .chat-list-li:active {
    background: black !important;
}

.app-chat .chat-body .chat-bubble-right small, .app-chat .tab-chat-body .chat-bubble-right small, .app-chat .chat-body .chat-bubble-left small, .app-chat .tab-chat-body .chat-bubble-left small {
    display: none;
}

.tabchat-wrapper.tabchat-list.antiscroll {
background: #111111;
}
}
