css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document url-prefix("http://fallout2online.com/forum") {
  
  *{
    background: none !important;
    color: silver !important;
    border-width: 1px !important;
    border-color: #333 !important;
    border-radius: 0px !important;
    text-shadow: 0 0 0 transparent, 0 0px 0px rgba(0,0,0,0.0) !important;
    box-shadow:  0 0 0 transparent, 0 0px 0px rgba(0,0,0,0.0) !important;
  }
  
  .externalLink, .internalLink {
    text-decoration: underline !important;
  }
  
  .externalLink:hover, .internalLink:hover {
    text-decoration: underline !important;
    color: white !important;    
  }
    
  h3 * {
    color: white !important;
  }
  
  .xenOverlay, .Menu, .secondaryControls{
    background-color: black !important;
    border-width: 1px !important;
    border-color: #888 !important;
  }
  
  body, .Tooltip, .Popup, .xenTooltip, #calroot, .mce-tooltip, .mce-container, .mce-panel, .xenPreviewTooltip {
    background-color: black !important;
  }
  
  #forums, #forums *, #ProfilePostList>*, .breadcrumb, .sidebar, .secondaryContent, .message, .messageUserBlock, .avatarHolder img, .messageList{
        border: 0px solid rgb(0, 0, 0) !important;
        font-family: Helvetica, Arial, sans-serif  !important; 
  }
  
  .mce-btn {
    border: 0px solid rgb(0, 0, 0) !important;
  }
  
  .message {
    min-height: 64px !important;
    padding: 0px !important;
    margin: 0px !important;
  }
  
  
  .message *{
    min-height: 16px !important;
    padding: 0px;
    margin: 0px;
  }
  
  .messageContent .messageText{
    font-family: Helvetica, Arial, sans-serif  !important; 
    line-height: 1.1em !important;
  } 
  
  .avatar img{
    width: 64px;
    height: 64px;
  }
  
  
  .nodeIcon, .loginButton, .arrow, .message .extraUserInfo, .message .userText, .breadcrumb, .nodeStats, .discussionList .secondRow, .previewTooltip .avatar  {
    display: none !important;
  }
  
  .previewTooltip .text{
    margin-left: 0px;
  }
  
  .likesSummary, .messageMeta  {
    opacity: 0.0;
  }
  
   .message:hover .messageMeta, .message:hover .likesSummary  {
    opacity: 0.65;
  }
  
  .profilePostListItem:hover .messageMeta{
    opacity: 0.65;
  }
  
  .button {
    border: 1px solid #888 !important;
    box-shadow: 0px 0px 0px 0px rgb(0,0,0) !important;
  }
  
}
