css:
@-moz-document url('http://www.dreadcast.net/EDC'), url-prefix('http://www.dreadcast.net/EDC') {
/*       'Nostalgia' by .1019      */
/*            DC Wikast            */
/* i really want this to be global */

#header_logo {								/*Header du Forum*/
    position: absolute;
    top: 10px;
    left: 100px;
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

#header {									/*L'image du bandeau + quick checkup du personnage*/
    filter: grayscale(100%);
    -webkit-filter: grayscale(100%);
    -moz-filter: grayscale(100%);
    -ms-filter: grayscale(100%);
    -o-filter: grayscale(100%);
}

body {										/*Le fond du forum*/
    background: #0e0e0e;
}

a {											/*Les liens*/
    color: rgb(66, 127, 175);
    text-shadow: none;
}

#main_content .couleur2 {					/*Les liens, bis bis*/
    color: rgb(66, 127, 175);
}

.link {										/*Les liens, bis*/
    cursor: pointer;
    color: rgb(66, 127, 175);
    text-shadow: none;
}

#menu_droite h3, #menu_gauche h3 {			/*Les + et - (actions, modérateurs, etc)*/
    color: #B0B0B0;
}

#main_content {								/*Le fond du forum centré*/
    color: grey;
    background: #222222;
    border: 1px solid #434343;
    border-bottom: 0px;
}

#header_accueil {							/*Le header de l'EDC*/
    background: #222;
}

#header_accueil .forum_description, 
#header_accueil p {							/*Le bienvenue sur les EDC*/
    color: #B1B1B1;
}

#main_content h1 {							/*Same*/
    color: rgb(66, 127, 175);
}

#liste_blocs h2 img {						/*Les petites images d'étoiles et listes et tout*/
    filter: invert(100%);
    -webkit-filter: invert(100%);
    -moz-filter: invert(100%);
    -ms-filter: invert(100%);
    -o-filter: invert(100%);
}

#menu_short a {								/*Les icônes du menu de l'EDC (passer à la liste de ses articles etc)*/
    background: #171717;
}

#menu_short a img {							/*Les images des icônes*/
    filter: invert(100%);
    -webkit-filter: invert(100%);
    -moz-filter: invert(100%);
    -ms-filter: invert(100%);
    -o-filter: invert(100%);
}

#liste_blocs {								/*La liste à côté de l'EDC*/
    background: #171717;
    color: #444;
}

#liste_blocs .separator {					/*Séparateur entre les titres et écrits*/
    background: #444;
}

#liste_blocs p, #liste_blocs table {		/*Ecrits dans la liste*/
    color: #808080;
}

#infos_droite h4 {							/*Même chose*/
    color: grey;
}

#main_content .couleur2 img {				/*L'image étoiler cet article*/
    filter: invert(97%);
    -webkit-filter: invert(97%);
    -moz-filter: invert(97%);
    -ms-filter: invert(97%);
    -o-filter: invert(97%);
}

#page_content .elt_liste_personnage {		/*Petit fix sur le padding quand on regarde qui on a étoilé*/
    padding: 6px 5px 3px;
}

#page_content .elt_liste_personnage:hover {	/*Hover sur les "qui on a étoilé"*/
    background: #171717;
}

#side_menu li {								/*Les options*/
    border-left: 5px solid #101010;
}

#side_menu li.selected, 
#side_menu li:hover {						/*Same*/
    border-left: 5px solid #616161;
}

#main_content h1 {							/*Titre du forum de DreadCast*/
    color: rgb(66, 127, 175);
}

#main_content .texte .quote {				/*L'effet citation: comprend aussi le bandeau du FP*/
    background: #434343;
    border: 1px solid #525252;
    color: #A0A0A0;
}

#main_content .texte .invisible {			/*Invisible is invisible*/
    color: #222222;
}

#main_content .texte .quote .invisible {
    color: #434343;
}

#main_content .bleu {						/*Couleur bleue dans les posts*/
    color: #4C7696;
}

#main_content .noir {						/*Couleur noire dans les posts*/
    color: #000;
}

textarea {									/*La réponse rapide*/
    border: 1px solid #434343;
    color: grey;
    background: #101010;
}

input, select {								/*Là où on met du texte*/
    border: 1px solid #434343;
    color: grey;
    background: #101010;
}

#zone_smileys {								/*Les smileys*/
    border: 1px solid #505050;
    background: #434343;
}

#zone_modif_texte {							/*Les différents BBCode*/
    border: 1px solid #505050;
    background: #434343;
}

#zone_modif_texte div {						/*Same*/
    color: grey !important;
}

#zone_crea_liens {							/*'Note', 'Personnage', 'EDC'...*/
    border: 1px solid #505050;
    background: #434343;
}

#zone_crea_liens div {						/*'Note', 'Personnage', 'EDC'...*/
    color: grey !important;
}

.bouton {									/*Les boutons poster & prévisualiser*/
    border: 1px solid #434343;
    color: grey;
    background: #303030;
}

#main {										/*Truc un peu chiant dans le footer qui fait que si on l'enlève y a une sorte de barre noire*/
 	background: #222;
}

#main_bordure_gauche_3 {					/*Coin gauche du footer (bas de page) juste sous le forum*/
    background: #222222;
    border-left: 1px solid #434343;
    border-bottom: 1px solid #434343;
}

#main_bordure_droite_3 {					/*Coin droit du footer juste sous le forum*/
    background: #222222;
    border-right: 1px solid #434343;
    border-bottom: 1px solid #434343;
}

#main_bordure_bas {							/*Footer du bas, juste sous le forum*/
    background: #222222;
    border-bottom: 1px solid #434343;
}

#footer {									/*Le footer*/
    background: #0E0E0E;
    color: grey;
}
}
