css:
@-moz-document url-prefix("https://mail.zoho.com/zm/") {
body.SC_nrp {
    background-color: #f5f5f5;
}
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 400;
    src: local('Roboto'), local('Roboto-Regular'), url(https://fonts.gstatic.com/s/roboto/v15/oMMgfZMQthOryQo9n22dcuvvDin1pK8aKteLpeZ5c0A.woff2) format('woff2'), url(https://fonts.gstatic.com/s/roboto/v15/CrYjSnGjrRCn0pd9VQsnFOvvDin1pK8aKteLpeZ5c0A.woff) format('woff');
}
@font-face {
    font-family: 'Roboto';
    font-style: normal;
    font-weight: 700;
    src: local('Roboto Bold'), local('Roboto-Bold'), url(https://fonts.gstatic.com/s/roboto/v15/d-6IYplOFocCacKzxwXSOJBw1xU1rKptJj_0jans920.woff2) format('woff2'), url(https://fonts.gstatic.com/s/roboto/v15/d-6IYplOFocCacKzxwXSOLO3LdcAZYWl9Si6vvxL-qU.woff) format('woff');
}
@font-face {
    font-family: 'Roboto';
    font-style: italic;
    font-weight: 400;
    src: local('Roboto Italic'), local('Roboto-Italic'), url(https://fonts.gstatic.com/s/roboto/v15/vPcynSL0qHq_6dX7lKVByXYhjbSpvc47ee6xR_80Hnw.woff2) format('woff2'), url(https://fonts.gstatic.com/s/roboto/v15/1pO9eUAp8pSF8VnRTP3xnnYhjbSpvc47ee6xR_80Hnw.woff) format('woff');
}
@font-face {
    font-family: 'Roboto';
    font-style: italic;
    font-weight: 700;
    src: local('Roboto Bold Italic'), local('Roboto-BoldItalic'), url(https://fonts.gstatic.com/s/roboto/v15/t6Nd4cfPRhZP44Q5QAjcC4gp9Q8gbYrhqGlRav_IXfk.woff2) format('woff2'), url(https://fonts.gstatic.com/s/roboto/v15/t6Nd4cfPRhZP44Q5QAjcC7rIa-7acMAeDBVuclsi6Gc.woff) format('woff');
}
body.font1, body.font1 .SC_pv, body.font1 .zmL, body.font1 .ztb-popover-label, body.font1 input, body.font1 select, body.font1 td, body.font1 textarea {
    font-family: Roboto, sans-serif !important;
    font-size: 13px !important;
}
body,
body .SC_pv,
body .zmL,
body input,
body select,
body td,
body textarea,
.ztb-font-family {
    font-family: Roboto, sans-serif;
    font-size: 13px;
    -webkit-font-smoothing: antialiased;
}
body.SC_nrp {
    background-color: #f9f9f9;
}
/* ICONS */

#ztb-search-filter,
.SC_cp li div.sel,
.SC_select,
.SC_srch>table>tbody>tr td:after,
.ztb-notifications-icon,
.ztb-settings-icon,
[class^=msi-],
[msi*=" msi-"] {
    color: #000;
}

.zmThmDBlue .zm_apps .msi-links:after, .zmThmDBlue .zm_apps .msi-notes:after, .zmThmDBlue .zm_apps .msi-calendar:after, .zmThmDBlue .zm_apps .msi-mail:after, .zmThmDBlue .zm_apps .msi-contacts:after, .zmThmDBlue .zm_apps .msi-task:after {
    color: #aaa;
}

.zmThmDBlue .zm_apps .msi-mail:after {
    color: #333a45;
}
/* TOPBAR & SEARCH */

.tempAppBar #ztb-switch-menu {
    width: 20px;
    padding: 12px 25px 0;
}
a#ztb-logo {
    margin-left: 19px;
}
#ztb-search {
    border: 0px;
    border-radius: 0px;
    background: #FFF;
    left: 280px;
    width: 250px;
    padding-left: 1px;
    margin-top: 6px;
}
.tempAppBar #ztb-search {
    left: 286px;
}
#ztb-search.ztb-search-focus #ztb-search-filter,
#ztb-search-action:hover,
#ztb-search-filter:hover,
#ztb-search-filter:hover .ztb-search-dd {
    opacity: 0.4;
}
#ztb-topband #ztb-search-field {
    top: 1px;
    width: 185px;
}
/* APPS TAB */

.zm_apps {
    background-color: #003a59;
}
.zm_apps li i::before {
    color: #e5e5e5;
}
.zm_apps li.sel i::before,
.zm_apps li:hover i::before {
    color: #fff;
}
.tempAppBar .zm_apps {
    width: 70px;
}

/* LAYOUT */

.SCl,
.SCl .SC_ct,
.zm_compose {
    background-color: #fff;
}
.SCl,
.SCl .SC_ct,
.zm_compose {
    background-color: #fff;
}
.SCm_parent {
    left: 301px;
}
.tempAppBar .SCl, .tempAppBar .zm_compose {
    left: 70px;
}

.zmThmDBlue .SCl, .zmThmDBlue .SClDup, .zmThmDBlue .zm_compose {
	background-color: #fff;
}

/* COMPOSE BUTTON */

.zm_comp {
    top: 18px;
    width: 200px;
}
.SCl,
.zm_compose {
    left: 60px;
    width: 100px;
    border-right: 1px solid #e5e5e5;
}
.zm_comp span:first-child {
    width: 169px;
}
.zm_compose {
    height: 65px;
    top: 40px;
    width: 240px;
    border-right: 1px solid #f0f0f0;
    border-top: 1px solid #f0f0f0;
}
.zm_compul {
    width: 198px;
}
/* COMPOSE VIEW */

.zmCnew > .SCmb {
    background-color: #f5f5f5;
}
/* NEW ITEM INDICATION */

.SC_ct b,
.zmCR .SCmb.SC_frt i::before {
    /*    color: #017fbe;*/
    
    color: #000;
    font-size: 90%;
}
/* MAIL TABS */

.zm_tabs {
    background-color: #f9f9f9;
    right: 0px;
    margin: 0px;
    padding: 0px;
}
.SCmc {
    border-right: 1px solid #f0f0f0;
    box-shadow: 0 0 0 0 #ccc;
}
.SC_tab {
    left: -8px;
}
/* FOLDER TREE */

.SCl {
    width: 240px;
    border-right: 1px solid #f0f0f0;
}
.SC_tre {
    color: #111;
    padding: 4px 4px 4px 25px;
}
.SC_tre span {
    font-size: 100%;
}
.zm_txt {
    color: #000 !important;
}
.zm_txt.SC_bld {
    /*  color: #017fbe !important;*/
    
    font-weight: bold;
}
.SClDup {
    background-color: #FFF;
}
.tempAppBar .SCl, .tempAppBar .SClDup, .tempAppBar .zm_compose {
    left: 70px;
}
.SC_ltl, .tempAppBar .SC_ltl {
    padding: 8px 4px 8px 25px;
}

.SC_ltl span {
    color: #111;
    font-weight: 400;
    /*  -moz-osx-font-smoothing: unset;
    text-rendering: none;*/
}
.SC_calApp .SC_ltl span {
    color: #888;
    /*  -moz-osx-font-smoothing: grayscale;*/
    
    text-rendering: optimizeLegibility;
}
.SC_mailApp .SC_ltl span {
    color: #888 !important;
    font-size: 90%;
}
.SC_ct {
    left: 200px;
}
.SCl .Stre .SC_ct {
    background-color: #e3edf2 !important;
}
.Stre,
.SC_tre:hover,
.zmL:hover {
    background-color: #e3edf2 !important;
}
.SC_calApp .SC_ltl:hover,
.SC_taskApp .SC_ltl:hover,
.SC_noteApp .SC_ltl:hover {
    background-color: #e3edf2 !important;
}
.SC_calApp .SC_ltl:hover {
    background-color: #fff !important;
}
.SC_tre:hover .SC_ct {
    background-color: #e3edf2 !important;
}
.zmThmDBlue .SC_tre .SC_ct {
    background: none;
}

/* BUTTONS */

.SCmb b:hover {
    background-color: #fff;
}
.SC_ltl:hover b i::before {
    background-color: #fff;
    color: #017fbe;
    padding: 3px;
}
.SC_ltl b:hover i::before {
    background-color: #f0f0f0;
}
/* MAIL HEADER */

.SC_mhd {
    background-color: #f9f9f9;
    height: 29px;
    padding: 10px 0 4px 11px;
}
.SCmc > .SCmb {
    background-color: #f9f9f9;
    border-bottom: 1px solid #f0f0f0;
    padding-top: 0px;
}
.Sevent_update .zm_mention,
.Stask_list li,
.Stask_parent,
.Stask_sub,
.zmL,
.zmTV {
    background: none;
    border-bottom: 1px solid #f0f0f0;
}
.SC_tab li.sel {
    background-color: #fff;
    border: 1px solid #f0f0f0;
    opacity: 1 !important;
}
.SC_phd {
    background: #f9f9f9;
    border-bottom: 1px solid #f0f0f0;
}
.SCl {
    bottom: 25px;
    top: 103px;
}
.SC_ltl {
    padding: 5px 4px 5px 25px;
}
.SC_mclst {
    top: 61px;
}
.zmL.zmSLst:hover,
.zmSLst {
    background-color: #e3edf2 !important;
}
/* LIST ITEM */

.zmSLst {
    border-bottom: 1px solid #e3edf2 !important;
}
.zmL {
    color: #000;
    height: 46px;
    padding-bottom: 8px !important;
    ;
}
.zmFrm {
    color: #000;
    padding-top: 11px;
    padding-bottom: 4px;
    font-size: 14px;
}
.zmL .msi-subtask,
.zmtCt {
    top: 35px;
}
.zmSub span {
    color: #666;
    font-size: 100%;
}
.zmDate,
.zmSze {
    color: #666;
}
.zm_urd .zmSub {
    font-weight: normal;
    color: #666;
}
.SC_pv {
    box-shadow: 0 0 10px 0 rgba(0, 0, 0, 0.1);
}
/* MAIL DETAIL VIEW */

.Sevent_update .zm_mention,
.Stask_list li,
.Stask_parent,
.Stask_sub,
.zmL,
.zmTV,
.zmmre {
    background: none;
    border-bottom: 1px solid #f0f0f0;
}
.zmPHdr.zm_tm .zmmre {
    border-bottom: 0px;
}
.SC_attachviewer .SC_mhd .SC_flt .SC_mtb,
.SC_streams .SC_mhd .SC_flt .SC_mtb {
    top: 9px;
}
/* SPAM VIEW */

.SC_trsh .SC_mclst {
    top: 93px;
}
.SC_mtb span::before {
    top: 4px;
    margin-left: -11px;
}
.SC_simg {
    background-color: #fffacc;
    border-bottom: 1px solid #f0ebc0;
    padding-top: 0px;
    padding-bottom: 0px;
}
.SC_simg span::before {
    top: 10px;
    margin-left: -13px;
}
.SC_simg font {
    margin-top: 0px;
}
.SCS_cmnt > ul {
    background-color: #f9f9f9;
}
/* CALENDAR */

.zcl_tpnvcn > div {
    background-color: #f5f5f5;
}
.zcl_tpnv > div.zcl_sl {
    border-bottom-color: #017fbe;
}
.msi-add {
    color: #017fbe;
    font-weight: 400;
}
.msi-plus {
    color: #017fbe;
}
.SC_set .SC_flt {
    margin-left: 9px;
}
.zcl_smtadd {
    margin-top: 5px;
}
.zcl {
    border-right: 0px;
}
/* STREAMS */

.SCS_tab li.stSel,
.SCS_tab li:hover {
    border-bottom: 3px solid #017fbe;
}
}
