css:
@namespace url(http://www.w3.org/1999/xhtml);

@-moz-document domain("moodlearn.ariel.ac.il") {
  #navbar_ariel_img {
background-image: url("https://moodle.org/theme/image.php/moodleorgcleaned_moodleorg/theme_moodleorgcleaned/1452697804/moodle-logo") !important;;
    background-position: 13px 20px !important;
    left: 5px;
    top: 10px;
    height: 82px;
    width: 234px;
    position: fixed !important;
    background-color: rgba(9, 44, 114 ) !important;

  }
  .navbar-fixed-top, .navbar-fixed-bottom {
    position: absolute !important;
    right: 0px;
    left: 0px;
    z-index: 1030;
    margin-bottom: 0px;
    
}
  .navbar .logininfo {
    float: left;
    margin-top: 76px;
}
  #fgroup_id_buttonar {
    background-color: rgba(9, 44, 114, 0.69) !important;
}
  #page-content {
    background-image: url("http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/confectionary.png");
    border-radius: 5px;
  }
  .coursebox {
    padding: 5px;
    margin-bottom: 15px;
    border: 3px dotted #ddd;
    -webkit-border-radius: 4px;
    -moz-border-radius: 4px;
    border-radius: 4px;
}
  .yui3-skin-sam .yui3-tabview-panel {
    background: #f5f5f5;
    border: 1px solid #808080;
    border-top-color: #243356;
    padding: .25em .5em;
    border-radius: 1px 1px 3px 3px;
}
  body {
    font-size: 13px;
    color: #000;
    background-image: url("http://subtlepatterns2015.subtlepatterns.netdna-cdn.com/patterns/confectionary.png");
}
  .courses .coursebox.odd {
    background: white;
  }
  .block {
    border: 2px solid #ADB7CC;
    background-color: #f3f3f5;
}
}
