css:
@-moz-document domain(yandex.ru) {
.main__content {
	margin-left: 100px;
}
body {
	background-color: #f5f5f5;
}
.serp-header__wrapper {
	height: 70px;
}
.serp-item__title-link {
	color: #444b52;
}
.serp-adv__add {
	display: none;
}
.container__first,
#banner,
.widgets__row {
	display: none;
}
.rows__row_main {
	margin-top: 10%;
}
.domik2__dropdown-bg {
	background-color: white;
}
.b-page_tld_com-tr,
.serp-url__link:visited,
.serp-url__link,
.serp-url__link:link,
.serp-url__link:visited,
.serp_url__text {
	color: #747087;
}
.navigation__region{
	width: 200px;
	background-color: #e3e0de;
	border-radius: 0px 7px 7px 0px;
}
.navigation__item_state_selected{
	background-color: #fafafa;	
	border-radius: 0px 7px 7px 0px;
	 border: 1px solid #e3e0de;
}
.navigation__item_state_selected .service:before {
	background-color: #996300!important;

}
.websearch-button__text{
	border: 2px solid #e3e0de;
    background-color: #e3e0de;

}
.input_theme_websearch{
	border: 2px solid #e3e0de;	
}
.button_theme_websearch .button__text {
    position: relative;
    display: block;
    box-sizing: border-box;
    border: solid #e3e0de;
    border-width: 2px 0;
    background-color: #e3e0de;
    -webkit-transition: border-color .1s ease-out,background .1s ease-out;
    transition: border-color .1s ease-out,background .1s ease-out;

}
.link_blue_yes:link, .link_blue_yes:visited, .link_blue_yes.link_pseudo_yes {
    color: grey;
}
.home-tabs__link {
    margin-right: 2.6%;
	background-color: #e3e0de;
	border-radius: 20px;
	padding-top: 5px;
	padding-bottom: 5px;
	padding-left: 10px;
	padding-right: 10px;
	
}
.home-tabs__link:hover {
	color: black!important;
	background-color: #bdbdbd;
	margin-right: 2.6%;
	border-radius: 20px;
	padding-top: 5px;
	padding-bottom: 5px;
	padding-left: 10px;
	padding-right: 10px;
}

.i-ua_browser_desktop .link:hover {
    color: #414141!important;
}

.popup_theme_ffffff:before {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    content: '';
    border-radius: 7px;
    box-shadow: 0 10px 20px -5px rgba(0,0,0,.4);
}
.link, .link:visited{
    color: #615c57!important;
}
.pager__item_current_yes, .pager__item_current_yes:hover {
    cursor: default;
    background-color: gainsboro;
}
.distro {
    margin-bottom: 70px;
    display: none;
}
.z-images__thumbs-list{
    position: relative;
    overflow: hidden;
    margin: 0;
    border: none;
    background: 0 0;
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
}
.user .user__first-letter {
    color: gray;
    text-transform: uppercase;
    margin: 1px;
    text-shadow: 1px 0px 5px gray;
}
.footer.footer_distro_yes.clearfix{
 	    background-color: #e3e0de!important;   
}
.copyright, .footer__age-warning {
    white-space: nowrap;
    color: black;
}
.z-entity-search {
    position: static;
    margin-left: 34px;
    -webkit-transition: margin-left .25s;
    transition: margin-left .25s;
    background-color: #fff;
    padding: 20px;
    border-radius: 10px;
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
}
.serp-item {
    background-color: #fff;
    padding: 10px;
    border-radius: 7px;
}
.favicon_page_0 .favicon__icon {
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
}
.page-layout_page_search.serp-controller_isTileMorda_no .serp-item_layout_horizontal {
    min-height: 112px;
    box-shadow: 0 5 5px rgba(0,0,0,0.5);
    margin-top: 5px;
}
.navigation_text-nav_yes {
    margin-left: 0;
}
.page-layout_page_search.serp-controller_isTileMorda_no .serp-item_layout_horizontal {
    max-height: 112px!important;
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
    margin-top: 10px;    
}
.serp-item__text{
 	display: none;   
}
.video-tabs .tabs-menu__tab_active_yes:after {
    background-color: #996300!important;
}
.b-page_new-snp-selection_yes .serp-list_type_search, .serp-controller_isTileMorda_no .serp-list_type_search {
    border: none;
    margin-left: 100px;
}
.related-serp_design_2 {
    display: none;
}
.favicon_page_0 .favicon__icon{
    box-shadow: 0 0 10px rgba(0,0,0,0.5);
    border-radius: 50%;
}

.input_theme_websearch:before {
    position: absolute;
    top: 0;
    right: 0;
    bottom: 0;
    left: 0;
    content: '';
    border-style: solid;
    border-color: #fff;
    background-color: #fff;
    background-clip: padding-box;
}

.input_theme_bordergray .input__box {
    background: #f3f1ed;
    border-bottom: 1px solid #dad8d5;
    padding-bottom: 4px;
    margin: 0 0 1px;
    border-radius: 20px;
    padding: 5px;
    padding-right: 10px;
}

.domik2__dropdown-forgot {
    font-size: 16px;
    margin-right: 5px;
    background-color: #e3e0de;
    border-radius: 20px;
    width: 24px;
}

.domik2__dropdown-bg {
    background-color: white;
    border-radius: 20px;
}

.domik2 .auth__button {
    width: 70px;
    background-color: #e5e2dc;
    border-radius: 20px;
    margin: 5px;
    height: 30px;
    font-size: 15px!important;
}

.domik2 .auth__button, .domik2 .auth__qr {
    font-size: 16px;
    line-height: 0;
    -webkit-transition: background-color .15s,visibility 0s;
    transition: background-color .15s,visibility 0s;
}

.domik2__dropdown-row_last_yes {
    margin: 0;
    height: 37px;
    border-top: 0;
    position: relative;
}

.domik2__dropdown-row_checkbox_yes {
    margin-top: 2px;
    margin-bottom: 0;
}

.domik2__dropdown-row_last_yes {
    margin: 0;
    height: 40px;
    border-top: 0;
    position: relative;
}

.popup {
    position: absolute;
    z-index: 32700;
    display: none;
    box-sizing: border-box;
    border: 1px solid transparent;
    border-radius: 6px;
}

.domik2_expanded_yes .domik2__title {
    min-width: 86px;
    background: #f5f5f5;
}

.serp-user {
    font: 15px/30px Arial,sans-serif;
    display: inline-block;
    padding: 0 20px;
    white-space: nowrap;
    text-decoration: none;
    color: #000!important;
    border: 1px solid rgba(0,0,0,.2);
    border-radius: 20px;
}
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
    
}
