css:
@-moz-document url("https://8tracks.com"), url("http://8tracks.com"), url-prefix("https://8tracks.com"), url-prefix("http://8tracks.com"), domain("8tracks.com") {
#thin_header, #header_content, #player_box {
    background: url("http://i.imgur.com/zGhzMlq.png") repeat fixed!important;
}

#logged_in_topnav:hover #topnav_element, #logged_in_topnav.active #topnav_element {
    background-color: rgba(139, 201, 233, 0.7);
}

#thin_header a:hover {
    color: rgba(139, 201, 233, 0.8);
}

#player_progress_bar_meter, .inverted_counter {
    background: rgba(139, 201, 233, .7);
}

#now_playing .fav {
    color: rgba(255, 255, 255, 0.5);
}

.full-width.user_about.profile.clear.favable.vcard.dark-bg {
    background-color: rgba(238, 213, 235, 0.7);
}

.volume-container {
    background: rgb(139, 201, 233)!important;
}

.player_progress_bar {
    background: rgba(139, 201, 233, 0.4)!important;
    box-shadow: none!important;
}

a.follow.active:not(.just_clicked):hover {
    background: rgba(238, 213, 235, 0.4)!important;
    box-shadow: none!important;
}

.interactbox, .volume-range {
    background: rgba(238, 238, 238, 0.6)!important;
}

.track .fav {
    color: rgba(139, 201, 233, 0.3);
}

a.fav.active {
    color: rgb(238, 213, 235)!important;
}

 a.fav:hover {
    color: rgba(238, 213, 235, 0.5)!important;

}

.flatbutton, a.flatbutton, a.propername, .notifications_counter, #now_playing .fav:not(.just_clicked):hover {
    color: #8bc9e9;
}

.turquoise, a.turquoise, a.follow.active {
    color: #8bc9e9!important;
}

/*[[bgblr]]*/ /*[[mbgblr]]*/ /*[[nbgblr]]*/ /*[[sbgblr]]*/

#play_area {
    background-color: rgba(139, 201, 233, 0.7);
}

#explore_button, .tokenfield .token, .turquoise_button:hover, a.turquoise_button:hover, #mix_tags_display a:hover {
    background: #8bc9e9;
    border: 0px none;
}

#explore_button:hover, .turquoise_button, a.turquoise_button {
    background: #eed5eb;
    border: 0px none;
}

#player_like_button.active, #youtube_like_button.active, a#like_button.active .i-like, #now_playing .track_metadata .label, #explore_button, .player_button:not(.just_clicked):hover, #mix_tags_display a:hover {
    color: #eed5eb;
}

 .turquoise:hover, a.turquoise:hover, a:hover>.turquoise, .turquoise.active {
    color: #eed5eb!important;
}

a.follow, .card {
    border: 0px none;
}

#profile_top a.follow {
    background: rgba(139, 201, 233, .8);
    color: #eed5eb;
}

#profile_top a.follow:hover {
    background-color: #8bc9e9;
    color: #fff;
}

.dark-bg {
    color: #fff;
}
}
